import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

// Page Imports
import Dashboard from './pages/Dashboard';
import Events from './pages/Events';
import MyEvents from './pages/MyEvents';
import Profile from './pages/Profile';
import EditProfile from './pages/EditProfile';
import ChangePassword from './pages/ChangePassword';
import Settings from './pages/Settings'; 
import ThemeSelection from './pages/ThemeSelection';
import BottomNav from './components/BottomNav';

function App() {
  const [theme, setTheme] = useState('light'); 
  const [searchTerm, setSearchTerm] = useState("");
  const [user, setUser] = useState({
    name: "Priya Sharma",
    email: "priya.sharma@university.edu",
    college: "Engineering Tech Institute",
    regNo: "ETI-2023-4589"
  });

  // Events with fixed, reliable images
  const [allEvents] = useState([
    { 
      id: 1, 
      title: "AI & Future Tech Workshop", 
      date: "Apr 05, 2024 · 09:00 AM", 
      location: "Innovation Lab, 3rd Floor", 
      category: "Tech",
      image: "https://images.unsplash.com/photo-1485827404703-89b55fcc595e?w=800&q=80" 
    },
    { 
      id: 2, 
      title: "Inter-College Music Battle", 
      date: "Apr 12, 2024 · 05:00 PM", 
      location: "Main Stadium", 
      category: "Cultural",
      image: "https://images.unsplash.com/photo-1501281668745-f7f57925c3b4?w=800&q=80" 
    },
    { 
      id: 3, 
      title: "University Basketball Finals", 
      date: "Apr 20, 2024 · 10:00 AM", 
      location: "Indoor Sports Complex", 
      category: "Sports",
      image: "https://images.unsplash.com/photo-1546519638-68e109498ffc?w=800&q=80" 
    }
  ]);

  const [registeredList] = useState([]);

  const filteredEvents = allEvents.filter(event => 
    event.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // MOBILE APP SIZING STYLES
  const appStyles = {
    maxWidth: '375px', // Standard Mobile Width
    margin: '0 auto',  // Center the app on screen
    backgroundColor: theme === 'dark' ? '#0f172a' : '#fff',
    color: theme === 'dark' ? '#f8fafc' : '#1e293b',
    minHeight: '100vh',
    position: 'relative',
    boxShadow: '0 0 20px rgba(0,0,0,0.1)', // Makes it look like an app
    transition: 'all 0.3s ease',
    overflowX: 'hidden'
  };

  return (
    <Router>
      <div style={appStyles}>
        <Routes>
          <Route path="/" element={
            <Dashboard 
              user={user} 
              events={filteredEvents} 
              theme={theme} 
              regCount={registeredList.length} 
            />
          } />
          <Route path="/events" element={
            <Events allEvents={allEvents} theme={theme} searchTerm={searchTerm} setSearchTerm={setSearchTerm} />
          } />
          <Route path="/my-events" element={<MyEvents theme={theme} />} />
          <Route path="/profile" element={<Profile user={user} theme={theme} />} />
          <Route path="/edit-profile" element={<EditProfile user={user} setUser={setUser} theme={theme} />} />
          <Route path="/change-password" element={<ChangePassword theme={theme} />} />
          <Route path="/settings" element={<Settings theme={theme} />} />
          <Route path="/settings/theme" element={<ThemeSelection currentTheme={theme} setTheme={setTheme} />} />
        </Routes>
        <BottomNav theme={theme} />
      </div>
    </Router>
  );
}

export default App;
