import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Search, Home, Calendar, Ticket, User, Menu } from 'lucide-react';

const Events = () => {
  const navigate = useNavigate();
  const filters = ['All', 'Tech', 'Cultural', 'Sports', 'Workshops'];

  return (
    <div style={{...styles.container, paddingBottom: '90px'}}>
      <header style={styles.header}>
        <h2 style={{margin:0}}>Events</h2>
        <Menu size={24} />
      </header>

      <div style={styles.searchContainer}>
        <Search size={18} style={styles.searchIcon} />
        <input type="text" placeholder="Search events..." style={styles.input} />
      </div>

      <div style={{display:'flex', gap:'10px', overflowX:'auto', padding:'0 16px 15px', scrollbarWidth:'none'}}>
        {filters.map(f => (
          <button key={f} style={{padding:'8px 15px', borderRadius:'10px', border:'none', backgroundColor: f==='All'?'#e0e7ff':'#f8fafc', color: f==='All'?'#2563eb':'#64748b', fontWeight:'bold'}}>{f}</button>
        ))}
      </div>

      <div style={{padding:'0 16px', overflowY:'auto', height:'calc(100vh - 250px)'}}>
        {[1, 2, 3].map(i => (
          <div key={i} style={{...styles.eventCard, minWidth:'unset', marginBottom:'20px'}}>
             <div style={{height:'150px', backgroundColor:'#2563eb'}} />
             <div style={styles.cardInfo}>
               <h4 style={{margin:0}}>University Event {i}</h4>
               <p style={{fontSize:'12px', color:'#94a3b8'}}>Main Campus Auditorium</p>
               <button style={{...styles.regBtn, width:'100%', marginTop:'10px'}}>Register</button>
             </div>
          </div>
        ))}
      </div>

      <nav style={styles.bottomNav}>
        <div style={styles.navItem} onClick={() => navigate('/')}><Home size={22} /><span>Home</span></div>
        <div style={{...styles.navItem, color: '#2563eb'}} onClick={() => navigate('/events')}><Calendar size={22} /><span>Events</span></div>
        <div style={styles.navItem} onClick={() => navigate('/my-events')}><Ticket size={22} /><span>My Events</span></div>
        <div style={styles.navItem}><User size={22} /><span>Profile</span></div>
      </nav>
    </div>
  );
};

export default Events;
