import React, { useState } from 'react';
import { 
  Home, Calendar, Ticket, User, Settings, LogOut, Menu, X 
} from 'lucide-react';
import { useNavigate, useLocation } from 'react-router-dom';

const Sidebar = ({ user, theme, onLogout }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const isDark = theme === 'dark';
  
  // State to control sidebar expansion
  const [isExpanded, setIsExpanded] = useState(true);

  // Define navigation items
  const menuItems = [
    { label: 'Dashboard', icon: <Home size={22} />, path: '/' },
    { label: 'Events', icon: <Calendar size={22} />, path: '/events' },
    { label: 'My Tickets', icon: <Ticket size={22} />, path: '/my-events' },
    { label: 'Profile', icon: <User size={22} />, path: '/profile' },
    { label: 'Settings', icon: <Settings size={22} />, path: '/settings' },
  ];

  const styles = {
    // SIDEBAR CONTAINER
    sidebar: {
      width: isExpanded ? '260px' : '80px', // Toggle width
      height: '100vh',
      backgroundColor: isDark ? '#1e293b' : '#fff',
      borderRight: isDark ? '1px solid #334155' : '1px solid #e2e8f0',
      display: 'flex',
      flexDirection: 'column',
      padding: '20px',
      position: 'fixed',
      left: 0,
      top: 0,
      zIndex: 1000,
      transition: 'width 0.3s ease', // Smooth transition
      // Hide on mobile (use bottom nav instead)
      '@media (max-width: 768px)': {
        display: 'none'
      }
    },

    // LOGO AREA
    logoSection: {
      display: 'flex',
      alignItems: 'center',
      gap: '12px',
      marginBottom: '40px',
      paddingLeft: '5px',
      overflow: 'hidden' // Hide text when collapsed
    },
    logoBox: {
      minWidth: '40px',
      height: '40px',
      backgroundColor: '#6366f1',
      borderRadius: '10px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      color: '#fff',
      fontWeight: 'bold',
      fontSize: '18px'
    },
    logoText: {
      fontSize: '20px',
      fontWeight: '800',
      color: isDark ? '#fff' : '#1e293b',
      whiteSpace: 'nowrap', // Prevent text wrapping
      opacity: isExpanded ? 1 : 0, // Fade text
      transition: 'opacity 0.2s',
      display: isExpanded ? 'block' : 'none'
    },

    // MENU TOGGLE BUTTON (Hamburger)
    toggleBtn: {
      position: 'absolute',
      top: '20px',
      right: isExpanded ? '20px' : '50%', // Move to center when collapsed
      transform: isExpanded ? 'none' : 'translateX(50%)',
      cursor: 'pointer',
      color: '#64748b',
      transition: 'all 0.3s'
    },

    // NAVIGATION LIST
    nav: {
      flex: 1,
      display: 'flex',
      flexDirection: 'column',
      gap: '10px'
    },
    navItem: (isActive) => ({
      display: 'flex',
      alignItems: 'center',
      gap: '15px',
      padding: '12px 14px',
      borderRadius: '12px',
      cursor: 'pointer',
      backgroundColor: isActive ? (isDark ? '#334155' : '#eff6ff') : 'transparent',
      color: isActive ? '#6366f1' : (isDark ? '#cbd5e1' : '#64748b'),
      fontWeight: isActive ? '600' : '500',
      transition: 'all 0.2s',
      justifyContent: isExpanded ? 'flex-start' : 'center' // Center icon when collapsed
    }),
    navLabel: {
      whiteSpace: 'nowrap',
      display: isExpanded ? 'block' : 'none'
    },

    // USER PROFILE SECTION (Bottom)
    userSection: {
      marginTop: 'auto',
      borderTop: isDark ? '1px solid #334155' : '1px solid #f1f5f9',
      paddingTop: '20px',
      display: 'flex',
      alignItems: 'center',
      gap: '12px',
      justifyContent: isExpanded ? 'flex-start' : 'center'
    },
    avatar: {
      width: '40px',
      height: '40px',
      borderRadius: '50%',
      objectFit: 'cover'
    },
    userInfo: {
      display: isExpanded ? 'block' : 'none',
      whiteSpace: 'nowrap'
    },
    userName: { fontSize: '14px', fontWeight: 'bold', color: isDark ? '#fff' : '#1e293b' },
    userRole: { fontSize: '12px', color: '#64748b' },

    // LOGOUT BUTTON
    logoutBtn: {
      marginTop: '10px',
      display: 'flex',
      alignItems: 'center',
      gap: '15px',
      padding: '12px 14px',
      borderRadius: '12px',
      cursor: 'pointer',
      color: '#ef4444',
      fontWeight: '600',
      justifyContent: isExpanded ? 'flex-start' : 'center',
      transition: 'background 0.2s'
    }
  };

  return (
    <>
      {/* Spacer to push main content right */}
      <div className="sidebar-spacer" style={{ 
        width: isExpanded ? '260px' : '80px', 
        transition: 'width 0.3s ease',
        display: window.innerWidth < 768 ? 'none' : 'block' // Hide spacer on mobile
      }}></div>

      <div style={styles.sidebar} className="desktop-sidebar">
        
        {/* Toggle Button (Hamburger / Close) */}
        <div style={styles.toggleBtn} onClick={() => setIsExpanded(!isExpanded)}>
          {isExpanded ? <Menu size={20} /> : <Menu size={24} />}
        </div>

        {/* Logo Section */}
        <div style={styles.logoSection}>
          <div style={styles.logoBox}>ES</div>
          <div style={styles.logoText}>EventSphere</div>
        </div>

        {/* Navigation Items */}
        <div style={styles.nav}>
          {menuItems.map((item) => {
            const isActive = location.pathname === item.path;
            return (
              <div 
                key={item.label} 
                style={styles.navItem(isActive)} 
                onClick={() => navigate(item.path)}
                title={!isExpanded ? item.label : ''} // Tooltip when collapsed
              >
                {item.icon}
                <span style={styles.navLabel}>{item.label}</span>
              </div>
            );
          })}
        </div>

        {/* User Profile */}
        <div style={styles.userSection}>
          <img src={user.profileImage} alt="User" style={styles.avatar} />
          <div style={styles.userInfo}>
            <div style={styles.userName}>{user.name.split(' ')[0]}</div>
            <div style={styles.userRole}>{user.role === 'student' ? 'Student' : 'Teacher'}</div>
          </div>
        </div>

        {/* Logout */}
        <div 
          style={styles.logoutBtn} 
          onClick={onLogout}
          title={!isExpanded ? 'Log Out' : ''}
          onMouseEnter={(e) => e.currentTarget.style.backgroundColor = isDark ? '#334155' : '#fef2f2'}
          onMouseLeave={(e) => e.currentTarget.style.backgroundColor = 'transparent'}
        >
          <LogOut size={22} />
          <span style={styles.navLabel}>Log Out</span>
        </div>

      </div>
    </>
  );
};

export default Sidebar;
