import React, { useState } from 'react';
import { Bell, Search } from 'lucide-react'; // Or whichever icon library you use
import './Dashboard.css'; // Make sure to import the CSS below

const DashboardHeader = () => {
  const [darkMode, setDarkMode] = useState(false);

  return (
    <div className="header-container">
      {/* Left Side: Welcome Message */}
      <div className="user-welcome">
        <img src="https://via.placeholder.com/40" alt="User" className="user-avatar" />
        <div>
          <p className="welcome-text">Welcome back,</p>
          <h3>cdc</h3>
        </div>
      </div>

      {/* Right Side: Search, Notification, AND THEME SWITCH */}
      <div className="header-actions">
        
        {/* Existing Search Bar */}
        <div className="search-bar">
          <Search size={18} color="#888" />
          <input type="text" placeholder="Search events..." />
        </div>

        {/* Existing Notification Icon */}
        <div className="notification-icon">
          <Bell size={24} color="#555" />
          <span className="notification-dot"></span>
        </div>

        {/* --- ðŸ†• NEW THEME TOGGLE SWITCH START ðŸ†• --- */}
        <div className="theme-toggle">
          <label className="switch">
            <input 
              type="checkbox" 
              checked={darkMode} 
              onChange={() => setDarkMode(!darkMode)} 
            />
            <span className="slider round"></span>
          </label>
        </div>
        {/* --- ðŸ†• NEW THEME TOGGLE SWITCH END ðŸ†• --- */}

      </div>
    </div>
  );
};

export default DashboardHeader;
