import React from 'react';
import { ArrowLeft } from 'lucide-react';
import { useNavigate } from 'react-router-dom';

const Notifications = ({ theme, notificationsList }) => {
  const navigate = useNavigate();
  const isDark = theme === 'dark';

  const styles = {
    container: {
      maxWidth: '800px',
      margin: '0 auto',
      padding: '24px',
      fontFamily: 'sans-serif',
      color: isDark ? '#fff' : '#1e293b'
    },
    header: { display: 'flex', alignItems: 'center', gap: '15px', marginBottom: '24px' },
    title: { fontSize: '24px', fontWeight: 'bold' },
    
    // List Item
    item: {
      display: 'flex', gap: '16px',
      backgroundColor: isDark ? '#1e293b' : '#fff',
      padding: '16px',
      borderRadius: '16px',
      marginBottom: '12px',
      border: isDark ? '1px solid #334155' : '1px solid #e2e8f0'
    },
    iconBox: (bg) => ({
      width: '40px', height: '40px', borderRadius: '12px',
      backgroundColor: bg, display: 'flex', alignItems: 'center', justifyContent: 'center',
      flexShrink: 0
    }),
    content: { flex: 1 },
    itemTitle: { fontWeight: 'bold', fontSize: '15px', marginBottom: '4px' },
    desc: { fontSize: '14px', color: '#64748b', marginBottom: '6px' },
    time: { fontSize: '12px', color: '#94a3b8' }
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <ArrowLeft style={{cursor:'pointer'}} onClick={() => navigate(-1)} />
        <h1 style={styles.title}>Notifications</h1>
      </div>

      {notificationsList.length === 0 ? (
        <p style={{textAlign:'center', color:'#94a3b8'}}>No new notifications</p>
      ) : (
        notificationsList.map(notif => (
          <div key={notif.id} style={styles.item}>
            <div style={styles.iconBox(notif.bgColor)}>{notif.icon}</div>
            <div style={styles.content}>
              <div style={styles.itemTitle}>{notif.typeLabel}</div>
              <div style={styles.desc}>{notif.desc}</div>
              <div style={styles.time}>{notif.time}</div>
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default Notifications;
