import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';

// Page Imports
import Dashboard from './pages/Dashboard';
import Events from './pages/Events';
import MyEvents from './pages/MyEvents';
import Profile from './pages/Profile';
import EditProfile from './pages/EditProfile';
import ChangePassword from './pages/ChangePassword';
import Settings from './pages/Settings'; 
import ThemeSelection from './pages/ThemeSelection';
import EventDetails from './pages/EventDetails';
import Notifications from './pages/Notifications';
import BottomNav from './components/BottomNav';

const AppContent = ({ 
  theme, setTheme, user, setUser, allEvents, handleRegister, 
  searchTerm, setSearchTerm, registeredEvents, filteredEvents, appStyles 
}) => {
  const location = useLocation();
  
  // Pages where the BottomNav (Home, Events, My Events, Profile) MUST show
  const showNavOn = ['/', '/events', '/my-events', '/profile', '/notifications'];
  const shouldShow = showNavOn.includes(location.pathname);

  return (
    <div style={appStyles}>
      <Routes>
        <Route path="/" element={<Dashboard user={user} events={filteredEvents} theme={theme} regCount={registeredEvents.length} onRegister={handleRegister} />} />
        <Route path="/events" element={<Events allEvents={allEvents} theme={theme} searchTerm={searchTerm} setSearchTerm={setSearchTerm} onRegister={handleRegister} />} />
        <Route path="/my-events" element={<MyEvents theme={theme} events={registeredEvents} />} />
        <Route path="/event-details/:id" element={<EventDetails allEvents={allEvents} theme={theme} onRegister={handleRegister} />} />
        <Route path="/notifications" element={<Notifications theme={theme} />} />
        <Route path="/profile" element={<Profile user={user} theme={theme} />} />
        <Route path="/edit-profile" element={<EditProfile user={user} setUser={setUser} theme={theme} />} />
        <Route path="/change-password" element={<ChangePassword theme={theme} />} />
        <Route path="/settings" element={<Settings theme={theme} />} />
        <Route path="/settings/theme" element={<ThemeSelection currentTheme={theme} setTheme={setTheme} />} />
      </Routes>
      {shouldShow && <BottomNav theme={theme} />}
    </div>
  );
};

function App() {
  const [theme, setTheme] = useState('light'); 
  const [searchTerm, setSearchTerm] = useState("");
  const [user, setUser] = useState({
    name: "Priya Sharma",
    email: "priya.sharma@university.edu",
    college: "Engineering Tech Institute",
    regNo: "ETI-2023-4589"
  });

  const [allEvents, setAllEvents] = useState([
    { id: 1, title: "AI & Future Tech Workshop", date: "Apr 05, 2024 · 09:00 AM", location: "Innovation Lab", category: "Tech", image: "https://images.unsplash.com/photo-1485827404703-89b55fcc595e?w=800&q=80", isRegistered: false },
    { id: 2, title: "Inter-College Music Battle", date: "Apr 12, 2024 · 05:00 PM", location: "Main Stadium", category: "Cultural", image: "https://images.unsplash.com/photo-1501281668745-f7f57925c3b4?w=800&q=80", isRegistered: false },
    { id: 3, title: "University Basketball Finals", date: "Apr 20, 2024 · 10:00 AM", location: "Indoor Sports Complex", category: "Sports", image: "https://images.unsplash.com/photo-1546519638-68e109498ffc?w=800&q=80", isRegistered: false }
  ]);

  const handleRegister = (eventId) => {
    setAllEvents(prev => prev.map(ev => ev.id === eventId ? { ...ev, isRegistered: true } : ev));
  };

  const filteredEvents = allEvents.filter(e => e.title.toLowerCase().includes(searchTerm.toLowerCase()));
  const registeredEvents = allEvents.filter(e => e.isRegistered);

  const appStyles = {
    maxWidth: '430px', margin: '0 auto', minHeight: '100vh', position: 'relative',
    backgroundColor: theme === 'dark' ? '#0f172a' : '#fff', transition: 'all 0.3s ease', overflowX: 'hidden'
  };

  return (
    <Router>
      <AppContent 
        theme={theme} setTheme={setTheme} user={user} setUser={setUser}
        allEvents={allEvents} handleRegister={handleRegister}
        searchTerm={searchTerm} setSearchTerm={setSearchTerm}
        registeredEvents={registeredEvents} filteredEvents={filteredEvents}
        appStyles={appStyles}
      />
    </Router>
  );
}

export default App;
