import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { Eye, EyeOff, ArrowLeft, ArrowRight, AlertCircle } from 'lucide-react';

const StudentSignUp = ({ onLogin }) => {
  const navigate = useNavigate();
  const [step, setStep] = useState(1); 
  const [isStudent, setIsStudent] = useState(true);
  const [showPass, setShowPass] = useState(false);
  const [errors, setErrors] = useState({}); // Store error messages

  const [isDesktop, setIsDesktop] = useState(window.innerWidth > 900);
  useEffect(() => {
    const handleResize = () => setIsDesktop(window.innerWidth > 900);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    collegeName: '',
    rollNo: '',
    department: '',
    password: '',
    confirmPassword: ''
  });

  const styles = {
    // Page & Card Layout
    pageContainer: {
      height: '100vh', width: '100vw', display: 'flex', alignItems: 'center', justifyContent: 'center',
      fontFamily: "'Inter', sans-serif", backgroundColor: '#f3f4f6', overflow: 'hidden'
    },
    mainWrapper: {
      display: 'flex', width: isDesktop ? '950px' : '95%', height: isDesktop ? '600px' : 'auto', maxHeight: '95vh',
      backgroundColor: '#fff', borderRadius: '24px', overflow: 'hidden',
      boxShadow: '0 20px 60px -10px rgba(0, 0, 0, 0.15)', flexDirection: 'row'
    },

    // Left Panel (Purple)
    leftPanel: {
      flex: 1, background: 'linear-gradient(135deg, #6366f1 0%, #8b5cf6 100%)',
      display: isDesktop ? 'flex' : 'none', flexDirection: 'column', alignItems: 'center', justifyContent: 'center',
      color: '#fff', padding: '40px', textAlign: 'center'
    },
    glassCard: {
      backgroundColor: 'rgba(255, 255, 255, 0.1)', backdropFilter: 'blur(10px)', borderRadius: '20px',
      padding: '20px', border: '1px solid rgba(255, 255, 255, 0.2)', marginTop: '20px', maxWidth: '300px'
    },
    heroImage: { width: '100%', borderRadius: '10px', objectFit: 'cover' },

    // Right Panel (White Form)
    rightPanel: {
      flex: 1.2, padding: isDesktop ? '40px 60px' : '30px', display: 'flex', flexDirection: 'column',
      justifyContent: 'center', backgroundColor: '#fff', position: 'relative'
    },

    header: { textAlign: 'center', marginBottom: '25px' },
    title: { fontSize: '26px', fontWeight: '800', color: '#1f2937', marginBottom: '8px' },
    stepIndicator: { fontSize: '13px', color: '#6366f1', fontWeight: '600', textTransform: 'uppercase', letterSpacing: '1px' },

    inputGroup: { display: 'flex', flexDirection: 'column', gap: '6px', marginBottom: '16px' },
    label: { fontSize: '13px', fontWeight: '700', color: '#374151', marginLeft: '2px' },

    // Input styling with error state logic
    input: (hasError) => ({
      width: '100%', padding: '14px', borderRadius: '12px', 
      border: hasError ? '2px solid #ef4444' : '2px solid #e5e7eb', // Red border if error
      backgroundColor: '#fff', fontSize: '15px', outline: 'none', color: '#111827',
      transition: 'border-color 0.2s'
    }),

    errorText: { fontSize: '12px', color: '#ef4444', display: 'flex', alignItems: 'center', gap: '4px', marginTop: '2px' },

    actionBtn: {
      width: '100%', padding: '16px', borderRadius: '12px', border: 'none',
      background: '#6366f1', color: '#fff', fontSize: '16px', fontWeight: 'bold',
      cursor: 'pointer', marginTop: '10px', boxShadow: '0 4px 12px rgba(99, 102, 241, 0.3)',
      display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px'
    },
    backBtn: {
      background: 'none', border: 'none', color: '#6b7280', fontSize: '14px', fontWeight: '600',
      cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '5px', marginBottom: '20px',
      alignSelf: 'flex-start'
    },

    toggleContainer: {
      display: 'flex', backgroundColor: '#f3f4f6', borderRadius: '10px', padding: '4px', marginBottom: '20px'
    },
    toggleBtn: (active) => ({
      flex: 1, padding: '8px', borderRadius: '8px', border: 'none', fontSize: '13px', fontWeight: '600',
      cursor: 'pointer', backgroundColor: active ? '#6366f1' : 'transparent', color: active ? '#fff' : '#6b7280', transition: 'all 0.2s'
    })
  };

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
    // Clear error when user starts typing
    if(errors[e.target.name]) {
      setErrors({...errors, [e.target.name]: null});
    }
  };

  // --- VALIDATION LOGIC ---
  const validateStep1 = () => {
    let newErrors = {};
    if(!formData.fullName.trim()) newErrors.fullName = "Full name is required";
    if(!formData.email.trim()) newErrors.email = "Email is required";
    else if(!/\S+@\S+\.\S+/.test(formData.email)) newErrors.email = "Invalid email format";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateStep2 = () => {
    let newErrors = {};
    if(!formData.collegeName.trim()) newErrors.collegeName = "College name is required";
    if(!formData.rollNo.trim()) newErrors.rollNo = isStudent ? "Roll number is required" : "Employee ID is required";
    if(!formData.department || formData.department === "Select") newErrors.department = "Please select a department";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateStep3 = () => {
    let newErrors = {};
    if(!formData.password) newErrors.password = "Password is required";
    else if(formData.password.length < 6) newErrors.password = "Password must be at least 6 chars";

    if(formData.password !== formData.confirmPassword) newErrors.confirmPassword = "Passwords do not match";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleNext = () => { 
    if(step === 1) {
      if(validateStep1()) setStep(2);
    } else if (step === 2) {
      if(validateStep2()) setStep(3);
    }
  };

  const handleBack = () => { if(step > 1) setStep(step - 1); };

  const handleSubmit = () => {
    if(validateStep3()) {
      onLogin({ 
        name: formData.fullName,
        college: formData.collegeName, 
        role: isStudent ? 'student' : 'teacher',
      });
      navigate('/');
    }
  };

  // --- RENDER STEPS ---
  const renderStep1 = () => (
    <>
      <div style={styles.inputGroup}>
        <label style={styles.label}>Full Name</label>
        <input style={styles.input(errors.fullName)} placeholder="e.g. John Doe" name="fullName" value={formData.fullName} onChange={handleChange} autoFocus />
        {errors.fullName && <div style={styles.errorText}><AlertCircle size={12}/> {errors.fullName}</div>}
      </div>
      <div style={styles.inputGroup}>
        <label style={styles.label}>Email Address</label>
        <input style={styles.input(errors.email)} placeholder="you@example.com" name="email" type="email" value={formData.email} onChange={handleChange} />
        {errors.email && <div style={styles.errorText}><AlertCircle size={12}/> {errors.email}</div>}
      </div>
      <button style={styles.actionBtn} onClick={handleNext}>
        Continue <ArrowRight size={18} />
      </button>
    </>
  );

  const renderStep2 = () => (
    <>
      <div style={styles.toggleContainer}>
        <button style={styles.toggleBtn(isStudent)} onClick={() => setIsStudent(true)}>Student</button>
        <button style={styles.toggleBtn(!isStudent)} onClick={() => setIsStudent(false)}>Teacher</button>
      </div>

      <div style={styles.inputGroup}>
        <label style={styles.label}>College Name</label>
        <input style={styles.input(errors.collegeName)} placeholder="Engineering Tech Institute" name="collegeName" value={formData.collegeName} onChange={handleChange} />
        {errors.collegeName && <div style={styles.errorText}><AlertCircle size={12}/> {errors.collegeName}</div>}
      </div>

      <div style={{display:'flex', gap:'15px'}}>
        <div style={{flex:1, ...styles.inputGroup}}>
          <label style={styles.label}>{isStudent ? "Roll No" : "Emp ID"}</label>
          <input style={styles.input(errors.rollNo)} placeholder={isStudent ? "ID-2024" : "EMP-01"} name="rollNo" value={formData.rollNo} onChange={handleChange} />
          {errors.rollNo && <div style={styles.errorText}><AlertCircle size={12}/> {errors.rollNo}</div>}
        </div>
        <div style={{flex:1, ...styles.inputGroup}}>
          <label style={styles.label}>Dept</label>
          <select style={styles.input(errors.department)} name="department" value={formData.department} onChange={handleChange}>
            <option>Select</option>
            <option>CSE</option>
            <option>ECE</option>
            <option>MECH</option>
          </select>
          {errors.department && <div style={styles.errorText}><AlertCircle size={12}/> {errors.department}</div>}
        </div>
      </div>
      <button style={styles.actionBtn} onClick={handleNext}>
        Continue <ArrowRight size={18} />
      </button>
    </>
  );

  const renderStep3 = () => (
    <>
      <div style={styles.inputGroup}>
        <label style={styles.label}>Password</label>
        <div style={{position:'relative'}}>
           <input style={styles.input(errors.password)} placeholder="â€¢â€¢â€¢â€¢â€¢â€¢" name="password" type={showPass ? "text" : "password"} value={formData.password} onChange={handleChange} />
           <div style={{position:'absolute', right:15, top:15, cursor:'pointer', color:'#9ca3af'}} onClick={() => setShowPass(!showPass)}>
             {showPass ? <EyeOff size={20}/> : <Eye size={20}/>}
           </div>
        </div>
        {errors.password && <div style={styles.errorText}><AlertCircle size={12}/> {errors.password}</div>}
      </div>

      <div style={styles.inputGroup}>
        <label style={styles.label}>Confirm Password</label>
        <input style={styles.input(errors.confirmPassword)} placeholder="â€¢â€¢â€¢â€¢â€¢â€¢" name="confirmPassword" type="password" value={formData.confirmPassword} onChange={handleChange} />
        {errors.confirmPassword && <div style={styles.errorText}><AlertCircle size={12}/> {errors.confirmPassword}</div>}
      </div>

      <div style={{display:'flex', gap:'10px', alignItems:'center', marginTop:'10px', marginBottom:'20px'}}>
        <input type="checkbox" style={{width:'18px', height:'18px', accentColor:'#6366f1'}} />
        <span style={{fontSize:'13px', color:'#6b7280'}}>I agree to Terms & Privacy Policy</span>
      </div>

      <button style={{...styles.actionBtn, background:'#10b981'}} onClick={handleSubmit}>
        Complete Registration
      </button>
    </>
  );

  return (
    <div style={styles.pageContainer}>
      <div style={styles.mainWrapper}>

        {/* LEFT PANEL */}
        <div style={styles.leftPanel}>
          <div style={{fontSize:'42px', marginBottom:'10px'}}>ðŸš€</div>
          <h1 style={{fontSize:'32px', fontWeight:'800', marginBottom:'10px'}}>Join Us!</h1>
          <p style={{fontSize:'15px', opacity:0.9, marginBottom:'30px'}}>Create your account in 3 easy steps.</p>
          <div style={styles.glassCard}>
            <img src="https://images.unsplash.com/photo-1523240795612-9a054b0db644?w=400&q=80" alt="Students" style={styles.heroImage} />
          </div>
        </div>

        {/* RIGHT PANEL */}
        <div style={styles.rightPanel}>

          {step > 1 && (
            <button style={styles.backBtn} onClick={handleBack}>
              <ArrowLeft size={16} /> Back
            </button>
          )}

          <div style={styles.header}>
            <div style={styles.stepIndicator}>Step {step} of 3</div>
            <h2 style={styles.title}>
              {step === 1 ? "Let's get started" : step === 2 ? "Academic Details" : "Set Password"}
            </h2>
          </div>

          <div style={{animation: 'fadeIn 0.3s ease-in-out'}}>
            {step === 1 && renderStep1()}
            {step === 2 && renderStep2()}
            {step === 3 && renderStep3()}
          </div>

          <div style={{textAlign:'center', marginTop:'25px', fontSize:'14px', color:'#6b7280'}}>
            Already have an account? <Link to="/signin" style={{color: '#6366f1', fontWeight:'bold', textDecoration:'none'}}>Login</Link>
          </div>

        </div>

      </div>
    </div>
  );
};

export default StudentSignUp;
