import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { Eye, EyeOff, ArrowLeft, LogIn, AlertCircle } from 'lucide-react';

const StudentSignIn = ({ onLogin }) => {
  const navigate = useNavigate();
  const [isStudent, setIsStudent] = useState(true);
  const [showPass, setShowPass] = useState(false);
  const [errors, setErrors] = useState({});

  // Responsive Check
  const [isDesktop, setIsDesktop] = useState(window.innerWidth > 900);
  useEffect(() => {
    const handleResize = () => setIsDesktop(window.innerWidth > 900);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const styles = {
    // Page & Card Layout
    pageContainer: {
      height: '100vh', width: '100vw', display: 'flex', alignItems: 'center', justifyContent: 'center',
      fontFamily: "'Inter', sans-serif", backgroundColor: '#f3f4f6', overflow: 'hidden'
    },
    mainWrapper: {
      display: 'flex', width: isDesktop ? '900px' : '95%', 
      // Changed height to 'auto' with padding to prevent cutting off content
      height: 'auto', minHeight: isDesktop ? '550px' : 'auto', maxHeight: '95vh',
      backgroundColor: '#fff', borderRadius: '24px', overflow: 'hidden',
      boxShadow: '0 20px 60px -10px rgba(0, 0, 0, 0.15)', flexDirection: 'row'
    },

    // Left Panel (Purple)
    leftPanel: {
      flex: 1, background: 'linear-gradient(135deg, #6366f1 0%, #8b5cf6 100%)',
      display: isDesktop ? 'flex' : 'none', flexDirection: 'column', alignItems: 'center', justifyContent: 'center',
      color: '#fff', padding: '40px', textAlign: 'center'
    },
    glassCard: {
      backgroundColor: 'rgba(255, 255, 255, 0.1)', backdropFilter: 'blur(10px)', borderRadius: '20px',
      padding: '20px', border: '1px solid rgba(255, 255, 255, 0.2)', marginTop: '20px', maxWidth: '300px'
    },
    heroImage: { width: '100%', borderRadius: '10px', objectFit: 'cover' },

    // Right Panel (White Form)
    rightPanel: {
      flex: 1.2, padding: isDesktop ? '40px 60px' : '30px', display: 'flex', flexDirection: 'column',
      justifyContent: 'center', backgroundColor: '#fff', position: 'relative'
    },

    header: { textAlign: 'center', marginBottom: '25px' },
    title: { fontSize: '28px', fontWeight: '800', color: '#1f2937', marginBottom: '8px' },
    subtitle: { fontSize: '14px', color: '#6b7280' },

    inputGroup: { display: 'flex', flexDirection: 'column', gap: '6px', marginBottom: '16px' },
    label: { fontSize: '13px', fontWeight: '700', color: '#374151', marginLeft: '2px' },
    
    input: (hasError) => ({
      width: '100%', padding: '14px', borderRadius: '12px', 
      border: hasError ? '2px solid #ef4444' : '2px solid #e5e7eb',
      backgroundColor: '#fff', fontSize: '15px', outline: 'none', color: '#111827',
      transition: 'border-color 0.2s',
      boxSizing: 'border-box'
    }),
    
    errorText: { fontSize: '12px', color: '#ef4444', display: 'flex', alignItems: 'center', gap: '4px', marginTop: '2px' },

    actionBtn: {
      width: '100%', padding: '16px', borderRadius: '12px', border: 'none',
      background: '#6366f1', color: '#fff', fontSize: '16px', fontWeight: 'bold',
      cursor: 'pointer', marginTop: '10px', boxShadow: '0 4px 12px rgba(99, 102, 241, 0.3)',
      display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px',
      transition: 'transform 0.1s'
    },
    
    backBtn: {
      background: 'none', border: 'none', color: '#6b7280', fontSize: '14px', fontWeight: '600',
      cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '5px', marginBottom: '20px',
      alignSelf: 'flex-start'
    },

    toggleContainer: {
      display: 'flex', backgroundColor: '#f3f4f6', borderRadius: '10px', padding: '4px', marginBottom: '20px'
    },
    toggleBtn: (active) => ({
      flex: 1, padding: '10px', borderRadius: '8px', border: 'none', fontSize: '13px', fontWeight: '600',
      cursor: 'pointer', backgroundColor: active ? '#6366f1' : 'transparent', color: active ? '#fff' : '#6b7280', transition: 'all 0.2s'
    }),

    forgotPass: { textAlign: 'right', fontSize: '13px', color: '#6366f1', fontWeight: '600', cursor: 'pointer', marginTop: '-10px', marginBottom: '20px' },
    
    // Explicit style for the bottom link area
    bottomLink: {
      textAlign: 'center', 
      marginTop: '25px', 
      fontSize: '14px', 
      color: '#6b7280',
      paddingBottom: '10px' // Add padding to ensure it's not cut off
    }
  };

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
    if(errors[e.target.name]) setErrors({...errors, [e.target.name]: null});
  };
  

  const validate = () => {
    let newErrors = {};
    if(!formData.email.trim()) newErrors.email = "Email is required";
    if(!formData.password) newErrors.password = "Password is required";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // ... inside StudentSignUp.jsx

  // ... inside StudentSignIn.jsx

  const handleSubmit = () => {
    if(validate()) {
      // MOCK DATA SIMULATION (Since no backend)
      onLogin({ 
        name: "Demo User", // You can change this
        email: formData.email,
        role: isStudent ? 'student' : 'teacher',
        college: 'Engineering Tech Institute',
        department: 'CSE',       // <--- Default for Sign In demo
        rollNo: '24-CSE-001',    // <--- Default for Sign In demo
        profileImage: 'https://images.unsplash.com/photo-1494790108377-be9c29b29330?w=200'
      });
      navigate('/');
    }
  };

  // ... rest of component

  
  // ... rest of component

  return (
    <div style={styles.pageContainer}>
      <div style={styles.mainWrapper}>
        
        {/* LEFT PANEL */}
        <div style={styles.leftPanel}>
          <div style={{fontSize:'48px', marginBottom:'15px'}}>ðŸ‘‹</div>
          <h1 style={{fontSize:'32px', fontWeight:'800', marginBottom:'10px'}}>Welcome Back!</h1>
          <p style={{fontSize:'15px', opacity:0.9, marginBottom:'30px', maxWidth:'280px'}}>
            Log in to access your event dashboard, tickets, and certificates.
          </p>
          <div style={styles.glassCard}>
            <img src="https://images.unsplash.com/photo-1543269865-cbf427effbad?w=400&q=80" alt="Login Illustration" style={styles.heroImage} />
          </div>
        </div>

        {/* RIGHT PANEL */}
        <div style={styles.rightPanel}>
          
          <button style={styles.backBtn} onClick={() => navigate('/')}>
            <ArrowLeft size={16} /> Home
          </button>

          <div style={styles.header}>
            <h2 style={styles.title}>Login Account</h2>
            <p style={styles.subtitle}>Please sign in to continue</p>
          </div>

          <div style={styles.toggleContainer}>
            <button style={styles.toggleBtn(isStudent)} onClick={() => setIsStudent(true)}>Student</button>
            <button style={styles.toggleBtn(!isStudent)} onClick={() => setIsStudent(false)}>Teacher</button>
          </div>

          {/* FORM */}
          <div style={{animation: 'fadeIn 0.3s ease-in-out'}}>
            
            <div style={styles.inputGroup}>
              <label style={styles.label}>Email Address</label>
              <input 
                style={styles.input(errors.email)} 
                placeholder="you@example.com" 
                name="email" 
                type="email" 
                value={formData.email} 
                onChange={handleChange} 
                autoFocus
              />
              {errors.email && <div style={styles.errorText}><AlertCircle size={12}/> {errors.email}</div>}
            </div>

            <div style={styles.inputGroup}>
              <label style={styles.label}>Password</label>
              <div style={{position:'relative'}}>
                 <input 
                   style={styles.input(errors.password)} 
                   placeholder="â€¢â€¢â€¢â€¢â€¢â€¢" 
                   name="password" 
                   type={showPass ? "text" : "password"} 
                   value={formData.password} 
                   onChange={handleChange} 
                 />
                 <div style={{position:'absolute', right:15, top:15, cursor:'pointer', color:'#9ca3af'}} onClick={() => setShowPass(!showPass)}>
                   {showPass ? <EyeOff size={20}/> : <Eye size={20}/>}
                 </div>
              </div>
              {errors.password && <div style={styles.errorText}><AlertCircle size={12}/> {errors.password}</div>}
            </div>

            <div style={styles.forgotPass}>Forgot Password?</div>

            <button 
              style={styles.actionBtn} 
              onClick={handleSubmit}
              onMouseEnter={(e) => e.currentTarget.style.transform = 'scale(1.02)'}
              onMouseLeave={(e) => e.currentTarget.style.transform = 'scale(1)'}
            >
              Login <LogIn size={18} />
            </button>
          </div>

          {/* DYNAMIC REGISTRATION LINK */}
          <div style={styles.bottomLink}>
            Don't have an account?{' '}
            <Link 
              to={isStudent ? "/signup" : "/teacher-signup"} 
              style={{color: '#6366f1', fontWeight:'bold', textDecoration:'none'}}
            >
              Register {isStudent ? 'as Student' : 'as Teacher'}
            </Link>
          </div>

        </div>

      </div>
    </div>
  );
};

export default StudentSignIn;
