import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';

const StudentSignUp = ({ onLogin }) => {
  const navigate = useNavigate();
  const [isStudent, setIsStudent] = useState(true);

  const [formData, setFormData] = useState({
    fullName: '',
    collegeName: '',
    rollNo: '',
    department: '',
    email: '',
    password: '',
    confirmPassword: ''
  });

  const styles = {
    // 1. PAGE CONTAINER (Purple Background, Centered)
    pageContainer: {
      height: '100vh',
      width: '100vw',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontFamily: "'Inter', sans-serif",
      background: 'linear-gradient(135deg, #7c3aed 0%, #a855f7 100%)', // Purple Gradient
      overflow: 'hidden' // No Scroll on the page itself
    },

    // 2. THE CARD (The only thing visible now)
    card: {
      width: '90%',
      maxWidth: '420px', // Standard mobile width limits
      maxHeight: '90vh', // Ensures it fits on screen without main scroll
      backgroundColor: '#fff',
      borderRadius: '24px',
      padding: '30px',
      boxShadow: '0 20px 60px rgba(0,0,0,0.2)',
      display: 'flex',
      flexDirection: 'column',
      // Allow scroll INSIDE the card if the form is too tall for a small screen
      overflowY: 'auto' 
    },

    // Header
    header: { textAlign: 'center', marginBottom: '20px' },
    title: { fontSize: '24px', fontWeight: '800', color: '#1e293b', marginBottom: '4px' },
    subtitle: { fontSize: '13px', color: '#64748b' },

    // Toggle Switch
    toggleContainer: {
      display: 'flex',
      backgroundColor: '#f1f5f9',
      borderRadius: '10px',
      padding: '4px',
      marginBottom: '20px'
    },
    toggleBtn: (active) => ({
      flex: 1, padding: '10px', borderRadius: '8px', border: 'none',
      fontSize: '13px', fontWeight: '600', cursor: 'pointer',
      backgroundColor: active ? '#7c3aed' : 'transparent', 
      color: active ? '#fff' : '#64748b',
      transition: 'all 0.2s'
    }),

    // Form Layout
    form: { display: 'flex', flexDirection: 'column', gap: '12px' },
    fieldGroup: { display: 'flex', flexDirection: 'column', gap: '4px' },

    // Labels & Inputs
    label: { fontSize: '12px', fontWeight: '700', color: '#374151', marginLeft: '2px' },
    input: {
      width: '100%', padding: '12px', borderRadius: '10px',
      border: '1px solid #e2e8f0', backgroundColor: '#f8fafc', 
      fontSize: '13px', outline: 'none', color: '#333',
      boxSizing: 'border-box'
    },

    // Rows for side-by-side inputs
    row: { display: 'flex', gap: '10px' },
    col: { flex: 1, display: 'flex', flexDirection: 'column', gap: '4px' },

    // Actions
    terms: { fontSize: '12px', color: '#64748b', display: 'flex', alignItems: 'center', gap: '6px', marginTop: '5px' },

    submitBtn: {
      width: '100%', padding: '14px', borderRadius: '12px', border: 'none',
      backgroundColor: '#7c3aed', color: '#fff',
      fontSize: '15px', fontWeight: 'bold', cursor: 'pointer', marginTop: '10px',
      boxShadow: '0 4px 15px rgba(124, 58, 237, 0.3)'
    },

    loginLink: { textAlign: 'center', marginTop: '15px', fontSize: '13px', color: '#64748b' }
  };

  const handleChange = (e) => setFormData({...formData, [e.target.name]: e.target.value});

  const handleSubmit = () => {
    onLogin({ 
      name: formData.fullName || 'New User',
      college: formData.collegeName || 'Engineering Tech Institute', 
      role: isStudent ? 'student' : 'teacher',
    });
    navigate('/');
  };

  return (
    <div style={styles.pageContainer}>
      <div style={styles.card}>

        {/* Header */}
        <div style={styles.header}>
          <h2 style={styles.title}>Create Account</h2>
          <p style={styles.subtitle}>Sign up to get started</p>
        </div>

        {/* Toggle Student/Teacher */}
        <div style={styles.toggleContainer}>
          <button style={styles.toggleBtn(isStudent)} onClick={() => setIsStudent(true)}>Student</button>
          <button style={styles.toggleBtn(!isStudent)} onClick={() => setIsStudent(false)}>Teacher</button>
        </div>

        {/* The Form */}
        <div style={styles.form}>

          <div style={styles.fieldGroup}>
            <label style={styles.label}>Full Name</label>
            <input style={styles.input} placeholder="e.g. John Doe" name="fullName" onChange={handleChange} />
          </div>

          <div style={styles.fieldGroup}>
            <label style={styles.label}>College Name</label>
            <input style={styles.input} placeholder="Engineering Tech Institute" name="collegeName" onChange={handleChange} />
          </div>

          <div style={styles.row}>
             <div style={styles.col}>
               <label style={styles.label}>{isStudent ? "Roll No" : "Emp ID"}</label>
               <input style={styles.input} placeholder={isStudent ? "ID-2024" : "EMP-01"} name="rollNo" onChange={handleChange} />
             </div>
             <div style={styles.col}>
               <label style={styles.label}>Dept</label>
               <select style={styles.input} name="department" onChange={handleChange}>
                 <option>Select</option>
                 <option>CSE</option>
                 <option>ECE</option>
                 <option>MECH</option>
               </select>
             </div>
          </div>

          <div style={styles.fieldGroup}>
            <label style={styles.label}>Email Address</label>
            <input style={styles.input} placeholder="you@example.com" name="email" type="email" onChange={handleChange} />
          </div>

          <div style={styles.row}>
            <div style={styles.col}>
               <label style={styles.label}>Password</label>
               <input style={styles.input} placeholder="••••••" name="password" type="password" onChange={handleChange} />
            </div>
            <div style={styles.col}>
               <label style={styles.label}>Confirm</label>
               <input style={styles.input} placeholder="••••••" name="confirmPassword" type="password" onChange={handleChange} />
            </div>
          </div>

          <div style={styles.terms}>
             <input type="checkbox" style={{cursor: 'pointer'}} />
             <span>I agree to Terms & Privacy</span>
          </div>

          <button style={styles.submitBtn} onClick={handleSubmit}>Register</button>
        </div>

         <div style={styles.loginLink}>
           Already have an account? <Link to="/signin" style={{color: '#7c3aed', fontWeight:'bold', textDecoration:'none'}}>Login</Link>
         </div>

      </div>
    </div>
  );
};

export default StudentSignUp;
