import React from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowLeft, ChevronRight } from 'lucide-react';

const Settings = ({ theme }) => {
  const navigate = useNavigate();

  const settingsItems = [
    { title: "Notifications", desc: "Manage push and in-app alerts", path: "/notifications" },
    { title: "App Theme", desc: "Light / Dark appearance", status: theme === 'dark' ? "Dark" : "Light", path: "/settings/theme" },
    { title: "Language", desc: "Choose your preferred language", status: "English", path: "/settings/language" },
    { title: "Privacy Policy", desc: "How your data is collected", path: "/privacy" },
    { title: "Terms & Conditions", desc: "Review app guidelines", path: "/terms" }
  ];

  const styles = {
    container: { backgroundColor: theme === 'dark' ? '#0f172a' : '#f8fafc', minHeight: '100vh', color: theme === 'dark' ? '#fff' : '#1e293b' },
    card: { display: 'flex', alignItems: 'center', backgroundColor: theme === 'dark' ? '#1e293b' : '#fff', padding: '16px', margin: '12px 16px', borderRadius: '16px', border: theme === 'dark' ? '1px solid #334155' : '1px solid #f1f5f9', cursor: 'pointer' },
    statusBadge: { backgroundColor: '#f0f4ff', color: '#5c5cfc', fontSize: '11px', padding: '4px 10px', borderRadius: '10px', fontWeight: 'bold' }
  };

  return (
    <div style={styles.container}>
      <header style={{ display: 'flex', alignItems: 'center', padding: '16px', backgroundColor: theme === 'dark' ? '#1e293b' : '#fff' }}>
        <ArrowLeft size={24} onClick={() => navigate(-1)} style={{ cursor: 'pointer' }} />
        <h2 style={{ marginLeft: '16px', fontSize: '18px', fontWeight: 'bold' }}>Settings</h2>
      </header>
      <div style={{ marginTop: '10px' }}>
        {settingsItems.map((item, index) => (
          <div key={index} style={styles.card} onClick={() => navigate(item.path)}>
            <div style={{ flex: 1 }}>
              <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                <span style={{ fontWeight: 'bold', fontSize: '15px' }}>{item.title}</span>
                {item.status && <span style={styles.statusBadge}>{item.status}</span>}
              </div>
              <p style={{ fontSize: '12px', color: '#94a3b8', margin: '4px 0 0 0' }}>{item.desc}</p>
            </div>
            <ChevronRight size={20} color="#cbd5e1" />
          </div>
        ))}
      </div>
    </div>
  );
};

export default Settings;
