import React, { useState } from 'react';
import { Bell, Clock, MapPin, Search } from 'lucide-react';
import Notifications from './Notifications'; // Import your separate Notifications file

const Dashboard = ({ user, events, theme, regCount }) => {
  const isDark = theme === 'dark';
  const [isNotiOpen, setIsNotiOpen] = useState(false);

  // 1. PAGE SWITCH LOGIC
  // If the user clicked the bell, show ONLY the Notifications component
  if (isNotiOpen) {
    return (
      <div style={{ 
        maxWidth: '400px', 
        margin: '0 auto', 
        padding: '16px', 
        minHeight: '100vh',
        backgroundColor: isDark ? '#0f172a' : '#fff' 
      }}>
        <Notifications theme={theme} setIsOpen={setIsNotiOpen} />
      </div>
    );
  }

  // 2. NORMAL DASHBOARD VIEW
  const styles = {
    container: { 
      maxWidth: '400px', 
      margin: '0 auto', 
      padding: '16px', 
      paddingBottom: '100px', 
      minHeight: '100vh',
      backgroundColor: isDark ? '#0f172a' : '#fff' 
    },
    header: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '24px' },
    greeting: { fontSize: '28px', fontWeight: 'bold', color: isDark ? '#fff' : '#1e293b' },
    eventCard: { 
      backgroundColor: isDark ? '#1e293b' : '#fff', 
      borderRadius: '20px', padding: '12px', marginBottom: '16px',
      border: isDark ? '1px solid #334155' : '1px solid #f1f5f9' 
    }
  };

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
          <div style={{ width: '36px', height: '36px', borderRadius: '50%', backgroundColor: '#2563eb', color: '#fff', display: 'flex', justifyContent: 'center', alignItems: 'center', fontWeight: 'bold' }}>ES</div>
          <span style={{ fontWeight: 'bold', color: isDark ? '#fff' : '#1e293b' }}>EventSphere</span>
        </div>
        <Bell 
            size={22} 
            onClick={() => setIsNotiOpen(true)} 
            style={{ cursor: 'pointer', color: isDark ? '#fff' : '#1e293b' }} 
        />
      </header>

      <h1 style={styles.greeting}>Hi Priya ðŸ‘‹</h1>
      <p style={{ color: '#64748b', fontSize: '14px', marginBottom: '24px' }}>Explore college events</p>

      <h3 style={{ fontSize: '16px', marginBottom: '16px', color: isDark ? '#fff' : '#1e293b' }}>Upcoming Events</h3>
      {events.map(event => (
        <div key={event.id} style={styles.eventCard}>
          <img src={event.image} alt={event.title} style={{ width: '100%', height: '160px', objectFit: 'cover', borderRadius: '14px', marginBottom: '12px' }} />
          <h4 style={{ margin: '0 0 6px 0', fontSize: '15px', color: isDark ? '#fff' : '#1e293b' }}>{event.title}</h4>
          <div style={{ display: 'flex', alignItems: 'center', gap: '6px', fontSize: '11px', color: '#94a3b8' }}>
            <Clock size={14} /> {event.date}
          </div>
          <button style={{ width: '100%', backgroundColor: '#2563eb', color: '#fff', border: 'none', padding: '12px', borderRadius: '12px', fontWeight: 'bold', marginTop: '12px' }}>
            Register
          </button>
        </div>
      ))}
    </div>
  );
};

export default Dashboard;
