import React, { useState } from 'react';
import { 
  Home, Calendar, Ticket, User, Settings, LogOut, Menu 
} from 'lucide-react';
import { useNavigate, useLocation } from 'react-router-dom';

const Sidebar = ({ user, theme, onLogout }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const isDark = theme === 'dark';
  
  const [isExpanded, setIsExpanded] = useState(false);

  const menuItems = [
    { label: 'Dashboard', icon: <Home size={22} />, path: '/' },
    { label: 'Events', icon: <Calendar size={22} />, path: '/events' },
    { label: 'My Tickets', icon: <Ticket size={22} />, path: '/my-events' },
    { label: 'Profile', icon: <User size={22} />, path: '/profile' },
    { label: 'Settings', icon: <Settings size={22} />, path: '/settings' },
  ];

  const styles = {
    // 1. SIDEBAR CONTAINER
    sidebar: {
      height: '100vh',
      // Collapsed width matches the icon button width exactly (plus minimal padding)
      width: isExpanded ? '250px' : '50px', 
      backgroundColor: isDark ? '#1e293b' : '#fff',
      borderRight: isDark ? '1px solid #334155' : '1px solid #e2e8f0',
      display: 'flex',
      flexDirection: 'column',
      padding: '12px 0', 
      position: 'fixed',
      left: 0,
      top: 0,
      zIndex: 1000,
      transition: 'width 0.3s ease',
      overflowX: 'hidden',
    },

    // 2. TOGGLE BUTTON
    topSection: {
      display: 'flex',
      alignItems: 'center',
      gap: '12px',
      marginBottom: '20px',
      paddingLeft: isExpanded ? '16px' : '0', 
      justifyContent: isExpanded ? 'flex-start' : 'center',
      height: '40px',
      overflow: 'hidden'
    },
    menuBtn: {
      cursor: 'pointer',
      color: isDark ? '#fff' : '#1e293b',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      width: '40px',
      height: '40px',
      borderRadius: '8px',
      // No extra margin that causes shifting
    },
    logoText: {
      fontSize: '18px',
      fontWeight: '800',
      color: '#6366f1',
      whiteSpace: 'nowrap',
      opacity: isExpanded ? 1 : 0,
      transition: 'opacity 0.2s',
      display: isExpanded ? 'block' : 'none'
    },

    // 3. NAV SECTION
    nav: {
      flex: 1,
      display: 'flex',
      flexDirection: 'column',
      gap: '8px', 
      width: '100%',
      // Remove horizontal padding to let items touch edges if needed
      padding: '0' 
    },
    
    navItem: (isActive) => ({
      display: 'flex',
      alignItems: 'center',
      gap: '14px',
      
      // KEY FIX: When collapsed, width is 100% of the sidebar (no side gaps)
      // When expanded, we give it side spacing for the "rounded button" look
      width: isExpanded ? '90%' : '100%', 
      
      // Center horizontally in the sidebar
      margin: isExpanded ? '0 auto' : '0',
      
      // Padding logic
      padding: isExpanded ? '12px' : '12px 0', 
      
      // Border Radius: Round when expanded, Square-ish when collapsed (to fill space)
      borderRadius: isExpanded ? '10px' : '0',
      
      cursor: 'pointer',
      
      backgroundColor: isActive ? (isDark ? '#334155' : '#eff6ff') : 'transparent',
      
      // Add a colored left strip for active state when collapsed (Visual Indicator)
      borderLeft: (!isExpanded && isActive) ? '3px solid #6366f1' : '3px solid transparent',
      
      color: isActive ? '#6366f1' : (isDark ? '#94a3b8' : '#64748b'),
      transition: 'all 0.2s',
      overflow: 'hidden',
      whiteSpace: 'nowrap',
      
      // Centering logic
      justifyContent: isExpanded ? 'flex-start' : 'center'
    }),
    
    navLabel: {
      fontWeight: '600',
      fontSize: '14px',
      opacity: isExpanded ? 1 : 0,
      display: isExpanded ? 'block' : 'none'
    },

    // 4. BOTTOM USER SECTION
    userSection: {
      marginTop: 'auto',
      borderTop: isDark ? '1px solid #334155' : '1px solid #f1f5f9',
      padding: '15px 0',
      display: 'flex',
      alignItems: 'center',
      gap: '12px',
      justifyContent: isExpanded ? 'flex-start' : 'center',
      paddingLeft: isExpanded ? '16px' : '0',
      overflow: 'hidden'
    },
    avatar: {
      width: '32px',
      height: '32px',
      borderRadius: '50%',
      objectFit: 'cover',
      minWidth: '32px',
      border: '1px solid #e2e8f0'
    },
    userDetails: {
      opacity: isExpanded ? 1 : 0,
      whiteSpace: 'nowrap',
      display: isExpanded ? 'block' : 'none'
    }
  };

  return (
    <>
      <div style={{ width: isExpanded ? '250px' : '50px', transition: 'width 0.3s ease' }}></div>

      <div style={styles.sidebar}>
        
        {/* Toggle */}
        <div style={styles.topSection}>
          <div style={styles.menuBtn} onClick={() => setIsExpanded(!isExpanded)}>
            <Menu size={22} />
          </div>
          <div style={styles.logoText}>EventSphere</div>
        </div>

        {/* Nav Items */}
        <div style={styles.nav}>
          {menuItems.map((item) => {
            const isActive = location.pathname === item.path;
            return (
              <div 
                key={item.label} 
                style={styles.navItem(isActive)} 
                onClick={() => navigate(item.path)}
                title={!isExpanded ? item.label : ''} 
              >
                <div style={{display: 'flex', alignItems:'center', justifyContent:'center', minWidth: isExpanded ? '24px' : '100%'}}>
                   {item.icon}
                </div>
                <span style={styles.navLabel}>{item.label}</span>
              </div>
            );
          })}
        </div>

        {/* User */}
        <div style={styles.userSection}>
          <img src={user.profileImage} alt="User" style={styles.avatar} />
          <div style={styles.userDetails}>
            <div style={{fontWeight: 'bold', fontSize: '13px', color: isDark?'#fff':'#333'}}>
              {user.name.split(' ')[0]}
            </div>
            <div style={{fontSize: '11px', color: '#64748b'}}>Student</div>
          </div>
        </div>

        {/* Logout */}
        <div style={{ padding: '0', marginBottom: '10px' }}>
          <div 
            style={{ ...styles.navItem(false), color: '#ef4444' }} 
            onClick={onLogout}
            title={!isExpanded ? "Log Out" : ""}
          >
            <div style={{display: 'flex', alignItems:'center', justifyContent:'center', minWidth: isExpanded ? '24px' : '100%'}}>
               <LogOut size={22} />
            </div>
            <span style={styles.navLabel}>Log Out</span>
          </div>
        </div>

      </div>
    </>
  );
};

export default Sidebar;
