import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { Home, Calendar, Ticket, User } from 'lucide-react';

const BottomNav = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const isActive = (path) => location.pathname === path;

  return (
    <nav style={{ position: 'fixed', bottom: 0, left: '50%', transform: 'translateX(-50%)', width: '100%', maxWidth: '375px', backgroundColor: '#fff', display: 'flex', justifyContent: 'space-around', padding: '12px 0', borderTop: '1px solid #f1f5f9' }}>
      <div onClick={() => navigate('/')} style={{ color: isActive('/') ? '#2563eb' : '#94a3b8', textAlign: 'center' }}><Home size={22} /><div style={{fontSize: '10px'}}>Home</div></div>
      <div onClick={() => navigate('/events')} style={{ color: isActive('/events') ? '#2563eb' : '#94a3b8', textAlign: 'center' }}><Calendar size={22} /><div style={{fontSize: '10px'}}>Events</div></div>
      <div onClick={() => navigate('/my-events')} style={{ color: isActive('/my-events') ? '#2563eb' : '#94a3b8', textAlign: 'center' }}><Ticket size={22} /><div style={{fontSize: '10px'}}>My Events</div></div>
      <div onClick={() => navigate('/profile')} style={{ color: isActive('/profile') ? '#2563eb' : '#94a3b8', textAlign: 'center' }}><User size={22} /><div style={{fontSize: '10px'}}>Profile</div></div>
    </nav>
  );
};

export default BottomNav;
