import React from 'react';
import { Home, Calendar, Ticket, User } from 'lucide-react';
import { useNavigate, useLocation } from 'react-router-dom';

const BottomNav = ({ theme }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const isDark = theme === 'dark';

  const navItems = [
    { icon: <Home size={24} />, label: "Home", path: "/" },
    { icon: <Calendar size={24} />, label: "Events", path: "/events" },
    { icon: <Ticket size={24} />, label: "My Events", path: "/my-events" },
    { icon: <User size={24} />, label: "Profile", path: "/profile" },
  ];

  const styles = {
    navContainer: {
      position: 'fixed',
      bottom: 0,
      left: '50%', // Move to middle of screen
      transform: 'translateX(-50%)', // Center alignment perfectly
      width: '100%',
      maxWidth: '430px', // LOCKS the width to match your app slide
      backgroundColor: isDark ? '#1e293b' : '#fff',
      padding: '12px 20px', // Adjusted padding for better button spacing
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      borderTop: isDark ? '1px solid #334155' : '1px solid #f1f5f9',
      boxShadow: '0 -4px 20px rgba(0,0,0,0.05)',
      zIndex: 1000,
      boxSizing: 'border-box' // Ensures padding doesn't expand width
    },
    navItem: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      gap: '4px',
      cursor: 'pointer',
      background: 'none',
      border: 'none',
      minWidth: '60px', // Ensures click area is good
    },
    label: {
      fontSize: '10px',
      fontWeight: '600',
    }
  };

  return (
    <div style={styles.navContainer}>
      {navItems.map((item) => {
        const isActive = location.pathname === item.path;
        return (
          <button 
            key={item.label} 
            onClick={() => navigate(item.path)} 
            style={styles.navItem}
          >
            <div style={{ color: isActive ? '#2563eb' : (isDark ? '#94a3b8' : '#64748b') }}>
              {item.icon}
            </div>
            <span style={{ 
              ...styles.label, 
              color: isActive ? '#2563eb' : (isDark ? '#94a3b8' : '#64748b') 
            }}>
              {item.label}
            </span>
          </button>
        );
      })}
    </div>
  );
};

export default BottomNav;
