import React from 'react';
import './Dashboard.css';
import { Home, Calendar, Ticket, User, Bell, Search } from 'lucide-react';

const Dashboard = () => {
  
  // 1. Define your click handler functions
  const handleRegister = (eventName) => {
    alert(`Registering for: ${eventName}`);
  };

  const handleNavClick = (page) => {
    console.log(`Navigating to ${page}`);
    // If using react-router, you'd use navigate(`/${page}`)
  };

  return (
    <div className="dashboard-container">
      {/* ... (previous code for header and greeting) ... */}

      {/* Events Section */}
      <div className="section-header">
        <h2>Upcoming Events</h2>
        {/* Added onClick for See All */}
        <button className="see-all" onClick={() => alert('Opening all events')}>
          See all
        </button>
      </div>

      <div className="events-scroll">
        <div className="event-card">
          <img src="https://via.placeholder.com/200x100" alt="Tech Summit" />
          <div className="event-info">
            <h4>Tech Innovators Summit</h4>
            <p className="host">Hosted by CSE Department</p>
            <div className="event-footer">
              <span>Mar 22 Â· 6:00 PM</span>
              
              {/* 2. Attach the click handler to the button */}
              <button 
                className="register-btn" 
                onClick={() => handleRegister('Tech Innovators Summit')}
              >
                Register
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Bottom Navigation */}
      <div className="bottom-nav">
        {/* 3. Make Nav Items clickable */}
        <div className="nav active" onClick={() => handleNavClick('home')}>
          <Home size={20} />
          <span>Home</span>
        </div>
        <div className="nav" onClick={() => handleNavClick('events')}>
          <Calendar size={20} />
          <span>Events</span>
        </div>
        {/* ... repeat for other nav items ... */}
      </div>
    </div>
  );
};

export default Dashboard;
