import React from 'react';
import { useNavigate } from 'react-router-dom';
import { User, Lock, Settings, LogOut, GraduationCap, FileText, ChevronRight, ArrowLeft } from 'lucide-react';

const Profile = ({ user, theme, onLogout }) => {
  const navigate = useNavigate();
  const isDark = theme === 'dark';

  const styles = {
    // 1. PAGE CONTAINER (Web Layout)
    container: {
      backgroundColor: isDark ? '#0f172a' : '#f8fafc',
      minHeight: '100%',
      fontFamily: "'Inter', sans-serif",
      padding: '40px',
      maxWidth: '1200px', // Limits width on huge screens
      margin: '0 auto'
    },

    // Header Area
    header: {
      marginBottom: '30px',
      display: 'flex',
      alignItems: 'center',
      gap: '15px'
    },
    title: { fontSize: '28px', fontWeight: '800', color: isDark ? '#fff' : '#1e293b' },
    subtitle: { color: '#64748b', fontSize: '15px' },

    // 2. GRID LAYOUT (The Web Structure)
    grid: {
      display: 'grid',
      // Desktop: 350px Left Sidebar, rest Right Content. Mobile: 1 column.
      gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))',
      gap: '30px',
      alignItems: 'start'
    },

    // --- LEFT COLUMN: IDENTITY CARD ---
    identityCard: {
      backgroundColor: isDark ? '#1e293b' : '#fff',
      borderRadius: '24px',
      padding: '40px 30px',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      boxShadow: '0 4px 20px rgba(0,0,0,0.03)',
      border: isDark ? '1px solid #334155' : '1px solid #e2e8f0'
    },
    avatarWrapper: {
      width: '120px', height: '120px', borderRadius: '50%',
      padding: '4px', background: 'linear-gradient(135deg, #4f46e5, #ec4899)',
      marginBottom: '20px'
    },
    avatar: {
      width: '100%', height: '100%', borderRadius: '50%', objectFit: 'cover',
      border: '4px solid #fff', backgroundColor: '#f1f5f9'
    },
    name: { fontSize: '24px', fontWeight: '800', color: isDark ? '#fff' : '#1e293b', marginBottom: '5px' },
    email: { color: '#64748b', marginBottom: '20px' },
    badge: {
      backgroundColor: '#eff6ff', color: '#4f46e5',
      padding: '8px 20px', borderRadius: '20px',
      fontSize: '13px', fontWeight: '700', border: '1px solid #c7d2fe'
    },

    // --- RIGHT COLUMN: DETAILS & ACTIONS ---
    rightColumn: {
      display: 'flex',
      flexDirection: 'column',
      gap: '24px'
    },

    // Info Section (College Details)
    detailsBox: {
      backgroundColor: isDark ? '#1e293b' : '#fff',
      borderRadius: '20px',
      padding: '30px',
      boxShadow: '0 2px 10px rgba(0,0,0,0.02)',
      border: isDark ? '1px solid #334155' : '1px solid #e2e8f0'
    },
    sectionTitle: { fontSize: '18px', fontWeight: 'bold', marginBottom: '20px', color: isDark ? '#fff' : '#1e293b' },
    detailRow: {
      display: 'flex', alignItems: 'center', justifyContent: 'space-between',
      padding: '15px 0', borderBottom: isDark ? '1px solid #334155' : '1px solid #f1f5f9'
    },
    detailLabel: { color: '#94a3b8', fontSize: '14px', fontWeight: '600' },
    detailValue: { color: isDark ? '#fff' : '#1e293b', fontWeight: '700', fontSize: '15px' },

    // Action Grid (The Buttons)
    actionsGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fill, minmax(240px, 1fr))', // Creates cards
      gap: '20px'
    },
    actionCard: {
      backgroundColor: isDark ? '#1e293b' : '#fff',
      padding: '20px',
      borderRadius: '16px',
      cursor: 'pointer',
      border: isDark ? '1px solid #334155' : '1px solid #e2e8f0',
      transition: 'all 0.2s',
      display: 'flex',
      alignItems: 'center',
      gap: '15px'
    },
    iconBox: (color, bg) => ({
      width: '45px', height: '45px', borderRadius: '12px',
      backgroundColor: bg, color: color,
      display: 'flex', alignItems: 'center', justifyContent: 'center'
    })
  };

  return (
    <div style={styles.container}>
      {/* Header */}
      <div style={styles.header}>
        <div style={{ cursor: 'pointer', padding: '8px', background: isDark?'#334155':'#fff', borderRadius:'8px' }} onClick={() => navigate(-1)}>
          <ArrowLeft size={20} />
        </div>
        <div>
          <div style={styles.title}>My Profile</div>
          <div style={styles.subtitle}>Manage your account and preferences</div>
        </div>
      </div>

      <div style={styles.grid}>
        
        {/* LEFT: Identity Card */}
        <div style={styles.identityCard}>
          <div style={styles.avatarWrapper}>
            <img src={user.profileImage} alt="Profile" style={styles.avatar} />
          </div>
          <div style={styles.name}>{user.name}</div>
          <div style={styles.email}>{user.email}</div>
          <span style={styles.badge}>Student Account</span>
        </div>

        {/* RIGHT: Content */}
        <div style={styles.rightColumn}>
          
          {/* 1. Academic Details */}
          <div style={styles.detailsBox}>
            <div style={styles.sectionTitle}>Academic Information</div>
            <div style={styles.detailRow}>
              <span style={styles.detailLabel}>Institution Name</span>
              <span style={styles.detailValue}>{user.college}</span>
            </div>
            <div style={{ ...styles.detailRow, borderBottom: 'none' }}>
              <span style={styles.detailLabel}>Registration ID</span>
              <span style={styles.detailValue}>{user.regNo}</span>
            </div>
          </div>

          {/* 2. Account Actions (Grid of Cards) */}
          <div>
            <div style={styles.sectionTitle}>Account Settings</div>
            <div style={styles.actionsGrid}>
              
              <div style={styles.actionCard} onClick={() => navigate('/edit-profile')}>
                <div style={styles.iconBox('#4f46e5', '#eef2ff')}><User size={22} /></div>
                <div>
                  <div style={{fontWeight:'700', color: isDark?'#fff':'#1e293b'}}>Edit Profile</div>
                  <div style={{fontSize:'12px', color:'#64748b'}}>Update personal info</div>
                </div>
              </div>

              <div style={styles.actionCard} onClick={() => navigate('/change-password')}>
                <div style={styles.iconBox('#059669', '#d1fae5')}><Lock size={22} /></div>
                <div>
                  <div style={{fontWeight:'700', color: isDark?'#fff':'#1e293b'}}>Security</div>
                  <div style={{fontSize:'12px', color:'#64748b'}}>Change password</div>
                </div>
              </div>

              <div style={styles.actionCard} onClick={() => navigate('/settings')}>
                <div style={styles.iconBox('#d97706', '#fef3c7')}><Settings size={22} /></div>
                <div>
                  <div style={{fontWeight:'700', color: isDark?'#fff':'#1e293b'}}>Preferences</div>
                  <div style={{fontSize:'12px', color:'#64748b'}}>Theme & Language</div>
                </div>
              </div>

              <div style={styles.actionCard} onClick={onLogout}>
                <div style={styles.iconBox('#ef4444', '#fee2e2')}><LogOut size={22} /></div>
                <div>
