import React, { useState } from 'react';
import { Search, MapPin, Calendar, Filter, ArrowRight, X, Check } from 'lucide-react'; 
import { useNavigate } from 'react-router-dom';

const Events = ({ allEvents, theme, searchTerm, setSearchTerm }) => {
  const navigate = useNavigate();
  const isDark = theme === 'dark';

  // 1. FILTER STATE
  const [showFilters, setShowFilters] = useState(false);
  const [activeCategory, setActiveCategory] = useState('All');

  // Categories
  const categories = ['All', 'Tech', 'Cultural', 'Sports', 'Workshop', 'Seminar'];

  // 2. FILTER LOGIC
  const filteredEvents = allEvents.filter(event => {
    if (activeCategory === 'All') return true;
    return event.category === activeCategory;
  });

  const styles = {
    // 1. CONTAINER: Responsive Width
    container: { 
      padding: '2vw', 
      fontFamily: "'Inter', sans-serif", 
      maxWidth: '95vw', // Responsive max width
      margin: '0 auto',
      minHeight: '100vh',
      color: isDark ? '#fff' : '#1e293b'
    },
    header: { marginBottom: '4vh' },
    title: { fontSize: '2.5vw', fontWeight: '800', marginBottom: '1vh' },
    subtitle: { color: '#64748b', fontSize: '1.2vw' },

    // Controls Section
    controls: { display: 'flex', gap: '1.5vw', marginBottom: '3vh', flexWrap: 'wrap' },
    
    // Search Box: Responsive Size
    searchBox: {
      flex: 1, 
      minWidth: '25vw', 
      display: 'flex', 
      alignItems: 'center',
      backgroundColor: isDark ? '#1e293b' : '#fff',
      border: isDark ? '1px solid #334155' : '1px solid #e2e8f0',
      borderRadius: '1vw', 
      padding: '1vh 1.5vw',
      boxShadow: '0 0.5vh 1vh rgba(0,0,0,0.02)'
    },
    input: {
      border: 'none', 
      outline: 'none', 
      width: '100%', 
      fontSize: '1.1vw', 
      marginLeft: '0.8vw',
      backgroundColor: 'transparent', 
      color: isDark ? '#fff' : '#1e293b'
    },
    
    // Filter Button
    filterBtn: {
      display: 'flex', 
      alignItems: 'center', 
      gap: '0.5vw', 
      padding: '0 2vw',
      borderRadius: '1vw',
      backgroundColor: showFilters ? '#3b82f6' : (isDark ? '#334155' : '#fff'),
      border: showFilters ? '1px solid #3b82f6' : (isDark ? '1px solid #475569' : '1px solid #e2e8f0'),
      color: showFilters ? '#fff' : (isDark ? '#fff' : '#475569'),
      cursor: 'pointer', 
      fontWeight: '600', 
      fontSize: '1vw',
      transition: 'all 0.2s',
      height: 'auto'
    },

    // Filter Chips Area
    filterPanel: {
      display: showFilters ? 'flex' : 'none',
      gap: '1vw',
      flexWrap: 'wrap',
      marginBottom: '4vh',
      padding: '1.5vw',
      backgroundColor: isDark ? '#1e293b' : '#f8fafc',
      borderRadius: '1vw',
      border: isDark ? '1px solid #334155' : '1px solid #e2e8f0',
      animation: 'fadeIn 0.2s ease-in-out'
    },
    chip: (isActive) => ({
      padding: '0.8vh 1.5vw',
      borderRadius: '2vw',
      fontSize: '1vw',
      fontWeight: '600',
      cursor: 'pointer',
      border: 'none',
      display: 'flex', 
      alignItems: 'center', 
      gap: '0.5vw',
      backgroundColor: isActive ? '#3b82f6' : (isDark ? '#334155' : '#fff'),
      color: isActive ? '#fff' : (isDark ? '#94a3b8' : '#64748b'),
      boxShadow: isActive ? '0 0.5vh 1.5vh rgba(59, 130, 246, 0.3)' : '0 0.2vh 0.5vh rgba(0,0,0,0.02)'
    }),

    // Grid System
    grid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fill, minmax(22vw, 1fr))', // Responsive Columns
      gap: '2vw'
    },
    card: {
      backgroundColor: isDark ? '#1e293b' : '#fff',
      borderRadius: '1.5vw', 
      overflow: 'hidden',
      border: isDark ? '1px solid #334155' : '1px solid #e2e8f0',
      transition: 'transform 0.2s, box-shadow 0.2s',
      cursor: 'pointer', 
      display: 'flex', 
      flexDirection: 'column', 
      height: '100%'
    },
    // Image Height in VH
    cardImage: { 
      width: '100%', 
      height: '20vh', 
      objectFit: 'cover' 
    },
    cardBody: { 
      padding: '1.5vw', 
      flex: 1, 
      display: 'flex', 
      flexDirection: 'column' 
    },
    categoryTag: {
      display: 'inline-block', 
      fontSize: '0.9vw', 
      fontWeight: '600',
      color: '#3b82f6', 
      backgroundColor: isDark ? 'rgba(59, 130, 246, 0.1)' : '#eff6ff',
      padding: '0.4vh 0.8vw', 
      borderRadius: '0.5vw', 
      marginBottom: '1vh', 
      width: 'fit-content'
    },
    cardTitle: { 
      fontSize: '1.4vw', 
      fontWeight: 'bold', 
      marginBottom: '1vh', 
      lineHeight: '1.4',
      color: isDark ? '#fff' : '#1e293b'
    },
    meta: { 
      display: 'flex', 
      alignItems: 'center', 
      gap: '0.5vw', 
      fontSize: '1vw', 
      color: '#64748b', 
      marginBottom: '0.5vh' 
    },
    footer: { 
      marginTop: 'auto', 
      paddingTop: '1.5vh', 
      borderTop: isDark ? '1px solid #334155' : '1px solid #f1f5f9', 
      display: 'flex', 
      justifyContent: 'space-between', 
      alignItems: 'center', 
      color: '#3b82f6', 
      fontWeight: '600', 
      fontSize: '1vw' 
    },
    
    // Empty State
    noResults: {
      gridColumn: '1 / -1',
      textAlign: 'center',
      padding: '5vh',
      color: '#94a3b8',
      fontSize: '1.2vw'
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>Explore Events</h1>
        <p style={styles.subtitle}>Discover workshops, competitions, and seminars.</p>
      </div>

      {/* Search & Filter Button */}
      <div style={styles.controls}>
        <div style={styles.searchBox}>
          <Search size={20} color="#94a3b8" />
          <input 
            style={styles.input} 
            placeholder="Search events..." 
            value={searchTerm} 
            onChange={(e) => setSearchTerm(e.target.value)} 
          />
        </div>
        <button 
          style={styles.filterBtn} 
          onClick={() => setShowFilters(!showFilters)}
        >
          {showFilters ? <X size={18} /> : <Filter size={18} />} 
          {showFilters ? 'Close' : 'Filters'}
        </button>
      </div>

      {/* Filter Selection Panel */}
      <div style={styles.filterPanel}>
        {categories.map(cat => (
          <button 
            key={cat} 
            style={styles.chip(activeCategory === cat)}
            onClick={() => setActiveCategory(cat)}
          >
            {activeCategory === cat && <Check size={14} />}
            {cat}
          </button>
        ))}
      </div>

      {/* Events Grid */}
      <div style={styles.grid}>
        {filteredEvents.length > 0 ? (
          filteredEvents.map(event => (
            <div 
              key={event.id} 
              style={styles.card} 
              onClick={() => navigate(`/event-details/${event.id}`)}
              onMouseEnter={(e) => { e.currentTarget.style.transform = 'translateY(-5px)'; e.currentTarget.style.boxShadow = '0 1vh 2vh rgba(0,0,0,0.08)'; }}
              onMouseLeave={(e) => { e.currentTarget.style.transform = 'translateY(0)'; e.currentTarget.style.boxShadow = 'none'; }}
            >
              <img src={event.image} alt="" style={styles.cardImage} />
              <div style={styles.cardBody}>
                <span style={styles.categoryTag}>{event.category}</span>
                <h3 style={styles.cardTitle}>{event.title}</h3>
                
                <div style={styles.meta}>
                  <Calendar size={16} /> {event.date}
                </div>
                <div style={styles.meta}>
                  <MapPin size={16} /> {event.location}
                </div>

                <div style={styles.footer}>
                  View Details <ArrowRight size={16} />
                </div>
              </div>
            </div>
          ))
        ) : (
          <div style={styles.noResults}>
            <h3>No events found</h3>
            <p>Try adjusting your search or filters.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Events;
