import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import { CheckCircle, Calendar, RefreshCw, Bell } from 'lucide-react'; // Import icons for data

// Page Imports
import Dashboard from './pages/Dashboard';
import Events from './pages/Events';
import MyEvents from './pages/MyEvents';
import Profile from './pages/Profile';
import EditProfile from './pages/EditProfile';
import ChangePassword from './pages/ChangePassword';
import Settings from './pages/Settings'; 
import ThemeSelection from './pages/ThemeSelection';
import EventDetails from './pages/EventDetails';
import Notifications from './pages/Notifications';
import TicketConfirmation from './pages/TicketConfirmation'; 
import BottomNav from './components/BottomNav';

const AppContent = ({ 
  theme, setTheme, user, setUser, allEvents, handleRegister, handleCancel, 
  searchTerm, setSearchTerm, registeredEvents, filteredEvents, appStyles,
  notifications, unreadCount, markNotificationsRead // <--- NEW PROPS
}) => {
  const location = useLocation();
  const showNavOn = ['/', '/events', '/my-events', '/profile'];
  const shouldShow = showNavOn.includes(location.pathname);

  const dynamicStyles = {
    ...appStyles,
    paddingBottom: shouldShow ? '100px' : '0px' 
  };

  return (
    <div style={dynamicStyles}>
      <Routes>
        <Route 
          path="/" 
          element={
            <Dashboard 
              user={user} 
              events={allEvents} 
              theme={theme} 
              regCount={registeredEvents.length} 
              onRegister={handleRegister}
              unreadCount={unreadCount} // Pass Count
              onReadNotifications={markNotificationsRead} // Pass Function to clear dot
            />
          } 
        />
        <Route path="/events" element={<Events allEvents={filteredEvents} theme={theme} searchTerm={searchTerm} setSearchTerm={setSearchTerm} onRegister={handleRegister} />} />
        <Route path="/my-events" element={<MyEvents theme={theme} events={registeredEvents} onCancel={handleCancel} />} />
        <Route path="/event-details/:id" element={<EventDetails allEvents={allEvents} theme={theme} onRegister={handleRegister} />} />
        
        {/* Pass notifications data to the page */}
        <Route path="/notifications" element={<Notifications theme={theme} notificationsList={notifications} />} />
        
        <Route path="/ticket-confirmation/:id" element={<TicketConfirmation allEvents={allEvents} theme={theme} onCancel={handleCancel} />} />
        <Route path="/profile" element={<Profile user={user} theme={theme} />} />
        <Route path="/edit-profile" element={<EditProfile user={user} setUser={setUser} theme={theme} />} />
        <Route path="/change-password" element={<ChangePassword theme={theme} />} />
        <Route path="/settings" element={<Settings theme={theme} />} />
        <Route path="/settings/theme" element={<ThemeSelection currentTheme={theme} setTheme={setTheme} />} />
      </Routes>

      {shouldShow && <BottomNav theme={theme} />}
    </div>
  );
};

function App() {
  const [theme, setTheme] = useState('light'); 
  const [searchTerm, setSearchTerm] = useState("");
  const [user, setUser] = useState({
    name: "Priya Sharma",
    email: "priya.sharma@university.edu",
    college: "Engineering Tech Institute",
    regNo: "ETI-2023-4589"
  });

  const [allEvents, setAllEvents] = useState([
    { id: 1, title: "AI & Future Tech Workshop", date: "Apr 05, 2024 · 09:00 AM", location: "Innovation Lab", category: "Tech", image: "https://images.unsplash.com/photo-1485827404703-89b55fcc595e?w=800&q=80", status: 'idle' },
    { id: 2, title: "Inter-College Music Battle", date: "Apr 12, 2024 · 05:00 PM", location: "Main Stadium", category: "Cultural", image: "https://images.unsplash.com/photo-1501281668745-f7f57925c3b4?w=800&q=80", status: 'idle' },
    { id: 3, title: "University Basketball Finals", date: "Apr 20, 2024 · 10:00 AM", location: "Indoor Sports Complex", category: "Sports", image: "https://images.unsplash.com/photo-1546519638-68e109498ffc?w=800&q=80", status: 'idle' }
  ]);

  // --- NOTIFICATION STATE ---
  // We keep the data here so we can track the count
  const [notifications, setNotifications] = useState([
    { id: 1, title: "AI & Future Tech Workshop", typeLabel: "Registration Approved", desc: "Your seat has been confirmed. Tap here to view your ticket.", time: "2h ago", icon: <CheckCircle size={20} color="#5c5cfc" />, bgColor: 'rgba(92, 92, 252, 0.1)', eventId: 1 },
    { id: 2, title: "Inter-College Music Battle", typeLabel: "New Event Added", desc: "Registration is now open! Check out the details and join.", time: "5h ago", icon: <Calendar size={20} color="#22c55e" />, bgColor: 'rgba(34, 197, 94, 0.1)', eventId: 2 },
    { id: 3, title: "University Basketball Finals", typeLabel: "Event Updated", desc: "The location has been changed to the Indoor Sports Complex.", time: "Yesterday", icon: <RefreshCw size={20} color="#f59e0b" />, bgColor: 'rgba(245, 158, 11, 0.1)', eventId: 3 },
    { id: 4, title: "AI & Future Tech Workshop", typeLabel: "Reminder", desc: "Don't forget, this event starts tomorrow at 09:00 AM.", time: "2d ago", icon: <Bell size={20} color="#ef4444" />, bgColor: 'rgba(239, 68, 68, 0.1)', eventId: 1 }
  ]);

  // Start with some unread count (e.g., 2)
  const [unreadCount, setUnreadCount] = useState(2);

  // Function to clear the red dot
  const markNotificationsRead = () => {
    setUnreadCount(0);
  };

  const handleRegister = (eventId) => {
    setAllEvents(prev => prev.map(ev => ev.id === eventId ? { ...ev, status: 'pending' } : ev));
    setTimeout(() => {
      setAllEvents(prev => prev.map(ev => ev.id === eventId ? { ...ev, status: 'approved' } : ev));
      // Optional: Add a new notification here dynamically
      setUnreadCount(prev => prev + 1); 
    }, 5000); 
  };

  const handleCancelRegistration = (eventId) => {
    setAllEvents(prev => prev.map(ev => ev.id === eventId ? { ...ev, status: 'idle' } : ev));
  };

  const filteredEvents = allEvents.filter(e => e.title.toLowerCase().includes(searchTerm.toLowerCase()));
  const registeredEvents = allEvents.filter(e => e.status === 'pending' || e.status === 'approved');

  const appStyles = {
    maxWidth: '430px', margin: '0 auto', minHeight: '100vh', position: 'relative',
    backgroundColor: theme === 'dark' ? '#0f172a' : '#fff', transition: 'all 0.3s ease', overflowX: 'hidden', boxSizing: 'border-box'
  };

  return (
    <Router>
      <AppContent 
        theme={theme} setTheme={setTheme} user={user} setUser={setUser}
        allEvents={allEvents} handleRegister={handleRegister} handleCancel={handleCancelRegistration}
        searchTerm={searchTerm} setSearchTerm={setSearchTerm}
        registeredEvents={registeredEvents} filteredEvents={filteredEvents}
        appStyles={appStyles}
        // Pass notification props
        notifications={notifications}
        unreadCount={unreadCount}
        markNotificationsRead={markNotificationsRead}
      />
    </Router>
  );
}

export default App;
