import React from 'react';
import { Search, MapPin, Calendar, Filter } from 'lucide-react'; 
import { useNavigate } from 'react-router-dom';
import GradientHeader from '../components/GradientHeader';

const Events = ({ allEvents, theme, searchTerm, setSearchTerm }) => {
  const navigate = useNavigate();
  const isDark = theme === 'dark';

  const styles = {
    container: { padding: 0, backgroundColor: isDark ? '#0f172a' : '#f8fafc', minHeight: '100vh', maxWidth: '430px', margin: '0 auto' },
    content: { padding: '20px' },
    searchBox: {
      display: 'flex', alignItems: 'center', gap: '10px', backgroundColor: isDark ? '#1e293b' : '#fff',
      padding: '12px 16px', borderRadius: '12px', marginBottom: '20px', boxShadow: '0 2px 8px rgba(0,0,0,0.05)'
    },
    input: { border: 'none', outline: 'none', width: '100%', fontSize: '15px', background: 'transparent', color: isDark ? '#fff' : '#000' },
    eventCard: { backgroundColor: isDark ? '#1e293b' : '#fff', borderRadius: '16px', padding: '16px', marginBottom: '16px', display: 'flex', gap: '15px', boxShadow: '0 2px 4px rgba(0,0,0,0.02)' }
  };

  return (
    <div style={styles.container}>
      <GradientHeader 
        title="Events" 
        subtitle="Find and register" 
        showBack={false} // Main nav, no back needed usually
      />

      <div style={styles.content}>
        {/* Search Bar */}
        <div style={styles.searchBox}>
          <Search size={20} color="#94a3b8" />
          <input 
            style={styles.input} 
            placeholder="Search events..." 
            value={searchTerm} 
            onChange={(e) => setSearchTerm(e.target.value)} 
          />
          <Filter size={20} color="#2563eb" />
        </div>

        {allEvents.map(event => (
          <div key={event.id} style={styles.eventCard} onClick={() => navigate(`/event-details/${event.id}`)}>
            <img src={event.image} alt="" style={{ width: '80px', height: '80px', borderRadius: '12px', objectFit: 'cover' }} />
            <div>
              <h4 style={{ margin: '0 0 5px 0', fontSize: '15px', color: isDark ? '#fff' : '#1e293b' }}>{event.title}</h4>
              <div style={{ fontSize: '12px', color: '#64748b', display: 'flex', alignItems: 'center', gap: '4px' }}><Calendar size={12} /> {event.date.split('·')[0]}</div>
              <div style={{ fontSize: '12px', color: '#64748b', marginTop: '4px', display: 'flex', alignItems: 'center', gap: '4px' }}><MapPin size={12} /> {event.location}</div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Events;
