import React from 'react';
import { useNavigate } from 'react-router-dom';
import { User, Lock, Settings, LogOut, ChevronRight } from 'lucide-react';
import GradientHeader from '../components/GradientHeader';

const Profile = ({ user, theme, onLogout }) => {
  const navigate = useNavigate();
  const isDark = theme === 'dark';

  const styles = {
    container: { padding: 0, backgroundColor: isDark ? '#0f172a' : '#f8fafc', minHeight: '100vh', maxWidth: '430px', margin: '0 auto' },
    content: { padding: '20px' },
    menuBtn: {
      width: '100%', backgroundColor: isDark ? '#1e293b' : '#fff', padding: '16px', borderRadius: '16px',
      border: 'none', display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '12px',
      boxShadow: '0 2px 4px rgba(0,0,0,0.02)', cursor: 'pointer'
    },
    iconBox: (color, bg) => ({ width: '36px', height: '36px', borderRadius: '10px', backgroundColor: bg, color: color, display: 'flex', alignItems: 'center', justifyContent: 'center', marginRight: '15px' })
  };

  return (
    <div style={styles.container}>
      <GradientHeader title="Profile" subtitle="Manage your account" showBack={false} />

      <div style={styles.content}>
        {/* User Info Card */}
        <div style={{ backgroundColor: isDark?'#1e293b':'#fff', borderRadius: '20px', padding: '20px', textAlign: 'center', marginBottom: '24px', boxShadow: '0 4px 10px rgba(0,0,0,0.03)' }}>
          <div style={{ width: '80px', height: '80px', borderRadius: '50%', backgroundColor: '#e0e7ff', color: '#4338ca', fontSize: '32px', fontWeight: 'bold', display: 'flex', alignItems: 'center', justifyContent: 'center', margin: '0 auto 15px auto' }}>
            {user.name.charAt(0)}
          </div>
          <h2 style={{ margin: '0 0 5px 0', fontSize: '20px', color: isDark?'#fff':'#1e293b' }}>{user.name}</h2>
          <p style={{ margin: 0, color: '#64748b', fontSize: '14px' }}>{user.email}</p>
        </div>

        {/* Menu Items */}
        <button style={styles.menuBtn} onClick={() => navigate('/edit-profile')}>
          <div style={{ display: 'flex', alignItems: 'center' }}>
            <div style={styles.iconBox('#2563eb', '#eff6ff')}><User size={20} /></div>
            <span style={{ fontSize: '15px', fontWeight: '600', color: isDark?'#fff':'#334155' }}>Edit Profile</span>
          </div>
          <ChevronRight size={20} color="#cbd5e1" />
        </button>

        <button style={styles.menuBtn} onClick={onLogout}>
          <div style={{ display: 'flex', alignItems: 'center' }}>
            <div style={styles.iconBox('#ef4444', '#fef2f2')}><LogOut size={20} /></div>
            <span style={{ fontSize: '15px', fontWeight: '600', color: '#ef4444' }}>Logout</span>
          </div>
        </button>
      </div>
    </div>
  );
};

export default Profile;
