import React, { useState } from 'react';
import { Search, SlidersHorizontal, Clock, MapPin } from 'lucide-react';

const Events = ({ theme, allEvents }) => {
  const [activeCategory, setActiveCategory] = useState("All");
  const isDark = theme === 'dark';

  const categories = ["All", "Tech", "Cultural", "Sports", "Workshops"];

  const styles = {
    container: { 
      backgroundColor: isDark ? '#0f172a' : '#f8fafc', 
      height: '100vh', 
      display: 'flex', 
      flexDirection: 'column' 
    },
    // Fixed Top Header
    header: {
      padding: '16px 16px 10px 16px',
      backgroundColor: isDark ? '#0f172a' : '#fff',
    },
    titleRow: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '16px' },
    searchBar: {
      display: 'flex',
      alignItems: 'center',
      backgroundColor: isDark ? '#1e293b' : '#f1f5f9',
      padding: '10px 16px',
      borderRadius: '12px',
      marginBottom: '16px'
    },
    // Horizontal Scroll for Categories
    filterScroll: {
      display: 'flex',
      gap: '10px',
      overflowX: 'auto',
      paddingBottom: '10px',
      scrollbarWidth: 'none', // Hides scrollbar for clean look
    },
    categoryPill: (isActive) => ({
      padding: '8px 20px',
      borderRadius: '10px',
      backgroundColor: isActive ? '#5c5cfc' : (isDark ? '#1e293b' : '#f1f5f9'),
      color: isActive ? '#fff' : (isDark ? '#94a3b8' : '#64748b'),
      fontSize: '13px',
      fontWeight: '600',
      whiteSpace: 'nowrap',
      cursor: 'pointer'
    }),
    // Main Vertical Scroll Area
    scrollArea: {
      flex: 1,
      overflowY: 'auto',
      padding: '0 16px 100px 16px', // Extra bottom padding for BottomNav
    },
    eventCard: {
      backgroundColor: isDark ? '#1e293b' : '#fff',
      borderRadius: '20px',
      overflow: 'hidden',
      marginBottom: '20px',
      boxShadow: '0 4px 6px -1px rgba(0,0,0,0.05)',
      border: isDark ? '1px solid #334155' : 'none'
    },
    cardContent: { padding: '16px' },
    registerBtn: {
      backgroundColor: '#5c5cfc',
      color: '#fff',
      padding: '8px 18px',
      borderRadius: '10px',
      border: 'none',
      fontWeight: 'bold',
      fontSize: '13px',
      cursor: 'pointer'
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <div style={styles.titleRow}>
          <h2 style={{ margin: 0, fontSize: '20px', color: isDark ? '#fff' : '#1e293b' }}>Events</h2>
          <div style={{ backgroundColor: isDark ? '#1e293b' : '#f1f5f9', padding: '6px', borderRadius: '8px' }}>
            <SlidersHorizontal size={18} color={isDark ? '#fff' : '#1e293b'} />
          </div>
        </div>

        <div style={styles.searchBar}>
          <Search size={18} color="#94a3b8" />
          <input 
            placeholder="Search events..." 
            style={{ border: 'none', background: 'transparent', marginLeft: '10px', flex: 1, outline: 'none', color: isDark ? '#fff' : '#000' }} 
          />
        </div>

        <div style={styles.filterScroll} className="no-scrollbar">
          {categories.map(cat => (
            <div 
              key={cat} 
              onClick={() => setActiveCategory(cat)}
              style={styles.categoryPill(activeCategory === cat)}
            >
              {cat}
            </div>
          ))}
        </div>
      </div>

      {/* This area scrolls vertically */}
      <div style={styles.scrollArea}>
        {allEvents.map(event => (
          <div key={event.id} style={styles.eventCard}>
            <div style={{ height: '180px', backgroundColor: '#e2e8f0', backgroundImage: `url(${event.image})`, backgroundSize: 'cover' }} />
            <div style={styles.cardContent}>
              <h3 style={{ margin: '0 0 8px 0', fontSize: '16px', color: isDark ? '#fff' : '#1e293b' }}>{event.title}</h3>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-end' }}>
                <div>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '6px', fontSize: '12px', color: '#94a3b8', marginBottom: '4px' }}>
                    <Clock size={14} /> {event.date}
                  </div>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '6px', fontSize: '12px', color: '#94a3b8' }}>
                    <MapPin size={14} /> {event.location}
                  </div>
                </div>
                <button style={styles.registerBtn}>Register</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Events;
