import React, { useState } from 'react';
import { Home, Calendar, Ticket, User, Settings, LogOut, Menu } from 'lucide-react';
import { useNavigate, useLocation } from 'react-router-dom';

const Sidebar = ({ user, theme, onLogout }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const isDark = theme === 'dark';
  const [isExpanded, setIsExpanded] = useState(false);

  const menuItems = [
    { label: 'Dashboard', icon: <Home size={20} />, path: '/' },
    { label: 'Events', icon: <Calendar size={20} />, path: '/events' },
    { label: 'My Tickets', icon: <Ticket size={20} />, path: '/my-events' },
    { label: 'Profile', icon: <User size={20} />, path: '/profile' },
    { label: 'Settings', icon: <Settings size={20} />, path: '/settings' },
  ];

  const styles = {
    sidebar: {
      height: '100vh',
      width: isExpanded ? '240px' : '50px', // Fixed width
      backgroundColor: isDark ? '#1e293b' : '#fff',
      borderRight: isDark ? '1px solid #334155' : '1px solid #e2e8f0',
      display: 'flex', flexDirection: 'column', padding: '10px 0', 
      position: 'fixed', left: 0, top: 0, zIndex: 1000,
      transition: 'width 0.2s ease', overflowX: 'hidden',
    },
    topSection: {
      display: 'flex', alignItems: 'center', gap: '10px', marginBottom: '15px',
      paddingLeft: isExpanded ? '14px' : '0', justifyContent: isExpanded ? 'flex-start' : 'center',
      height: '40px'
    },
    menuBtn: {
      cursor: 'pointer', color: isDark ? '#fff' : '#1e293b', display: 'flex', alignItems: 'center', justifyContent: 'center', width: '30px', height: '30px',
    },
    logoText: {
      fontSize: '18px', fontWeight: '800', color: '#6366f1', whiteSpace: 'nowrap', opacity: isExpanded ? 1 : 0, display: isExpanded ? 'block' : 'none'
    },
    nav: { flex: 1, display: 'flex', flexDirection: 'column', gap: '5px' },
    navItem: (isActive) => ({
      display: 'flex', alignItems: 'center', gap: '12px',
      width: isExpanded ? '92%' : '100%', margin: isExpanded ? '0 auto' : '0',
      padding: isExpanded ? '10px' : '12px 0',
      borderRadius: isExpanded ? '8px' : '0',
      cursor: 'pointer',
      backgroundColor: isActive ? (isDark ? '#334155' : '#eff6ff') : 'transparent',
      borderLeft: (!isExpanded && isActive) ? '3px solid #6366f1' : '3px solid transparent',
      color: isActive ? '#6366f1' : '#64748b',
      justifyContent: isExpanded ? 'flex-start' : 'center'
    }),
    navLabel: { fontWeight: '600', fontSize: '13px', display: isExpanded ? 'block' : 'none' },
    userSection: { marginTop: 'auto', padding: '10px 0', display: 'flex', alignItems: 'center', gap: '10px', justifyContent: isExpanded ? 'flex-start' : 'center', paddingLeft: isExpanded ? '12px' : '0' },
    avatar: { width: '28px', height: '28px', borderRadius: '50%', objectFit: 'cover' },
  };

  return (
    // REMOVED THE SPACER DIV FROM HERE
    <div style={styles.sidebar}>
      <div style={styles.topSection}>
        <div style={styles.menuBtn} onClick={() => setIsExpanded(!isExpanded)}><Menu size={20} /></div>
        <div style={styles.logoText}>EventSphere</div>
      </div>
      <div style={styles.nav}>
        {menuItems.map((item) => {
          const isActive = location.pathname === item.path;
          return (
            <div key={item.label} style={styles.navItem(isActive)} onClick={() => navigate(item.path)} title={!isExpanded ? item.label : ''}>
              <div style={{display:'flex', justifyContent:'center', width:'20px'}}>{item.icon}</div>
              <span style={styles.navLabel}>{item.label}</span>
            </div>
          );
        })}
      </div>
      <div style={styles.userSection}>
        <img src={user.profileImage} alt="User" style={styles.avatar} />
        <div style={{display: isExpanded ? 'block' : 'none', fontSize:'12px', fontWeight:'bold', color: isDark ? '#fff' : '#333'}}>{user.name.split(' ')[0]}</div>
      </div>
      <div style={{...styles.navItem(false), color:'#ef4444', marginTop:'5px'}} onClick={onLogout}>
         <div style={{display:'flex', justifyContent:'center', width:'20px'}}><LogOut size={20} /></div>
         <span style={styles.navLabel}>Log Out</span>
      </div>
    </div>
  );
};
export default Sidebar;
