import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { Home, Calendar, Ticket, User } from 'lucide-react';

const BottomNav = ({ theme }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const isActive = (path) => location.pathname === path;

  const styles = {
    nav: {
      position: 'fixed',
      bottom: 0,
      left: '50%',
      transform: 'translateX(-50%)',
      width: '100%',
      maxWidth: '375px',
      // Dynamic colors for Light/Dark mode
      backgroundColor: theme === 'dark' ? '#1e293b' : '#ffffff',
      display: 'flex',
      justifyContent: 'space-around',
      padding: '12px 0',
      borderTop: theme === 'dark' ? '1px solid #334155' : '1px solid #f1f5f9',
      zIndex: 1000
    },
    item: (path) => ({
      // Active blue matches your design
      color: isActive(path) ? '#5c5cfc' : (theme === 'dark' ? '#94a3b8' : '#64748b'),
      textAlign: 'center',
      cursor: 'pointer',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      gap: '4px'
    })
  };

  return (
    <nav style={styles.nav}>
      <div onClick={() => navigate('/')} style={styles.item('/')}>
        <Home size={22} />
        <span style={{ fontSize: '10px' }}>Home</span>
      </div>
      <div onClick={() => navigate('/events')} style={styles.item('/events')}>
        <Calendar size={22} />
        <span style={{ fontSize: '10px' }}>Events</span>
      </div>
      <div onClick={() => navigate('/my-events')} style={styles.item('/my-events')}>
        <Ticket size={22} />
        <span style={{ fontSize: '10px' }}>My Events</span>
      </div>
      <div onClick={() => navigate('/profile')} style={styles.item('/profile')}>
        <User size={22} />
        <span style={{ fontSize: '10px' }}>Profile</span>
      </div>
    </nav>
  );
};

export default BottomNav;
