import React from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowLeft, Clock, MapPin } from 'lucide-react';

const MyEvents = ({ events, theme }) => {
  const isDark = theme === 'dark';
  const navigate = useNavigate();

  const styles = {
    container: {
      padding: '16px',
      minHeight: '100vh',
      backgroundColor: isDark ? '#0f172a' : '#f8fafc',
    },
    header: {
      display: 'flex',
      alignItems: 'center',
      gap: '15px',
      marginBottom: '20px',
      padding: '10px 0'
    },
    title: {
      fontSize: '20px',
      fontWeight: 'bold',
      color: isDark ? '#fff' : '#1e293b',
      margin: 0
    },
    sectionLabel: {
      fontSize: '12px',
      fontWeight: 'bold',
      color: '#94a3b8',
      textTransform: 'uppercase',
      letterSpacing: '1px',
      margin: '20px 0 15px 0'
    },
    eventCard: {
      backgroundColor: isDark ? '#1e293b' : '#fff',
      borderRadius: '20px',
      padding: '12px',
      marginBottom: '16px',
      border: isDark ? '1px solid #334155' : '1px solid #f1f5f9',
      boxShadow: '0 4px 6px rgba(0,0,0,0.02)'
    },
    emptyState: {
      textAlign: 'center',
      marginTop: '100px',
      color: '#94a3b8'
    }
  };

  return (
    <div style={styles.container}>
      {/* Header with Back Navigation */}
      <header style={styles.header}>
        <ArrowLeft 
          size={24} 
          onClick={() => navigate(-1)} 
          style={{ cursor: 'pointer', color: isDark ? '#fff' : '#1e293b' }} 
        />
        <h2 style={styles.title}>My Events</h2>
      </header>

      {/* Dynamic Count Label */}
      <p style={styles.sectionLabel}>
        REGISTERED EVENTS ({events.length})
      </p>

      {/* List of Registered Events */}
      {events.length > 0 ? (
        events.map(event => (
          <div key={event.id} style={styles.eventCard}>
            <div style={{ display: 'flex', gap: '12px' }}>
              <img 
                src={event.image} 
                alt={event.title} 
                style={{ width: '80px', height: '80px', borderRadius: '12px', objectFit: 'cover' }} 
              />
              <div style={{ flex: 1 }}>
                <h4 style={{ margin: '0 0 4px 0', fontSize: '15px', color: isDark ? '#fff' : '#1e293b' }}>
                  {event.title}
                </h4>
                <div style={{ display: 'flex', alignItems: 'center', gap: '5px', fontSize: '12px', color: '#94a3b8' }}>
                  <Clock size={14} /> {event.date.split('·')[0]}
                </div>
                <div style={{ display: 'flex', alignItems: 'center', gap: '5px', fontSize: '12px', color: '#94a3b8', marginTop: '4px' }}>
                  <MapPin size={14} /> {event.location}
                </div>
              </div>
            </div>
            <div style={{ 
              marginTop: '12px', 
              padding: '8px', 
              backgroundColor: '#f0fdf4', 
              color: '#16a34a', 
              textAlign: 'center', 
              borderRadius: '10px', 
              fontSize: '12px', 
              fontWeight: 'bold' 
            }}>
              ✓ Confirmed
            </div>
          </div>
        ))
      ) : (
        <div style={styles.emptyState}>
          <p>You haven't registered for any events yet.</p>
        </div>
      )}
    </div>
  );
};

export default MyEvents;
