import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

// Page Imports
import Dashboard from './pages/Dashboard';
import Events from './pages/Events';
import MyEvents from './pages/MyEvents';
import Profile from './pages/Profile';
import EditProfile from './pages/EditProfile';
import ChangePassword from './pages/ChangePassword';
import Settings from './pages/Settings'; 
import ThemeSelection from './pages/ThemeSelection';
import BottomNav from './components/BottomNav';

function App() {
  // 1. Central State Management
  // This controls the look and the search functionality across the whole app
  const [theme, setTheme] = useState('light'); 
  const [searchTerm, setSearchTerm] = useState("");
  const [user, setUser] = useState({
    name: "Priya Sharma",
    email: "priya.sharma@university.edu",
    college: "Engineering Tech Institute",
    regNo: "ETI-2023-4589"
  });

  // 2. Verified Event Data
  // These Unsplash links are high-resolution to ensure your "dark boxes" are replaced with images
  const [allEvents] = useState([
    { 
      id: 1, 
      title: "Campus Tech Summit 2024", 
      date: "Mar 18, 2024 · 10:00 AM", 
      location: "Main Auditorium, Block A", 
      category: "Tech",
      image: "https://images.unsplash.com/photo-1540575467063-178a50c2df87?w=800&q=80" 
    },
    { 
      id: 2, 
      title: "Annual Cultural Night", 
      date: "Mar 22, 2024 · 6:30 PM", 
      location: "Open Air Theatre", 
      category: "Cultural",
      image: "https://images.unsplash.com/photo-1514525253361-bee871870402?w=800&q=80" 
    },
    { 
      id: 3, 
      title: "Spring Sports Fest", 
      date: "Mar 30, 2024 · 8:00 AM", 
      location: "University Sports Ground", 
      category: "Sports",
      image: "https://images.unsplash.com/photo-1508098682722-e99c43a406b2?w=800&q=80" 
    }
  ]);

  const [registeredList, setRegisteredList] = useState([]);

  // 3. Search Logic
  // This filters the events for the Dashboard view specifically
  const filteredEvents = allEvents.filter(event => 
    event.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // 4. Global Style Wrapper
  const appStyles = {
    maxWidth: '430px', // Standard mobile width
    margin: '0 auto',
    backgroundColor: theme === 'dark' ? '#0f172a' : '#fff',
    color: theme === 'dark' ? '#f8fafc' : '#1e293b',
    minHeight: '100vh',
    position: 'relative',
    transition: 'background-color 0.3s ease',
    overflowX: 'hidden'
  };

  return (
    <Router>
      <div style={appStyles}>
        <Routes>
          {/* Dashboard Route */}
          <Route path="/" element={
            <Dashboard 
              user={user} 
              events={filteredEvents} 
              searchTerm={searchTerm} 
              setSearchTerm={setSearchTerm} 
              regCount={registeredList.length} 
              theme={theme} 
            />
          } />

          {/* Events List Route */}
          <Route path="/events" element={
            <Events 
              allEvents={allEvents} 
              theme={theme} 
              searchTerm={searchTerm} 
              setSearchTerm={setSearchTerm} 
            />
          } />

          {/* Other Routes */}
          <Route path="/my-events" element={<MyEvents theme={theme} registeredList={registeredList} />} />
          <Route path="/profile" element={<Profile user={user} theme={theme} />} />
          <Route path="/edit-profile" element={<EditProfile user={user} setUser={setUser} theme={theme} />} />
          <Route path="/change-password" element={<ChangePassword theme={theme} />} />
          <Route path="/settings" element={<Settings theme={theme} />} />
          <Route path="/settings/theme" element={<ThemeSelection currentTheme={theme} setTheme={setTheme} />} />
        </Routes>

        {/* This bar stays at the bottom regardless of which page you are on */}
        <BottomNav theme={theme} />
      </div>
    </Router>
  );
}

export default App;
