import React from 'react';
import { Search, Bell, Clock, MapPin } from 'lucide-react';

const Dashboard = ({ user, events, theme, setSearchTerm }) => {
  const isDark = theme === 'dark';

  const handleSearchClick = () => {
    const query = prompt("Search for an event:");
    if (query !== null) setSearchTerm(query);
  };

  const handleNotifyClick = () => {
    alert("You have no new notifications!");
  };

  const styles = {
    container: { padding: '16px', paddingBottom: '100px' },
    header: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' },
    greeting: { fontSize: '28px', fontWeight: 'bold', color: isDark ? '#fff' : '#1e293b' },
    eventCard: { 
      borderRadius: '20px', 
      padding: '12px',
      backgroundColor: isDark ? '#1e293b' : '#fff',
      border: '1px solid #f1f5f9',
      marginBottom: '20px'
    },
    imgStyle: {
      width: '100%',
      height: '160px',
      borderRadius: '12px',
      objectFit: 'cover',
      marginBottom: '12px'
    }
  };

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
          <div style={{ width: '35px', height: '35px', borderRadius: '50%', backgroundColor: '#2563eb', color: '#fff', display: 'flex', justifyContent: 'center', alignItems: 'center', fontWeight: 'bold' }}>ES</div>
          <span style={{ fontWeight: 'bold', color: isDark ? '#fff' : '#1e293b' }}>EventSphere</span>
        </div>
        <div style={{ display: 'flex', gap: '15px' }}>
          {/* Functional Buttons */}
          <Search size={22} onClick={handleSearchClick} style={{ cursor: 'pointer' }} />
          <Bell size={22} onClick={handleNotifyClick} style={{ cursor: 'pointer' }} />
        </div>
      </header>

      <h1 style={styles.greeting}>Hi {user.name.split(' ')[0]} ðŸ‘‹</h1>
      <p style={{ color: '#64748b', fontSize: '14px' }}>Explore college events</p>

      <h3 style={{ margin: '20px 0 15px 0' }}>Upcoming Events</h3>
      
      {events.map(event => (
        <div key={event.id} style={styles.eventCard}>
          <img 
            src={event.image} 
            alt={event.title} 
            style={styles.imgStyle} 
            onError={(e) => { e.target.src = "https://via.placeholder.com/400x200?text=Event"; }}
          />
          <h4 style={{ margin: '0 0 8px 0' }}>{event.title}</h4>
          <div style={{ display: 'flex', alignItems: 'center', gap: '5px', fontSize: '12px', color: '#94a3b8' }}>
            <Clock size={14} /> {event.date}
          </div>
          <button style={{ width: '100%', backgroundColor: '#2563eb', color: '#fff', border: 'none', padding: '10px', borderRadius: '10px', marginTop: '10px', fontWeight: 'bold' }}>
            Register
          </button>
        </div>
      ))}
    </div>
  );
};

export default Dashboard;
