import React from 'react';
import { ArrowLeft } from 'lucide-react';
import { useNavigate } from 'react-router-dom';

const GradientHeader = ({ title, subtitle, showBack = false, rightElement }) => {
  const navigate = useNavigate();

  // Paste this inside GradientHeader.jsx
const styles = {
  header: {
    background: 'linear-gradient(to right, #0f172a, #1e293b)', // Dark Slate
    position: 'relative',
    overflow: 'hidden',
    padding: '24px 24px 32px 24px',
    color: '#fff',
    borderBottomLeftRadius: '24px',
    borderBottomRightRadius: '24px',
    marginBottom: '20px',
    borderBottom: '2px solid #06b6d4', // Cyan bottom border line
    boxShadow: '0 0 20px rgba(6, 182, 212, 0.2)' // Cyan glow
  },
  // Optional: Add this "glow" effect behind the title if you want
  title: {
    fontSize: '26px',
    fontWeight: '800',
    margin: '0',
    letterSpacing: '1px',
    textShadow: '0 0 10px rgba(6, 182, 212, 0.6)' // Neon text glow
  },
  // ... keep other styles the same
};
  return (
    <div style={styles.header}>
      {/* Top Row: Back Button & Right Element */}
      <div style={styles.topRow}>
        {showBack ? (
          <div style={styles.backBtn} onClick={() => navigate(-1)}>
            <ArrowLeft size={20} color="#fff" />
          </div>
        ) : (
          <div style={styles.placeholder}></div> // Keeps spacing balanced if no back button
        )}
        
        {rightElement && <div>{rightElement}</div>}
      </div>

      {/* Title Section */}
      <h1 style={styles.title}>{title}</h1>
      {subtitle && <div style={styles.subtitle}>{subtitle}</div>}
    </div>
  );
};

export default GradientHeader;
