import React, { useState } from 'react';
import { 
  Home, Calendar, Ticket, User, Settings, LogOut, Menu 
} from 'lucide-react';
import { useNavigate, useLocation } from 'react-router-dom';

const Sidebar = ({ user, theme, onLogout }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const isDark = theme === 'dark';
  
  const [isExpanded, setIsExpanded] = useState(false);

  const menuItems = [
    { label: 'Dashboard', icon: <Home size={22} />, path: '/' },
    { label: 'Events', icon: <Calendar size={22} />, path: '/events' },
    { label: 'My Tickets', icon: <Ticket size={22} />, path: '/my-events' },
    { label: 'Profile', icon: <User size={22} />, path: '/profile' },
    { label: 'Settings', icon: <Settings size={22} />, path: '/settings' },
  ];

  const styles = {
    // 1. SIDEBAR CONTAINER
    sidebar: {
      height: '100vh',
      width: isExpanded ? '250px' : '56px', // Tighter width (was 64px)
      backgroundColor: isDark ? '#1e293b' : '#fff',
      borderRight: isDark ? '1px solid #334155' : '1px solid #e2e8f0',
      display: 'flex',
      flexDirection: 'column',
      padding: '12px 0', 
      position: 'fixed',
      left: 0,
      top: 0,
      zIndex: 1000,
      transition: 'width 0.3s ease',
      overflowX: 'hidden',
    },

    // 2. TOP SECTION (Toggle & Logo)
    topSection: {
      display: 'flex',
      alignItems: 'center',
      gap: '12px',
      marginBottom: '20px',
      paddingLeft: isExpanded ? '16px' : '0', 
      justifyContent: isExpanded ? 'flex-start' : 'center',
      height: '50px',
      minHeight: '50px',
      overflow: 'hidden'
    },
    menuBtn: {
      cursor: 'pointer',
      color: isDark ? '#fff' : '#1e293b',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      width: '36px',
      height: '36px',
      borderRadius: '8px',
      transition: 'background 0.2s',
      // Center the button in the sidebar when collapsed
      margin: isExpanded ? '0' : '0 auto', 
    },
    logoText: {
      fontSize: '18px',
      fontWeight: '800',
      color: '#6366f1',
      whiteSpace: 'nowrap',
      opacity: isExpanded ? 1 : 0,
      transition: 'opacity 0.2s',
      display: isExpanded ? 'block' : 'none'
    },

    // 3. NAV SECTION
    nav: {
      flex: 1,
      display: 'flex',
      flexDirection: 'column',
      gap: '6px', 
      width: '100%',
    },
    navItem: (isActive) => ({
      display: 'flex',
      alignItems: 'center',
      gap: '14px',
      // If collapsed: Fixed square size (40px) & centered. 
      // If expanded: Full width minus padding & left aligned.
      width: isExpanded ? '90%' : '40px', 
      height: '40px',
      margin: '0 auto', // Centers the box horizontally
      padding: isExpanded ? '0 12px' : '0', // Inner padding only when expanded
      
      borderRadius: '10px',
      cursor: 'pointer',
      
      backgroundColor: isActive ? (isDark ? '#334155' : '#eff6ff') : 'transparent',
      color: isActive ? '#6366f1' : (isDark ? '#94a3b8' : '#64748b'),
      
      transition: 'all 0.2s',
      overflow: 'hidden',
      whiteSpace: 'nowrap',
      justifyContent: isExpanded ? 'flex-start' : 'center'
    }),
    navLabel: {
      fontWeight: '600',
      fontSize: '14px',
      opacity: isExpanded ? 1 : 0,
      display: isExpanded ? 'block' : 'none'
    },

    // 4. BOTTOM USER SECTION
    userSection: {
      marginTop: 'auto',
      borderTop: isDark ? '1px solid #334155' : '1px solid #f1f5f9',
      padding: '15px 0',
      display: 'flex',
      alignItems: 'center',
      gap: '12px',
      justifyContent: isExpanded ? 'flex-start' : 'center',
      paddingLeft: isExpanded ? '16px' : '0',
      overflow: 'hidden'
    },
    avatar: {
      width: '32px',
      height: '32px',
      borderRadius: '50%',
      objectFit: 'cover',
      minWidth: '32px',
      border: '1px solid #e2e8f0'
    },
    userDetails: {
      opacity: isExpanded ? 1 : 0,
      whiteSpace: 'nowrap',
      display: isExpanded ? 'block' : 'none'
    }
  };

  return (
    <>
      {/* Spacer */}
      <div style={{ width: isExpanded ? '250px' : '56px', transition: 'width 0.3s ease' }}></div>

      <div style={styles.sidebar}>
        
        {/* Top: Menu Icon */}
        <div style={styles.topSection}>
          <div 
            style={styles.menuBtn} 
            onClick={() => setIsExpanded(!isExpanded)}
            onMouseEnter={(e) => e.currentTarget.style.backgroundColor = isDark ? '#334155' : '#f1f5f9'}
            onMouseLeave={(e) => e.currentTarget.style.backgroundColor = 'transparent'}
          >
            <Menu size={22} />
          </div>
          <div style={styles.logoText}>EventSphere</div>
        </div>

        {/* Middle: Icons (Square Buttons when collapsed) */}
        <div style={styles.nav}>
          {menuItems.map((item) => {
            const isActive = location.pathname === item.path;
            return (
              <div 
                key={item.label} 
                style={styles.navItem(isActive)} 
                onClick={() => navigate(item.path)}
                title={!isExpanded ? item.label : ''} 
              >
                <div style={{display: 'flex', alignItems:'center', justifyContent:'center'}}>
                   {item.icon}
                </div>
                <span style={styles.navLabel}>{item.label}</span>
              </div>
            );
          })}
        </div>

        {/* Bottom: User */}
        <div style={styles.userSection}>
          <img src={user.profileImage} alt="User" style={styles.avatar} />
          <div style={styles.userDetails}>
            <div style={{fontWeight: 'bold', fontSize: '13px', color: isDark?'#fff':'#333'}}>
              {user.name.split(' ')[0]}
            </div>
            <div style={{fontSize: '11px', color: '#64748b'}}>Student</div>
          </div>
        </div>

        {/* Logout */}
        <div style={{ marginBottom: '10px' }}>
          <div 
            style={{ ...styles.navItem(false), color: '#ef4444' }} 
            onClick={onLogout}
            title={!isExpanded ? "Log Out" : ""}
          >
            <div style={{display: 'flex', alignItems:'center', justifyContent:'center'}}>
               <LogOut size={22} />
            </div>
            <span style={styles.navLabel}>Log Out</span>
          </div>
        </div>

      </div>
    </>
  );
};

export default Sidebar;
