import React from 'react';

const PhoneFrame = ({ children }) => {
  const styles = {
    // The background behind the phone
    wrapper: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      minHeight: '100vh',
      backgroundColor: '#e2e8f0', 
      padding: '20px',
      boxSizing: 'border-box',
      fontFamily: 'sans-serif'
    },
    // The Android Compact Body
    phone: {
      width: '100%',
      maxWidth: '360px', // <--- ANDROID COMPACT WIDTH
      height: '760px',   // <--- ANDROID COMPACT HEIGHT
      maxHeight: '90vh', 
      backgroundColor: '#fff',
      borderRadius: '35px', // Slightly less rounded for Android look
      boxShadow: '0 0 0 10px #1a202c, 0 20px 50px rgba(0,0,0,0.3)', // Darker bezel
      overflow: 'hidden', 
      position: 'relative',
      display: 'flex',
      flexDirection: 'column'
    },
    // The Notch (Punch-hole style for Android)
    notch: {
      position: 'absolute',
      top: '12px',
      left: '50%',
      transform: 'translateX(-50%)',
      width: '12px',
      height: '12px',
      backgroundColor: '#000',
      borderRadius: '50%',
      zIndex: 9999
    },
    // The Scrollable Screen Area
    screen: {
      flex: 1,
      overflowY: 'auto', 
      overflowX: 'hidden',
      height: '100%',
      position: 'relative',
      backgroundColor: '#fff' 
    }
  };

  return (
    <div style={styles.wrapper}>
      <div style={styles.phone}>
        {/* Android Punch-hole Camera */}
        <div style={styles.notch}></div>

        {/* App Content */}
        <div style={styles.screen} className="hide-scrollbar">
          {children}
        </div>
      </div>

      <style>{`
        .hide-scrollbar::-webkit-scrollbar {
          display: none;
        }
        .hide-scrollbar {
          -ms-overflow-style: none;
          scrollbar-width: none;
        }
      `}</style>
    </div>
  );
};

export default PhoneFrame;
