import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { ArrowLeft, CheckCircle } from 'lucide-react';

const StudentSignUp = ({ onLogin }) => {
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [isStudent, setIsStudent] = useState(true);

  // Form State - Using 'regNo' instead of 'rollNo'
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    collegeName: '',
    regNo: '', // <--- CHANGED
    department: '',
    password: '',
    confirmPassword: ''
  });

  const [errors, setErrors] = useState({});

  const styles = {
    container: {
      height: '100vh', width: '100vw', display: 'flex', alignItems: 'center', justifyContent: 'center',
      backgroundColor: '#f3f4f6', fontFamily: "'Inter', sans-serif"
    },
    card: {
      display: 'flex', width: '900px', height: '600px', backgroundColor: '#fff',
      borderRadius: '24px', overflow: 'hidden', boxShadow: '0 20px 60px -10px rgba(0,0,0,0.1)'
    },
    leftPanel: {
      flex: 1, background: 'linear-gradient(135deg, #6366f1, #8b5cf6)', padding: '40px',
      display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', color: '#fff', textAlign: 'center'
    },
    rightPanel: {
      flex: 1.2, padding: '50px', display: 'flex', flexDirection: 'column', position: 'relative'
    },
    header: { marginBottom: '30px', textAlign: 'center' },
    title: { fontSize: '26px', fontWeight: '800', color: '#1f2937', marginBottom: '10px' },
    stepIndicator: { fontSize: '12px', fontWeight: '600', color: '#6366f1', textTransform: 'uppercase', letterSpacing: '1px' },
    inputGroup: { marginBottom: '18px' },
    label: { display: 'block', fontSize: '13px', fontWeight: '700', color: '#374151', marginBottom: '6px' },
    input: {
      width: '100%', padding: '14px', borderRadius: '10px', border: '2px solid #e5e7eb',
      fontSize: '14px', outline: 'none', transition: 'all 0.2s', boxSizing: 'border-box'
    },
    row: { display: 'flex', gap: '15px' },
    btn: {
      width: '100%', padding: '16px', backgroundColor: '#6366f1', color: '#fff', border: 'none',
      borderRadius: '12px', fontSize: '16px', fontWeight: 'bold', cursor: 'pointer', marginTop: '20px',
      boxShadow: '0 4px 12px rgba(99, 102, 241, 0.3)'
    },
    backBtn: {
      position: 'absolute', top: '30px', left: '30px', background: 'none', border: 'none',
      cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '5px', color: '#6b7280', fontWeight: '600'
    },
    toggleContainer: { display: 'flex', backgroundColor: '#f3f4f6', borderRadius: '10px', padding: '4px', marginBottom: '20px' },
    toggleBtn: (active) => ({
      flex: 1, padding: '10px', borderRadius: '8px', border: 'none', fontSize: '13px', fontWeight: '600',
      cursor: 'pointer', backgroundColor: active ? '#6366f1' : 'transparent', color: active ? '#fff' : '#6b7280', transition: 'all 0.2s'
    }),
    error: { color: '#ef4444', fontSize: '12px', marginTop: '4px' }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setErrors({ ...errors, [e.target.name]: null });
  };

  const validateStep1 = () => {
    let errs = {};
    if (!formData.fullName) errs.fullName = "Full Name is required";
    if (!formData.email) errs.email = "Email is required";
    setErrors(errs);
    return Object.keys(errs).length === 0;
  };

  const validateStep2 = () => {
    let errs = {};
    if (!formData.collegeName) errs.collegeName = "College Name is required";
    if (!formData.regNo) errs.regNo = "Reg No is required"; // <--- VALIDATION CHANGED
    if (!formData.department) errs.department = "Department is required";
    setErrors(errs);
    return Object.keys(errs).length === 0;
  };

  const validateStep3 = () => {
    let errs = {};
    if (!formData.password) errs.password = "Password is required";
    if (formData.password !== formData.confirmPassword) errs.confirmPassword = "Passwords do not match";
    setErrors(errs);
    return Object.keys(errs).length === 0;
  };

  const handleNext = () => {
    if (step === 1 && validateStep1()) setStep(2);
    if (step === 2 && validateStep2()) setStep(3);
  };

  const handleSubmit = () => {
    if (validateStep3()) {
      onLogin({
        name: formData.fullName,
        email: formData.email,
        college: formData.collegeName,
        role: isStudent ? 'student' : 'teacher',
        department: formData.department,
        regNo: formData.regNo, // <--- PASSING REG NO
        profileImage: 'https://images.unsplash.com/photo-1535713875002-d1d0cf377fde?w=200'
      });
      navigate('/');
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <div style={styles.leftPanel}>
          <div style={{ fontSize: '40px', marginBottom: '10px' }}>ðŸš€</div>
          <h1 style={{ fontSize: '32px', fontWeight: '800', marginBottom: '10px' }}>Join Us!</h1>
          <p style={{ opacity: 0.9 }}>Create your account in 3 easy steps.</p>
        </div>

        <div style={styles.rightPanel}>
          {step > 1 && (
            <button style={styles.backBtn} onClick={() => setStep(step - 1)}>
              <ArrowLeft size={16} /> Back
            </button>
          )}

          <div style={styles.header}>
            <div style={styles.stepIndicator}>Step {step} of 3</div>
            <h2 style={styles.title}>
              {step === 1 ? "Personal Info" : step === 2 ? "Academic Details" : "Security"}
            </h2>
          </div>

          {step === 1 && (
            <div style={{ animation: 'fadeIn 0.3s' }}>
              <div style={styles.inputGroup}>
                <label style={styles.label}>Full Name</label>
                <input style={styles.input} name="fullName" placeholder="John Doe" value={formData.fullName} onChange={handleChange} />
                {errors.fullName && <div style={styles.error}>{errors.fullName}</div>}
              </div>
              <div style={styles.inputGroup}>
                <label style={styles.label}>Email Address</label>
                <input style={styles.input} name="email" placeholder="you@example.com" value={formData.email} onChange={handleChange} />
                {errors.email && <div style={styles.error}>{errors.email}</div>}
              </div>
              <button style={styles.btn} onClick={handleNext}>Continue <ArrowLeft size={16} style={{ rotate: '180deg' }} /></button>
            </div>
          )}

          {step === 2 && (
            <div style={{ animation: 'fadeIn 0.3s' }}>
              <div style={styles.toggleContainer}>
                <button style={styles.toggleBtn(isStudent)} onClick={() => setIsStudent(true)}>Student</button>
                <button style={styles.toggleBtn(!isStudent)} onClick={() => setIsStudent(false)}>Teacher</button>
              </div>

              <div style={styles.inputGroup}>
                <label style={styles.label}>College Name</label>
                <input style={styles.input} name="collegeName" placeholder="Engineering Tech Institute" value={formData.collegeName} onChange={handleChange} />
                {errors.collegeName && <div style={styles.error}>{errors.collegeName}</div>}
              </div>

              <div style={styles.row}>
                <div style={{ flex: 1 }}>
                  {/* LABEL CHANGED TO REG NO */}
                  <label style={styles.label}>Reg No</label> 
                  <input style={styles.input} name="regNo" placeholder="ETI-2024-001" value={formData.regNo} onChange={handleChange} />
                  {errors.regNo && <div style={styles.error}>{errors.regNo}</div>}
                </div>

                <div style={{ flex: 1 }}>
                  <label style={styles.label}>Dept</label>
                  <select style={styles.input} name="department" value={formData.department} onChange={handleChange}>
                    <option value="">Select</option>
                    <option value="CSE">CSE</option>
                    <option value="ECE">ECE</option>
                    <option value="MECH">MECH</option>
                  </select>
                  {errors.department && <div style={styles.error}>{errors.department}</div>}
                </div>
              </div>

              <button style={styles.btn} onClick={handleNext}>Continue <ArrowLeft size={16} style={{ rotate: '180deg' }} /></button>
            </div>
          )}

          {step === 3 && (
            <div style={{ animation: 'fadeIn 0.3s' }}>
              <div style={styles.inputGroup}>
                <label style={styles.label}>Password</label>
                <input style={styles.input} type="password" name="password" placeholder="â€¢â€¢â€¢â€¢â€¢â€¢" value={formData.password} onChange={handleChange} />
                {errors.password && <div style={styles.error}>{errors.password}</div>}
              </div>
              <div style={styles.inputGroup}>
                <label style={styles.label}>Confirm Password</label>
                <input style={styles.input} type="password" name="confirmPassword" placeholder="â€¢â€¢â€¢â€¢â€¢â€¢" value={formData.confirmPassword} onChange={handleChange} />
                {errors.confirmPassword && <div style={styles.error}>{errors.confirmPassword}</div>}
              </div>
              <button style={styles.btn} onClick={handleSubmit}>Create Account <CheckCircle size={18} /></button>
            </div>
          )}

          <div style={{ textAlign: 'center', marginTop: '20px', fontSize: '13px', color: '#6b7280' }}>
            Already have an account? <Link to="/signin" style={{ color: '#6366f1', fontWeight: 'bold', textDecoration: 'none' }}>Login</Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default StudentSignUp;
