import React from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowLeft, ChevronRight } from 'lucide-react';

const Settings = () => {
  const navigate = useNavigate();

  // Settings menu configuration
  const settingsItems = [
    { 
      title: "Notifications", 
      desc: "Manage push and in-app alerts", 
      path: "/settings/notifications" 
    },
    { 
      title: "App Theme", 
      desc: "Light / Dark appearance", 
      status: "Light", 
      path: "/settings/theme" 
    },
    { 
      title: "Language", 
      desc: "Choose your preferred language", 
      status: "English", 
      path: "/settings/language" 
    },
    { 
      title: "Privacy Policy", 
      desc: "How your data is collected and used", 
      path: "/privacy" 
    },
    { 
      title: "Terms & Conditions", 
      desc: "Review the guidelines for using the app", 
      path: "/terms" 
    }
  ];

  return (
    <div style={styles.container}>
      {/* Header */}
      <header style={styles.header}>
        <ArrowLeft size={24} onClick={() => navigate(-1)} style={{ cursor: 'pointer' }} />
        <h2 style={styles.headerTitle}>Settings</h2>
        <div style={{ width: 24 }}></div>
      </header>

      <div style={styles.list}>
        {settingsItems.map((item, index) => (
          <div 
            key={index} 
            style={styles.card} 
            onClick={() => item.path && navigate(item.path)}
          >
            <div style={styles.content}>
              <div style={styles.topRow}>
                <span style={styles.title}>{item.title}</span>
                {/* Status indicator for Theme/Language */}
                {item.status && <span style={styles.statusBadge}>{item.status}</span>}
              </div>
              <p style={styles.desc}>{item.desc}</p>
            </div>
            <ChevronRight size={20} color="#cbd5e1" />
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  container: { backgroundColor: '#f8fafc', minHeight: '100vh', fontFamily: 'sans-serif' },
  header: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '16px', backgroundColor: '#fff', borderBottom: '1px solid #f1f5f9' },
  headerTitle: { fontSize: '18px', fontWeight: 'bold', margin: 0 },
  list: { padding: '16px' },
  card: { 
    display: 'flex', 
    alignItems: 'center', 
    backgroundColor: '#fff', 
    padding: '16px', 
    borderRadius: '16px', 
    marginBottom: '12px', 
    border: '1px solid #f1f5f9',
    cursor: 'pointer'
  },
  content: { flex: 1 },
  topRow: { display: 'flex', alignItems: 'center', gap: '10px', marginBottom: '4px' },
  title: { fontSize: '15px', fontWeight: 'bold', color: '#1e293b' },
  statusBadge: { 
    backgroundColor: '#f0f4ff', 
    color: '#5c5cfc', 
    fontSize: '11px', 
    padding: '4px 10px', 
    borderRadius: '10px', 
    fontWeight: 'bold' 
  },
  desc: { fontSize: '12px', color: '#94a3b8', margin: 0 },
};

export default Settings;
