import React from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  User, Edit, Lock, Settings, LogOut, ArrowLeft, ChevronRight 
} from 'lucide-react';

const Profile = ({ user, theme, t, onLogout }) => { // <--- Accept 'onLogout' prop
  const navigate = useNavigate();
  const isDark = theme === 'dark';

  // Function to handle logout click
  const handleLogoutClick = () => {
    // 1. Call the logout function from App.js
    if (onLogout) {
      onLogout();
    }
    // 2. Redirect to Landing Page (optional, as App.js usually handles this via state)
    navigate('/');
  };

  const styles = {
    container: {
      padding: '20px',
      backgroundColor: isDark ? '#0f172a' : '#f8fafc', // Light gray background
      minHeight: '100vh',
      maxWidth: '430px',
      margin: '0 auto',
      boxSizing: 'border-box',
      fontFamily: 'sans-serif'
    },
    header: {
      display: 'flex',
      alignItems: 'center',
      marginBottom: '20px',
      position: 'relative',
      justifyContent: 'center'
    },
    backBtn: {
      position: 'absolute',
      left: 0,
      cursor: 'pointer',
      color: isDark ? '#fff' : '#1e293b'
    },
    headerTitle: {
      fontSize: '18px',
      fontWeight: 'bold',
      color: isDark ? '#fff' : '#1e293b'
    },
    // Main White Card
    mainCard: {
      backgroundColor: isDark ? '#1e293b' : '#fff',
      borderRadius: '24px',
      padding: '30px 20px',
      textAlign: 'center',
      boxShadow: '0 4px 20px rgba(0,0,0,0.02)',
      marginBottom: '20px'
    },
    avatarContainer: {
      width: '90px',
      height: '90px',
      margin: '0 auto 15px auto',
      position: 'relative'
    },
    avatar: {
      width: '100%',
      height: '100%',
      borderRadius: '50%',
      objectFit: 'cover',
      border: '3px solid #fff',
      boxShadow: '0 4px 10px rgba(0,0,0,0.1)'
    },
    name: {
      fontSize: '22px',
      fontWeight: '800',
      color: isDark ? '#fff' : '#1e293b',
      marginBottom: '5px'
    },
    email: {
      fontSize: '14px',
      color: '#64748b',
      marginBottom: '15px'
    },
    badge: {
      backgroundColor: '#5c5cfc',
      color: '#fff',
      padding: '6px 20px',
      borderRadius: '20px',
      fontSize: '12px',
      fontWeight: 'bold',
      display: 'inline-block'
    },
    // Details Section
    detailsCard: {
      backgroundColor: isDark ? '#1e293b' : '#fff',
      borderRadius: '20px',
      padding: '20px',
      marginBottom: '20px',
      display: 'flex',
      flexDirection: 'column',
      gap: '12px'
    },
    detailRow: {
      display: 'flex',
      justifyContent: 'space-between',
      fontSize: '14px'
    },
    detailLabel: {
      color: '#94a3b8'
    },
    detailValue: {
      fontWeight: 'bold',
      color: isDark ? '#fff' : '#1e293b'
    },
    // Menu Buttons
    menuButton: {
      width: '100%',
      backgroundColor: isDark ? '#1e293b' : '#fff',
      padding: '16px',
      borderRadius: '20px',
      border: 'none',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      marginBottom: '12px',
      cursor: 'pointer',
      boxShadow: '0 2px 5px rgba(0,0,0,0.02)'
    },
    menuLeft: {
      display: 'flex',
      alignItems: 'center',
      gap: '15px'
    },
    iconBox: (color, bg) => ({
      width: '36px',
      height: '36px',
      borderRadius: '10px',
      backgroundColor: bg,
      color: color,
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    }),
    menuText: {
      fontSize: '16px',
      fontWeight: '600',
      color: isDark ? '#fff' : '#1e293b'
    },
    logoutText: {
      fontSize: '16px',
      fontWeight: '600',
      color: '#ef4444' // Red color for logout
    }
  };

  return (
    <div style={styles.container}>
      {/* Header */}
      <div style={styles.header}>
        <div style={styles.backBtn} onClick={() => navigate(-1)}>
          <ArrowLeft size={24} />
        </div>
        <div style={styles.headerTitle}>Profile</div>
      </div>

      {/* Main Profile Card */}
      <div style={styles.mainCard}>
        <div style={styles.avatarContainer}>
          {/* Using a placeholder image or user's first initial */}
          <img 
            src="https://images.unsplash.com/photo-1494790108377-be9c29b29330?w=200&q=80" 
            alt="Profile" 
            style={styles.avatar} 
          />
        </div>
        <h2 style={styles.name}>{user.name}</h2>
        <p style={styles.email}>{user.email}</p>
        <div style={styles.badge}>Student</div>
      </div>

      {/* College Info */}
      <div style={styles.detailsCard}>
        <div style={styles.detailRow}>
          <span style={styles.detailLabel}>College Name</span>
          <span style={styles.detailValue}>{user.college}</span>
        </div>
        <div style={styles.detailRow}>
          <span style={styles.detailLabel}>Register Number</span>
          <span style={styles.detailValue}>{user.regNo}</span>
        </div>
      </div>

      {/* Menu Actions */}
      <button style={styles.menuButton} onClick={() => navigate('/edit-profile')}>
        <div style={styles.menuLeft}>
          <div style={styles.iconBox('#5c5cfc', '#e0e7ff')}>
            <User size={20} />
          </div>
          <span style={styles.menuText}>Edit Profile</span>
        </div>
        <ChevronRight size={20} color="#cbd5e1" />
      </button>

      <button style={styles.menuButton} onClick={() => navigate('/change-password')}>
        <div style={styles.menuLeft}>
          <div style={styles.iconBox('#5c5cfc', '#e0e7ff')}>
            <Lock size={20} />
          </div>
          <span style={styles.menuText}>Change Password</span>
        </div>
        <ChevronRight size={20} color="#cbd5e1" />
      </button>

      <button style={styles.menuButton} onClick={() => navigate('/settings')}>
        <div style={styles.menuLeft}>
          <div style={styles.iconBox('#5c5cfc', '#e0e7ff')}>
            <Settings size={20} />
          </div>
          <span style={styles.menuText}>Settings</span>
        </div>
        <ChevronRight size={20} color="#cbd5e1" />
      </button>

      {/* LOGOUT BUTTON */}
      <button style={styles.menuButton} onClick={handleLogoutClick}>
        <div style={styles.menuLeft}>
          <div style={styles.iconBox('#ef4444', '#fee2e2')}>
            <LogOut size={20} />
          </div>
          <span style={styles.logoutText}>Logout</span>
        </div>
        <ChevronRight size={20} color="#cbd5e1" />
      </button>

    </div>
  );
};

export default Profile;
