import React, { useState } from 'react'; // Fixes: 'useState' is not defined
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'; // Fixes: 'Router', 'Routes', 'Route' not defined

// Import your page components
import Dashboard from './pages/Dashboard'; // Fixes: 'Dashboard' is not defined
import Events from './pages/Events';       // Fixes: 'Events' is not defined
import MyEvents from './pages/MyEvents';   // Fixes: 'MyEvents' is not defined
import Profile from './pages/Profile';     // Fixes: 'Profile' is not defined

function App() {
  const [userName] = useState("Priya Sharma"); // Matches your profile design
  const [searchTerm, setSearchTerm] = useState("");
  
  // Data for your events pages
  const [allEvents] = useState([
    { 
      id: 1, 
      title: "Campus Tech Summit 2024", 
      date: "Mar 18, 2024 - 10:00 AM", 
      location: "Main Auditorium, Block A", 
      category: "Tech", 
      image: "https://images.unsplash.com/photo-1540575861501-7ad060e39fe5?w=800" 
    },
    { 
      id: 2, 
      title: "Annual Cultural Night", 
      date: "Mar 22, 2024 - 6:30 PM", 
      location: "Open Air Theatre", 
      category: "Cultural", 
      image: "https://images.unsplash.com/photo-1459749411177-042180ce673c?w=800" 
    }
  ]);

  const [registeredList] = useState([
    { id: 101, name: "Spring Tech Symposium", date: "Mar 18, 2024", venue: "Main Campus", ticketId: "ST-2024-1842" }
  ]);

  // Filter logic for global search
  const filteredEvents = allEvents.filter(event => 
    event.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Router>
      <div style={{ maxWidth: '375px', margin: '0 auto', backgroundColor: '#f8fafc', minHeight: '100vh' }}>
        <Routes>
          <Route path="/" element={
            <Dashboard 
              userName={userName} 
              events={filteredEvents} 
              searchTerm={searchTerm} 
              setSearchTerm={setSearchTerm} 
              regCount={registeredList.length} 
            />
          } />
          <Route path="/events" element={
            <Events 
              events={filteredEvents} 
              searchTerm={searchTerm} 
              setSearchTerm={setSearchTerm} 
            />
          } />
          <Route path="/my-events" element={
            <MyEvents registeredEvents={registeredList} />
          } />
          <Route path="/profile" element={
            <Profile userName={userName} />
          } />
        </Routes>
      </div>
    </Router>
  );
}

// IMPORTANT: This fixes the "export 'default' was not found" error
export default App;
