import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Search, Clock } from 'lucide-react';

const Events = ({ theme, allEvents, searchTerm, setSearchTerm }) => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState("All");
  const isDark = theme === 'dark';

  const filtered = allEvents.filter(e => 
    (activeTab === "All" || e.category === activeTab) &&
    e.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div style={{ 
      padding: '20px', 
      backgroundColor: isDark ? '#0f172a' : '#fff', 
      minHeight: '100vh', 
      paddingBottom: '120px', // INCREASED: clears the bottom nav
      boxSizing: 'border-box'
    }}>
      <div style={{ display: 'flex', alignItems: 'center', gap: '15px', marginBottom: '20px' }}>
         <h2 style={{ margin: 0, color: isDark ? '#fff' : '#1e293b' }}>Events</h2>
      </div>
      
      {/* Search Bar */}
      <div style={{ display: 'flex', alignItems: 'center', backgroundColor: isDark ? '#1e293b' : '#f1f5f9', padding: '12px', borderRadius: '12px', marginBottom: '20px' }}>
        <Search size={18} color="#94a3b8" />
        <input 
          value={searchTerm} 
          onChange={(e) => setSearchTerm(e.target.value)} 
          placeholder="Search events..." 
          style={{ border: 'none', background: 'transparent', marginLeft: '10px', flex: 1, color: isDark ? '#fff' : '#000', outline: 'none', fontSize: '16px' }}
        />
      </div>

      {/* Filter Tabs */}
      <div style={{ display: 'flex', gap: '10px', overflowX: 'auto', marginBottom: '20px', paddingBottom: '5px' }} className="hide-scrollbar">
        {["All", "Tech", "Cultural", "Sports"].map(tab => (
          <button key={tab} onClick={() => setActiveTab(tab)} style={{
            padding: '8px 20px', borderRadius: '20px', border: 'none', whiteSpace: 'nowrap',
            backgroundColor: activeTab === tab ? '#5c5cfc' : (isDark ? '#1e293b' : '#f1f5f9'),
            color: activeTab === tab ? '#fff' : '#94a3b8', fontWeight: 'bold', cursor: 'pointer'
          }}>{tab}</button>
        ))}
      </div>

      {/* Event Cards */}
      {filtered.map(event => (
        <div key={event.id} onClick={() => navigate(`/event-details/${event.id}`)} style={{ 
          backgroundColor: isDark ? '#1e293b' : '#fff', 
          borderRadius: '20px', 
          marginBottom: '20px', 
          overflow: 'hidden', 
          boxShadow: '0 4px 6px rgba(0,0,0,0.05)',
          border: isDark ? '1px solid #334155' : '1px solid #f1f5f9',
          cursor: 'pointer'
        }}>
          <img src={event.image} alt="" style={{ width: '100%', height: '160px', objectFit: 'cover' }} />
          <div style={{ padding: '15px' }}>
            <h3 style={{ margin: '0 0 10px 0', color: isDark ? '#fff' : '#1e293b' }}>{event.title}</h3>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div style={{ fontSize: '12px', color: '#94a3b8', display: 'flex', alignItems: 'center', gap: '5px' }}>
                <Clock size={14}/> {event.date}
              </div>
              <button style={{ backgroundColor: '#5c5cfc', color: '#fff', border: 'none', padding: '8px 16px', borderRadius: '8px', fontWeight: '600', cursor: 'pointer' }}>
                {event.isRegistered ? "View" : "Register"}
              </button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Events;
