import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { Settings } from 'lucide-react';

const StudentSignUp = ({ onLogin }) => {
  const navigate = useNavigate();
  const [isStudent, setIsStudent] = useState(true);

  const [formData, setFormData] = useState({
    fullName: '',
    collegeName: '',
    rollNo: '',
    department: '',
    email: '',
    password: '',
    confirmPassword: ''
  });

  const styles = {
    // 1. PAGE CONTAINER (Purple Gradient)
    pageContainer: {
      height: '100vh',
      width: '100vw',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontFamily: "'Inter', sans-serif",
      background: 'linear-gradient(135deg, #7c3aed 0%, #a855f7 100%)', 
      overflow: 'hidden' 
    },

    // 2. MAIN CARD WRAPPER
    mainWrapper: {
      display: 'flex',
      width: '90%',
      maxWidth: '900px',
      height: '85vh', 
      maxHeight: '650px', // Increased slightly to fit labels
      backgroundColor: '#fff',
      borderRadius: '24px',
      overflow: 'hidden',
      boxShadow: '0 20px 60px rgba(0,0,0,0.2)',
    },

    // 3. LEFT PANEL (Hidden on Mobile)
    leftPanel: {
      flex: 1,
      background: 'linear-gradient(135deg, #6d28d9 0%, #9333ea 100%)',
      display: 'none', 
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      color: '#fff',
      padding: '40px',
      textAlign: 'center'
    },

    logoIcon: { marginBottom: '20px' },
    welcomeTitle: { fontSize: '28px', fontWeight: '800', marginBottom: '10px' },
    welcomeText: { fontSize: '14px', opacity: 0.8, lineHeight: '1.6', maxWidth: '280px' },

    // 4. RIGHT PANEL (The Form)
    rightPanel: {
      flex: 1.3, 
      padding: '30px', 
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      backgroundColor: '#fff',
      overflowY: 'auto' // Scrolls internally if labels push content too tall
    },

    // Header
    header: { textAlign: 'center', marginBottom: '15px' },
    title: { fontSize: '22px', fontWeight: '800', color: '#1e293b', marginBottom: '4px' },
    subtitle: { fontSize: '13px', color: '#64748b' },

    // Toggle
    toggleContainer: {
      display: 'flex',
      backgroundColor: '#f1f5f9',
      borderRadius: '10px',
      padding: '4px',
      marginBottom: '15px'
    },
    toggleBtn: (active) => ({
      flex: 1, padding: '8px', borderRadius: '8px', border: 'none',
      fontSize: '13px', fontWeight: '600', cursor: 'pointer',
      backgroundColor: active ? '#7c3aed' : 'transparent', 
      color: active ? '#fff' : '#64748b',
      transition: 'all 0.2s'
    }),

    // FORM STYLES
    form: { display: 'flex', flexDirection: 'column', gap: '12px' },

    // Field Wrapper
    fieldGroup: { display: 'flex', flexDirection: 'column', gap: '5px' },

    // LABEL STYLE (The part you asked for)
    label: { 
      fontSize: '13px', 
      fontWeight: '700', 
      color: '#374151', // Dark Grey Text
      marginLeft: '2px' 
    },

    input: {
      width: '100%', padding: '12px', borderRadius: '10px',
      border: '1px solid #e2e8f0', backgroundColor: '#f8fafc', 
      fontSize: '13px', outline: 'none', color: '#333',
      boxSizing: 'border-box'
    },
    row: { display: 'flex', gap: '12px' },
    col: { flex: 1, display: 'flex', flexDirection: 'column', gap: '5px' },

    submitBtn: {
      width: '100%', padding: '14px', borderRadius: '12px', border: 'none',
      backgroundColor: '#7c3aed', color: '#fff',
      fontSize: '15px', fontWeight: 'bold', cursor: 'pointer', marginTop: '10px',
      boxShadow: '0 4px 15px rgba(124, 58, 237, 0.3)'
    },

    terms: { fontSize: '11px', color: '#64748b', display: 'flex', alignItems: 'center', gap: '6px', marginTop: '5px' },

    mobileCardStyle: {
       background: 'rgba(255,255,255,0.95)', 
       borderRadius: '20px'
    }
  };

  const isDesktop = window.innerWidth > 768; 

  const handleChange = (e) => setFormData({...formData, [e.target.name]: e.target.value});

  const handleSubmit = () => {
    onLogin({ 
      name: formData.fullName || 'New User',
      college: formData.collegeName || 'Engineering Tech Institute', 
      role: isStudent ? 'student' : 'teacher',
    });
    navigate('/');
  };

  return (
    <div style={styles.pageContainer}>
      <div style={{
        ...styles.mainWrapper, 
        backgroundColor: isDesktop ? '#fff' : 'transparent',
        boxShadow: isDesktop ? '0 20px 60px rgba(0,0,0,0.2)' : 'none', 
        height: isDesktop ? '600px' : 'auto', 
        maxWidth: isDesktop ? '900px' : '400px'
      }}>

        {/* LEFT PANEL */}
        <div style={{ ...styles.leftPanel, display: isDesktop ? 'flex' : 'none' }}>
          <div style={styles.logoIcon}><Settings size={50} color="#fff" /></div>
          <h1 style={styles.welcomeTitle}>EventSphere</h1>
          <p style={styles.welcomeText}>Manage workshops, tickets, and attendance seamlessly.</p>
        </div>

        {/* RIGHT PANEL */}
        <div style={{ ...styles.rightPanel, ...(isDesktop ? {} : styles.mobileCardStyle) }}>

          <div style={styles.header}>
            <h2 style={styles.title}>Create Account</h2>
            <p style={styles.subtitle}>Sign up to get started</p>
          </div>

          <div style={styles.toggleContainer}>
            <button style={styles.toggleBtn(isStudent)} onClick={() => setIsStudent(true)}>Student</button>
            <button style={styles.toggleBtn(!isStudent)} onClick={() => setIsStudent(false)}>Teacher</button>
          </div>

          <div style={styles.form}>

            {/* Full Name */}
            <div style={styles.fieldGroup}>
              <label style={styles.label}>Full Name</label>
              <input style={styles.input} placeholder="e.g. John Doe" name="fullName" onChange={handleChange} />
            </div>

            {/* College Name */}
            <div style={styles.fieldGroup}>
              <label style={styles.label}>College Name</label>
              <input style={styles.input} placeholder="Engineering Tech Institute" name="collegeName" onChange={handleChange} />
            </div>

            {/* Split Row: Roll No & Dept */}
            <div style={styles.row}>
               <div style={styles.col}>
                 <label style={styles.label}>{isStudent ? "Roll No" : "Emp ID"}</label>
                 <input style={styles.input} placeholder={isStudent ? "ID-2024" : "EMP-01"} name="rollNo" onChange={handleChange} />
               </div>
               <div style={styles.col}>
                 <label style={styles.label}>Dept</label>
                 <select style={styles.input} name="department" onChange={handleChange}>
                   <option>Select</option>
                   <option>CSE</option>
                   <option>ECE</option>
                   <option>MECH</option>
                 </select>
               </div>
            </div>

            {/* Email */}
            <div style={styles.fieldGroup}>
              <label style={styles.label}>Email Address</label>
              <input style={styles.input} placeholder="you@example.com" name="email" type="email" onChange={handleChange} />
            </div>

            {/* Password Row */}
            <div style={styles.row}>
              <div style={styles.col}>
                 <label style={styles.label}>Password</label>
                 <input style={styles.input} placeholder="••••••" name="password" type="password" onChange={handleChange} />
              </div>
              <div style={styles.col}>
                 <label style={styles.label}>Confirm</label>
                 <input style={styles.input} placeholder="••••••" name="confirmPassword" type="password" onChange={handleChange} />
              </div>
            </div>

            <div style={styles.terms}>
               <input type="checkbox" />
               <span>I agree to Terms & Privacy</span>
            </div>

            <button style={styles.submitBtn} onClick={handleSubmit}>Register</button>
          </div>

           <div style={{textAlign:'center', marginTop:'12px', fontSize:'12px', color:'#64748b'}}>
             Already have an account? <Link to="/signin" style={{color: '#7c3aed', fontWeight:'bold', textDecoration:'none'}}>Login</Link>
           </div>
        </div>

      </div>
    </div>
  );
};

export default StudentSignUp;
