import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { Eye, EyeOff, ArrowLeft, GraduationCap, Mail, Lock, User, Phone, Building2 } from 'lucide-react';

const StudentSignUp = ({ onLogin }) => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    fullName: '',
    rollNo: '',
    department: '',
    email: '',
    phone: '',
    password: '',
    confirmPassword: '',
    agree: false
  });

  const [showPass, setShowPass] = useState(false);
  const [errors, setErrors] = useState({});

  // Styles optimized for cross-platform compatibility
  const styles = {
    container: {
      minHeight: '100vh',
      display: 'flex',
      flexDirection: 'column',
      backgroundColor: '#f8fafc',
      fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif",
      boxSizing: 'border-box'
    },

    // Header Section
    header: {
      padding: 'clamp(16px, 4vw, 24px) clamp(16px, 4vw, 24px) 12px',
      backgroundColor: '#fff',
      borderBottom: '1px solid #e2e8f0'
    },
    backBtn: {
      display: 'inline-flex',
      alignItems: 'center',
      color: '#64748b',
      fontSize: 'clamp(13px, 2.5vw, 14px)',
      cursor: 'pointer',
      marginBottom: '12px',
      padding: '8px 0',
      transition: 'color 0.2s'
    },
    title: {
      fontSize: 'clamp(20px, 5vw, 28px)',
      fontWeight: '800',
      color: '#1e293b',
      margin: 0,
      letterSpacing: '-0.5px'
    },
    subtitle: {
      fontSize: 'clamp(12px, 2.5vw, 14px)',
      color: '#94a3b8',
      marginTop: '4px'
    },

    // Toggle (Student/Teacher)
    toggleContainer: {
      display: 'flex',
      margin: 'clamp(12px, 3vw, 20px) clamp(16px, 4vw, 24px)',
      backgroundColor: '#f1f5f9',
      borderRadius: '12px',
      padding: '4px',
      gap: '4px'
    },
    toggleBtn: (active) => ({
      flex: 1,
      border: 'none',
      padding: 'clamp(10px, 2vw, 12px)',
      borderRadius: '10px',
      fontSize: 'clamp(12px, 2.5vw, 14px)',
      fontWeight: '600',
      cursor: 'pointer',
      backgroundColor: active ? '#fff' : 'transparent',
      color: active ? '#5c5cfc' : '#94a3b8',
      boxShadow: active ? '0 2px 8px rgba(0,0,0,0.08)' : 'none',
      transition: 'all 0.2s ease',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '6px'
    }),

    // Form Area
    form: {
      padding: '0 clamp(16px, 4vw, 24px) clamp(16px, 4vw, 24px)',
      display: 'flex',
      flexDirection: 'column',
      gap: 'clamp(12px, 3vw, 16px)',
      flex: 1,
      maxWidth: '600px',
      width: '100%',
      margin: '0 auto',
      boxSizing: 'border-box'
    },
    row: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(140px, 1fr))',
      gap: 'clamp(10px, 2vw, 16px)'
    },
    col: {
      display: 'flex',
      flexDirection: 'column'
    },
    inputGroup: {
      position: 'relative'
    },
    inputIcon: {
      position: 'absolute',
      left: '12px',
      top: '50%',
      transform: 'translateY(-50%)',
      color: '#94a3b8',
      pointerEvents: 'none'
    },
    input: (hasIcon, error) => ({
      width: '100%',
      padding: hasIcon ? '12px 12px 12px 40px' : '12px 16px',
      borderRadius: '10px',
      border: `1px solid ${error ? '#ef4444' : '#e2e8f0'}`,
      fontSize: 'clamp(13px, 2.5vw, 14px)',
      outline: 'none',
      backgroundColor: '#fff',
      boxSizing: 'border-box',
      transition: 'all 0.2s',
      color: '#1e293b'
    }),
    label: {
      fontSize: 'clamp(11px, 2vw, 12px)',
      fontWeight: '600',
      color: '#475569',
      marginBottom: '6px',
      marginLeft: '2px',
      display: 'flex',
      alignItems: 'center',
      gap: '4px'
    },

    // Password visibility toggle
    passToggle: {
      position: 'absolute',
      right: '12px',
      top: '50%',
      transform: 'translateY(-50%)',
      background: 'none',
      border: 'none',
      cursor: 'pointer',
      color: '#94a3b8',
      padding: '4px'
    },

    // Error message
    error: {
      fontSize: '11px',
      color: '#ef4444',
      marginTop: '4px',
      marginLeft: '2px'
    },

    // Checkbox Area
    checkboxRow: {
      display: 'flex',
      alignItems: 'flex-start',
      gap: '10px',
      fontSize: 'clamp(12px, 2.5vw, 13px)',
      color: '#64748b',
      marginTop: '4px'
    },
    checkbox: {
      width: '18px',
      height: '18px',
      marginTop: '1px',
      cursor: 'pointer',
      accentColor: '#5c5cfc'
    },
    linkHighlight: {
      color: '#5c5cfc',
      fontWeight: '600',
      textDecoration: 'none'
    },

    // Bottom Action Area
    footer: {
      padding: 'clamp(16px, 4vw, 24px)',
      backgroundColor: '#fff',
      borderTop: '1px solid #e2e8f0',
      marginTop: 'auto'
    },
    submitBtn: {
      width: '100%',
      padding: 'clamp(14px, 3vw, 16px)',
      borderRadius: '12px',
      border: 'none',
      backgroundColor: '#5c5cfc',
      color: '#fff',
      fontSize: 'clamp(14px, 3vw, 16px)',
      fontWeight: '700',
      cursor: 'pointer',
      boxShadow: '0 4px 14px rgba(92, 92, 252, 0.35)',
      transition: 'all 0.2s ease',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '8px'
    },
    loginLink: {
      textAlign: 'center',
      fontSize: 'clamp(12px, 2.5vw, 14px)',
      color: '#94a3b8',
      marginTop: '16px'
    },

    // Department select styling
    select: {
      width: '100%',
      padding: '12px 16px',
      borderRadius: '10px',
      border: '1px solid #e2e8f0',
      fontSize: 'clamp(13px, 2.5vw, 14px)',
      outline: 'none',
      backgroundColor: '#fff',
      boxSizing: 'border-box',
      color: '#1e293b',
      cursor: 'pointer'
    },

    // Responsive grid for form sections
    section: {
      display: 'grid',
      gap: 'clamp(12px, 3vw, 16px)'
    },

    // Card style for form sections
    card: {
      backgroundColor: '#fff',
      borderRadius: '12px',
      padding: 'clamp(14px, 3vw, 20px)',
      border: '1px solid #e2e8f0',
      boxShadow: '0 1px 3px rgba(0,0,0,0.02)'
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.fullName.trim()) {
      newErrors.fullName = 'Full name is required';
    }

    if (!formData.rollNo.trim()) {
      newErrors.rollNo = 'Roll number is required';
    }

    if (!formData.department || formData.department === 'Select') {
      newErrors.department = 'Department is required';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = 'Please enter a valid email';
    }

    if (!formData.password) {
      newErrors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      newErrors.password = 'Password must be at least 6 characters';
    }

    if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match';
    }

    if (!formData.agree) {
      newErrors.agree = 'Please agree to the terms';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      // Simulating login
      onLogin({ 
        name: formData.fullName || 'New Student', 
        email: formData.email, 
        role: 'student',
        college: 'Engineering Tech Institute' 
      });
      navigate('/');
    }
  };

  return (
    <div style={styles.container}>
      {/* Header */}
      <div style={styles.header}>
        <div style={styles.backBtn} onClick={() => navigate('/')}>
          <ArrowLeft size={18} style={{marginRight: 6}}/> 
          <span>Back</span>
        </div>
        <h1 style={styles.title}>Create Account</h1>
        <p style={styles.subtitle}>Join EventSphere as a student</p>
      </div>

      {/* Toggle */}
      <div style={styles.toggleContainer}>
        <button style={styles.toggleBtn(true)}>
          <GraduationCap size={16} />
          <span>Student</span>
        </button>
        <button style={styles.toggleBtn(false)} onClick={() => navigate('/teacher-signup')}>
          <Building2 size={16} />
          <span>Teacher</span>
        </button>
      </div>

      {/* Form */}
      <form style={styles.form} onSubmit={handleSubmit}>
        {/* Personal Info Card */}
        <div style={styles.card}>
          <div style={styles.section}>
            {/* Full Name */}
            <div style={styles.col}>
              <label style={styles.label}>
                <User size={12} /> Full Name
              </label>
              <div style={styles.inputGroup}>
                <input 
                  style={styles.input(true, !!errors.fullName)} 
                  placeholder="Enter your full name" 
                  name="fullName" 
                  value={formData.fullName}
                  onChange={handleChange}
                />
              </div>
              {errors.fullName && <span style={styles.error}>{errors.fullName}</span>}
            </div>

            {/* Row: Roll No + Dept */}
            <div style={styles.row}>
              <div style={styles.col}>
                <label style={styles.label}>Roll Number</label>
                <input 
                  style={styles.input(true, !!errors.rollNo)} 
                  placeholder="e.g. ID-2024" 
                  name="rollNo" 
                  value={formData.rollNo}
                  onChange={handleChange}
                />
                {errors.rollNo && <span style={styles.error}>{errors.rollNo}</span>}
              </div>
              <div style={styles.col}>
                <label style={styles.label}>Department</label>
                <select 
                  style={styles.select} 
                  name="department" 
                  value={formData.department}
                  onChange={handleChange}
                >
                  <option value="Select">Select</option>
                  <option value="CSE">CSE</option>
                  <option value="ECE">ECE</option>
                  <option value="MECH">MECH</option>
                  <option value="CIVIL">CIVIL</option>
                  <option value="IT">IT</option>
                  <option value="EEE">EEE</option>
                </select>
                {errors.department && <span style={styles.error}>{errors.department}</span>}
              </div>
            </div>

            {/* Email */}
            <div style={styles.col}>
              <label style={styles.label}>
                <Mail size={12} /> Email Address
              </label>
              <div style={styles.inputGroup}>
                <input 
                  style={styles.input(true, !!errors.email)} 
                  type="email" 
                  placeholder="student@college.edu" 
                  name="email" 
                  value={formData.email}
                  onChange={handleChange}
                />
              </div>
              {errors.email && <span style={styles.error}>{errors.email}</span>}
            </div>
          </div>
        </div>

        {/* Password Card */}
        <div style={styles.card}>
          <div style={styles.section}>
            {/* Password */}
            <div style={styles.col}>
              <label style={styles.label}>
                <Lock size={12} /> Password
              </label>
              <div style={styles.inputGroup}>
                <input 
                  style={styles.input(true, !!errors.password)} 
                  type={showPass ? "text" : "password"} 
                  placeholder="Create a password" 
                  name="password" 
                  value={formData.password}
                  onChange={handleChange}
                />
                <button 
                  type="button" 
                  style={styles.passToggle}
                  onClick={() => setShowPass(!showPass)}
                >
                  {showPass ? <EyeOff size={18} /> : <Eye size={18} />}
                </button>
              </div>
              {errors.password && <span style={styles.error}>{errors.password}</span>}
            </div>

            {/* Confirm Password */}
            <div style={styles.col}>
              <label style={styles.label}>
                <Lock size={12} /> Confirm Password
              </label>
              <div style={styles.inputGroup}>
                <input 
                  style={styles.input(true, !!errors.confirmPassword)} 
                  type={showPass ? "text" : "password"} 
                  placeholder="Confirm your password" 
                  name="confirmPassword" 
                  value={formData.confirmPassword}
                  onChange={handleChange}
                />
              </div>
              {errors.confirmPassword && <span style={styles.error}>{errors.confirmPassword}</span>}
            </div>
          </div>
        </div>

        {/* Terms Checkbox */}
        <div style={styles.checkboxRow}>
          <input 
            type="checkbox" 
            style={styles.checkbox}
            checked={formData.agree}
            onChange={handleChange}
            name="agree"
            id="agree"
          />
          <label htmlFor="agree" style={{cursor: 'pointer', flex: 1}}>
            I agree to the <Link to="/terms" style={styles.linkHighlight}>Terms & Conditions</Link> and <Link to="/privacy" style={styles.linkHighlight}>Privacy Policy</Link>
          </label>
        </div>
        {errors.agree && <span style={styles.error}>{errors.agree}</span>}

        {/* Footer Actions */}
        <div style={styles.footer}>
          <button type="submit" style={styles.submitBtn}>
            Create Account
            <ArrowLeft size={18} style={{transform: 'rotate(180deg)'}} />
          </button>

          <div style={styles.loginLink}>
            Already have an account? <Link to="/signin" style={styles.linkHighlight}>Login</Link>
          </div>
        </div>
      </form>
    </div>
  );
};

export default StudentSignUp;
