import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { ArrowLeft } from 'lucide-react';

const StudentSignUp = ({ onLogin }) => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    fullName: '',
    rollNo: '',
    department: '',
    email: '',
    password: '',
    confirmPassword: ''
  });

  const [isStudent, setIsStudent] = useState(true);

  // --- CLEAN WHITE COMPACT STYLES ---
  const styles = {
    // The main container fixing height to viewport so no scroll
    container: {
      height: '100vh', 
      maxHeight: '-webkit-fill-available', // Fix for mobile safari
      display: 'flex',
      flexDirection: 'column',
      backgroundColor: '#ffffff', // Pure White Background
      fontFamily: "'Inter', sans-serif",
      overflow: 'hidden' // Prevent scrolling
    },

    // Header Section
    header: {
      padding: '20px 24px 10px 24px',
      flexShrink: 0 // Don't shrink
    },
    backBtn: {
      display: 'flex', alignItems: 'center', color: '#64748b',
      fontSize: '14px', cursor: 'pointer', marginBottom: '15px', fontWeight: '600'
    },
    title: { fontSize: '26px', fontWeight: '800', color: '#1e293b', margin: '0 0 5px 0' },
    subtitle: { fontSize: '14px', color: '#94a3b8' },

    // The Toggle Switch (Clean Grey/White)
    toggleContainer: {
      margin: '10px 24px 20px 24px',
      backgroundColor: '#f1f5f9', // Light grey track
      borderRadius: '12px', padding: '4px', display: 'flex', flexShrink: 0
    },
    toggleBtn: (active) => ({
      flex: 1, padding: '10px', borderRadius: '10px', border: 'none',
      fontSize: '13px', fontWeight: '700', cursor: 'pointer',
      backgroundColor: active ? '#ffffff' : 'transparent', // White active tab
      color: active ? '#2563eb' : '#94a3b8', // Blue active text
      boxShadow: active ? '0 2px 5px rgba(0,0,0,0.05)' : 'none',
      transition: 'all 0.2s ease'
    }),

    // Form Content Area (Fills remaining space)
    formContent: {
      flex: 1,
      padding: '0 24px 20px 24px',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'space-between' // Pushes footer to bottom
    },

    // Input Fields Layout
    inputsWrapper: {
      display: 'flex', flexDirection: 'column', gap: '14px'
    },
    row: { display: 'flex', gap: '14px' },
    col: { flex: 1, display: 'flex', flexDirection: 'column' },

    // Clean White Inputs
    label: { fontSize: '12px', fontWeight: '600', color: '#475569', marginBottom: '6px', marginLeft: '4px' },
    input: {
      width: '100%', padding: '14px', borderRadius: '12px',
      border: '1px solid #e2e8f0', // Subtle grey border
      backgroundColor: '#ffffff', // White input background
      fontSize: '14px', color: '#1e293b', outline: 'none', boxSizing: 'border-box',
      transition: 'border-color 0.2s'
    },

    // Footer Section (Button & Link)
    footer: { marginTop: 'auto', paddingTop: '15px' },
    submitBtn: {
      width: '100%', padding: '16px', borderRadius: '12px', border: 'none',
      backgroundColor: '#2563eb', color: '#fff', // Standard Blue
      fontSize: '16px', fontWeight: 'bold', cursor: 'pointer',
      boxShadow: '0 4px 12px rgba(37, 99, 235, 0.2)'
    },
    loginLink: { textAlign: 'center', fontSize: '13px', color: '#64748b', marginTop: '15px', fontWeight: '500' },
    linkHighlight: { color: '#2563eb', fontWeight: '700', textDecoration: 'none', marginLeft: '5px' }
  };

  const handleChange = (e) => setFormData({...formData, [e.target.name]: e.target.value});

  const handleSubmit = () => {
    onLogin({ 
      name: formData.fullName || 'New User', 
      email: formData.email, 
      role: isStudent ? 'student' : 'teacher',
      college: 'Engineering Tech Institute' 
    });
    navigate('/');
  };

  return (
    <div style={styles.container}>
      {/* Header */}
      <div style={styles.header}>
        <div style={styles.backBtn} onClick={() => navigate('/')}>
          <ArrowLeft size={18} style={{marginRight: 8}}/> Back to Home
        </div>
        <h1 style={styles.title}>Create Account</h1>
        <p style={styles.subtitle}>{isStudent ? 'Student' : 'Teacher'} Registration</p>
      </div>

      {/* Toggle Switch */}
      <div style={styles.toggleContainer}>
        <button style={styles.toggleBtn(isStudent)} onClick={() => setIsStudent(true)}>Student</button>
        <button style={styles.toggleBtn(!isStudent)} onClick={() => setIsStudent(false)}>Teacher</button>
      </div>

      {/* Form Content */}
      <div style={styles.formContent}>
        <div style={styles.inputsWrapper}>
          {/* Full Name */}
          <div style={styles.col}>
            <label style={styles.label}>Full Name</label>
            <input style={styles.input} placeholder="e.g. John Doe" name="fullName" onChange={handleChange} />
          </div>

          {/* Row: ID & Dept (Compact) */}
          <div style={styles.row}>
            <div style={styles.col}>
              <label style={styles.label}>{isStudent ? 'Roll Number' : 'Employee ID'}</label>
              <input style={styles.input} placeholder={isStudent ? "ID-2024" : "EMP-123"} name="rollNo" onChange={handleChange} />
            </div>
            <div style={styles.col}>
              <label style={styles.label}>Department</label>
              <select style={{...styles.input, appearance: 'none', backgroundImage: `url("data:image/svg+xml;charset=US-ASCII,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20width%3D%22292.4%22%20height%3D%22292.4%22%3E%3Cpath%20fill%3D%22%23CBD5E1%22%20d%3D%22M287%2069.4a17.6%2017.6%200%200%200-13-5.4H18.4c-5%200-9.3%201.8-12.9%205.4A17.6%2017.6%200%200%200%200%2082.2c0%205%201.8%209.3%205.4%2012.9l128%20127.9c3.6%203.6%207.8%205.4%2012.8%205.4s9.2-1.8%2012.8-5.4L287%2095c3.5-3.5%205.4-7.8%205.4-12.8%200-5-1.9-9.2-5.5-12.8z%22%2F%3E%3C%2Fsvg%3E")`, backgroundRepeat: 'no-repeat', backgroundPosition: 'right 12px center', backgroundSize: '10px'}} name="department" onChange={handleChange}>
                <option>Select Dept</option>
                <option>CSE</option>
                <option>ECE</option>
                <option>MECH</option>
                <option>CIVIL</option>
              </select>
            </div>
          </div>

          {/* Email */}
          <div style={styles.col}>
            <label style={styles.label}>Email Address</label>
            <input style={styles.input} type="email" placeholder="you@college.edu" name="email" onChange={handleChange} />
          </div>

          {/* Row: Passwords (Compact) */}
          <div style={styles.row}>
            <div style={styles.col}>
              <label style={styles.label}>Password</label>
              <input style={styles.input} type="password" placeholder="••••••" name="password" onChange={handleChange} />
            </div>
            <div style={styles.col}>
              <label style={styles.label}>Confirm</label>
              <input style={styles.input} type="password" placeholder="••••••" name="confirmPassword" onChange={handleChange} />
            </div>
          </div>
        </div>

        {/* Footer Actions */}
        <div style={styles.footer}>
          <button style={styles.submitBtn} onClick={handleSubmit}>Create Account</button>
          <div style={styles.loginLink}>
            Already have an account? 
            <Link to="/signin" style={styles.linkHighlight}>Log in</Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default StudentSignUp;
