import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { Home, Calendar, Ticket, User } from 'lucide-react';

const BottomNav = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const isActive = (path) => location.pathname === path;

  const navItems = [
    { icon: <Home size={22} />, label: 'Home', path: '/' },
    { icon: <Calendar size={22} />, label: 'Events', path: '/events' },
    { icon: <Ticket size={22} />, label: 'My Events', path: '/my-events' },
    { icon: <User size={22} />, label: 'Profile', path: '/profile' }
  ];

  return (
    <nav style={styles.bottomNav}>
      {navItems.map((item) => (
        <div 
          key={item.path}
          style={{
            ...styles.navItem,
            color: isActive(item.path) ? '#2563eb' : '#94a3b8', 
            fontWeight: isActive(item.path) ? 'bold' : 'normal'
          }} 
          onClick={() => navigate(item.path)}
        >
          {item.icon}
          <span style={{ fontSize: '11px', marginTop: '4px' }}>{item.label}</span>
        </div>
      ))}
    </nav>
  );
};

const styles = {
  bottomNav: { 
    position: 'fixed', bottom: 0, left: '50%', transform: 'translateX(-50%)', 
    width: '100%', maxWidth: '375px', backgroundColor: '#ffffff', 
    display: 'flex', justifyContent: 'space-around', padding: '12px 0', 
    borderTop: '1px solid #f1f5f9', zIndex: 1000 
  },
  navItem: { display: 'flex', flexDirection: 'column', alignItems: 'center', cursor: 'pointer' }
};

export default BottomNav;
