import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Search, Clock, CheckCircle } from 'lucide-react';

const Events = ({ theme, allEvents, searchTerm, setSearchTerm, onRegister }) => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState("All");
  const isDark = theme === 'dark';

  const filtered = allEvents.filter(e => 
    (activeTab === "All" || e.category === activeTab) &&
    e.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const styles = {
    container: { 
      padding: '20px', 
      backgroundColor: isDark ? '#0f172a' : '#fff', 
      minHeight: '100vh', 
      paddingBottom: '100px', 
      boxSizing: 'border-box',
      maxWidth: '430px', 
      margin: '0 auto',
      overflowX: 'hidden'
    },
    headerTitle: { margin: 0, color: isDark ? '#fff' : '#1e293b' },
    searchContainer: { display: 'flex', alignItems: 'center', backgroundColor: isDark ? '#1e293b' : '#f1f5f9', padding: '12px', borderRadius: '12px', marginBottom: '20px' },
    input: { border: 'none', background: 'transparent', marginLeft: '10px', flex: 1, color: isDark ? '#fff' : '#000', outline: 'none', fontSize: '16px' },
    
    // Filter Tabs
    tabsContainer: { 
      display: 'flex', 
      gap: '10px', 
      overflowX: 'auto', 
      marginBottom: '20px', 
      paddingBottom: '5px',
      scrollbarWidth: 'none'
    },
    tabBtn: (isActive) => ({
      padding: '8px 20px', 
      borderRadius: '20px', 
      border: 'none', 
      whiteSpace: 'nowrap',
      backgroundColor: isActive ? '#5c5cfc' : (isDark ? '#1e293b' : '#f1f5f9'),
      color: isActive ? '#fff' : '#94a3b8', 
      fontWeight: 'bold', 
      cursor: 'pointer',
      flexShrink: 0
    }),

    eventCard: { 
      backgroundColor: isDark ? '#1e293b' : '#fff', 
      borderRadius: '20px', 
      marginBottom: '20px', 
      overflow: 'hidden', 
      boxShadow: '0 4px 6px rgba(0,0,0,0.05)',
      border: isDark ? '1px solid #334155' : '1px solid #f1f5f9',
      cursor: 'pointer'
    },
    cardImg: { width: '100%', height: '160px', objectFit: 'cover' },
    cardContent: { padding: '15px' },
    cardTitle: { margin: '0 0 10px 0', color: isDark ? '#fff' : '#1e293b' },
    cardFooter: { display: 'flex', justifyContent: 'space-between', alignItems: 'center' },
    dateText: { fontSize: '12px', color: '#94a3b8', display: 'flex', alignItems: 'center', gap: '5px' },
    
    // Dynamic Button Styles
    actionBtn: (status) => ({ 
      backgroundColor: status === 'approved' ? '#22c55e' : (status === 'pending' ? '#f59e0b' : '#5c5cfc'), 
      color: '#fff', 
      border: 'none', 
      padding: '8px 16px', 
      borderRadius: '8px', 
      fontWeight: '600', 
      cursor: status === 'pending' ? 'default' : 'pointer',
      fontSize: '13px',
      minWidth: '80px'
    })
  };

  const handleButtonClick = (e, event) => {
    e.stopPropagation(); // Prevent opening details page when clicking button
    
    if (event.status === 'approved') {
      // Go to Ticket
      navigate(`/ticket-confirmation/${event.id}`);
    } else if (event.status === 'pending') {
      // Do nothing, just wait
    } else {
      // Register
      onRegister(event.id);
      navigate('/my-events'); // Go to status page
    }
  };

  return (
    <div style={styles.container}>
      <div style={{ display: 'flex', alignItems: 'center', gap: '15px', marginBottom: '20px' }}>
         <h2 style={styles.headerTitle}>Events</h2>
      </div>
      
      {/* Search Bar */}
      <div style={styles.searchContainer}>
        <Search size={18} color="#94a3b8" />
        <input 
          value={searchTerm} 
          onChange={(e) => setSearchTerm(e.target.value)} 
          placeholder="Search events..." 
          style={styles.input}
        />
      </div>

      {/* Filter Tabs */}
      <div style={styles.tabsContainer} className="hide-scrollbar">
        {["All", "Tech", "Cultural", "Sports", "Workshops"].map(tab => (
          <button 
            key={tab} 
            onClick={() => setActiveTab(tab)} 
            style={styles.tabBtn(activeTab === tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Event Cards */}
      {filtered.length > 0 ? (
        filtered.map(event => (
          <div key={event.id} onClick={() => navigate(`/event-details/${event.id}`)} style={styles.eventCard}>
            <img src={event.image} alt="" style={styles.cardImg} />
            <div style={styles.cardContent}>
              <h3 style={styles.cardTitle}>{event.title}</h3>
              <div style={styles.cardFooter}>
                <div style={styles.dateText}>
                  <Clock size={14}/> {event.date}
                </div>
                
                {/* Updated Button Logic */}
                <button 
                  style={styles.actionBtn(event.status)}
                  onClick={(e) => handleButtonClick(e, event)}
                  disabled={event.status === 'pending'}
                >
                  {event.status === 'approved' ? "View Ticket" : (event.status === 'pending' ? "Pending..." : "Register")}
                </button>
              </div>
            </div>
          </div>
        ))
      ) : (
        <div style={{ textAlign: 'center', color: '#94a3b8', marginTop: '50px' }}>
          No events found in this category.
        </div>
      )}
    </div>
  );
};

export default Events;
