import React from 'react';
import { Bell, Clock, CheckCircle } from 'lucide-react'; 
import { useNavigate } from 'react-router-dom'; 

const Dashboard = ({ user, events, theme, regCount, unreadCount, onReadNotifications }) => {
  const navigate = useNavigate(); 
  const isDark = theme === 'dark';

  const styles = {
    container: { padding: '20px', minHeight: '100vh', backgroundColor: isDark ? '#0f172a' : '#fff', boxSizing: 'border-box', maxWidth: '430px', margin: '0 auto' },
    header: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' },
    logo: { width: '40px', height: '40px', borderRadius: '50%', backgroundColor: '#2563eb', color: '#fff', display: 'flex', justifyContent: 'center', alignItems: 'center', fontWeight: 'bold' },
    logoText: { fontWeight: 'bold', fontSize: '18px', color: isDark ? '#fff' : '#1e293b' },
    greeting: { fontSize: '28px', fontWeight: 'bold', margin: '10px 0 5px 0', color: isDark ? '#fff' : '#1e293b' },
    subText: { color: '#64748b', fontSize: '14px', marginBottom: '24px' },
    statsRow: { display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '15px', marginBottom: '30px' },
    statCard: { backgroundColor: isDark ? '#1e293b' : '#f8fafc', padding: '16px', borderRadius: '20px', color: isDark ? '#fff' : '#1e293b', border: isDark ? '1px solid #334155' : '1px solid #f1f5f9', display: 'flex', flexDirection: 'column', gap: '5px' },
    sectionHeader: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '15px' },
    seeAllBtn: { background: 'none', border: 'none', color: '#2563eb', fontSize: '14px', fontWeight: 'bold', cursor: 'pointer' },
    eventCard: { backgroundColor: isDark ? '#1e293b' : '#fff', borderRadius: '20px', padding: '16px', marginBottom: '16px', border: isDark ? '1px solid #334155' : '1px solid #f1f5f9', boxShadow: '0 4px 6px rgba(0,0,0,0.02)', cursor: 'pointer' },
    regBtn: { width: '100%', backgroundColor: '#2563eb', color: '#fff', border: 'none', padding: '12px', borderRadius: '12px', fontWeight: 'bold', marginTop: '12px', fontSize: '14px', cursor: 'pointer' }
  };

  const handleBellClick = () => {
    onReadNotifications();
    navigate('/notifications');
  };

  // --- NEW LOGIC: DIRECT NAVIGATION ---
  const handleCardClick = (event) => {
    if (event.status === 'approved') {
      // 1. Direct Ticket (Skipping Details)
      navigate(`/ticket-confirmation/${event.id}`);
    } else if (event.status === 'pending') {
      // 2. Go to Status Page
      navigate('/my-events');
    } else {
      // 3. Go to Details to Register
      navigate(`/event-details/${event.id}`);
    }
  };

  return (
    <div style={styles.container}>
      {/* Top Header */}
      <header style={styles.header}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
          <div style={styles.logo}>ES</div>
          <span style={styles.logoText}>EventSphere</span>
        </div>
        
        <div style={{ position: 'relative', cursor: 'pointer' }} onClick={handleBellClick}>
          <Bell size={24} color={isDark ? '#94a3b8' : '#1e293b'} />
          {unreadCount > 0 && (
            <span style={{ position: 'absolute', top: -2, right: 0, width: '10px', height: '10px', backgroundColor: '#ef4444', borderRadius: '50%', border: `2px solid ${isDark ? '#0f172a' : '#fff'}` }}></span>
          )}
        </div>
      </header>

      <h1 style={styles.greeting}>Hi {user.name.split(' ')[0]} ðŸ‘‹</h1>
      <p style={styles.subText}>Explore college events</p>

      <div style={styles.statsRow}>
        <div style={styles.statCard}>
          <div style={{ fontSize: '13px', color: '#64748b' }}>Upcoming</div>
          <div style={{ fontSize: '24px', fontWeight: 'bold' }}>{events.length}</div>
        </div>
        <div style={styles.statCard}>
          <div style={{ fontSize: '13px', color: '#64748b' }}>Registered</div>
          <div style={{ fontSize: '24px', fontWeight: 'bold' }}>{regCount}</div>
        </div>
      </div>

      <div style={styles.sectionHeader}>
        <h3 style={{ fontSize: '18px', margin: 0, color: isDark ? '#fff' : '#1e293b' }}>Upcoming Events</h3>
        <button style={styles.seeAllBtn} onClick={() => navigate('/events')}>See All</button>
      </div>
      
      {events.slice(0, 2).map(event => (
        <div 
          key={event.id} 
          style={styles.eventCard} 
          // USE THE NEW HANDLER HERE
          onClick={() => handleCardClick(event)}
        >
          <img src={event.image} alt={event.title} style={{ width: '100%', height: '160px', objectFit: 'cover', borderRadius: '14px', marginBottom: '12px' }} />
          <h4 style={{ margin: '0 0 8px 0', fontSize: '18px', color: isDark ? '#fff' : '#1e293b' }}>{event.title}</h4>
          <div style={{ display: 'flex', alignItems: 'center', gap: '8px', fontSize: '14px', color: '#64748b' }}>
            <Clock size={16} /> {event.date}
          </div>
          
          {/* Button Text changes based on Status */}
          <button 
            style={{
              ...styles.regBtn,
              backgroundColor: event.status === 'approved' ? '#22c55e' : (event.status === 'pending' ? '#f59e0b' : '#2563eb'),
              cursor: event.status === 'pending' ? 'default' : 'pointer'
            }}
          >
            {event.status === 'approved' ? "View Ticket" : (event.status === 'pending' ? "Processing..." : "Register Now")}
          </button>
        </div>
      ))}
    </div>
  );
};

export default Dashboard;
