import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowLeft, Sun, Moon, Monitor } from 'lucide-react';

const ThemeSelection = ({ currentTheme, setTheme }) => {
  const navigate = useNavigate();
  const [tempTheme, setTempTheme] = useState(currentTheme);
  const [showToast, setShowToast] = useState(false);

  const handleApply = () => {
    setTheme(tempTheme); // Updates the global theme in App.js
    setShowToast(true);
    setTimeout(() => setShowToast(false), 2000);
  };

  // Theme options matching your design
  const themes = [
    { id: 'light', title: 'Light Mode', desc: 'Bright background with dark text', icon: <Sun size={20} /> },
    { id: 'dark', title: 'Dark Mode', desc: 'Dark background for low light', icon: <Moon size={20} /> },
  ];

  return (
    <div style={{ padding: '16px', color: tempTheme === 'dark' ? '#fff' : '#000' }}>
      <header style={{ display: 'flex', alignItems: 'center', marginBottom: '24px' }}>
        <ArrowLeft size={24} onClick={() => navigate(-1)} style={{ cursor: 'pointer' }} />
        <h2 style={{ marginLeft: '16px', fontSize: '18px' }}>App Theme</h2>
      </header>

      <div style={{ backgroundColor: tempTheme === 'dark' ? '#1e293b' : '#fff', borderRadius: '16px', border: '1px solid #e2e8f0' }}>
        {themes.map((t) => (
          <div key={t.id} onClick={() => setTempTheme(t.id)} style={{ display: 'flex', alignItems: 'center', padding: '16px', borderBottom: '1px solid #f1f5f9', cursor: 'pointer' }}>
            <div style={{ marginRight: '16px', color: '#6366f1' }}>{t.icon}</div>
            <div style={{ flex: 1 }}>
              <div style={{ fontWeight: 'bold', fontSize: '14px' }}>{t.title}</div>
              <div style={{ fontSize: '12px', color: '#94a3b8' }}>{t.desc}</div>
            </div>
            <div style={{ width: '20px', height: '20px', borderRadius: '50%', border: '2px solid #6366f1', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
              {tempTheme === t.id && <div style={{ width: '10px', height: '10px', backgroundColor: '#6366f1', borderRadius: '50%' }} />}
            </div>
          </div>
        ))}
      </div>

      <button onClick={handleApply} style={{ width: '100%', backgroundColor: '#6366f1', color: '#fff', border: 'none', padding: '16px', borderRadius: '12px', fontWeight: 'bold', marginTop: '40px', cursor: 'pointer' }}>
        Apply Theme
      </button>

      {showToast && (
        <div style={{ position: 'fixed', bottom: '100px', left: '50%', transform: 'translateX(-50%)', backgroundColor: '#dcfce7', color: '#166534', padding: '8px 24px', borderRadius: '20px', fontSize: '14px' }}>
          âœ“ Theme updated
        </div>
      )}
    </div>
  );
};

export default ThemeSelection;
