import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

// Page Imports
import Dashboard from './pages/Dashboard';
import Events from './pages/Events';
import MyEvents from './pages/MyEvents';
import Profile from './pages/Profile';
import BottomNav from './components/BottomNav';

function App() {
  const [theme, setTheme] = useState('light'); 
  const [searchTerm, setSearchTerm] = useState("");
  
  const [user] = useState({
    name: "Priya Sharma",
    email: "priya.sharma@university.edu"
  });

  const [allEvents] = useState([
    { 
      id: 1, 
      title: "Campus Tech Summit 2024", 
      date: "Mar 18, 2024 路 10:00 AM", 
      location: "Main Auditorium", 
      image: "https://images.unsplash.com/photo-1540575467063-178a50c2df87?w=800" 
    },
    { 
      id: 2, 
      title: "Annual Cultural Night", 
      date: "Mar 22, 2024 路 6:30 PM", 
      location: "Open Air Theatre", 
      image: "https://images.unsplash.com/photo-1514525253361-bee871870402?w=800" 
    }
  ]);

  // Logic for the Search Button
  const filteredEvents = allEvents.filter(event => 
    event.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const appStyles = {
    maxWidth: '390px', // Standard Mobile Size
    margin: '0 auto',
    backgroundColor: theme === 'dark' ? '#0f172a' : '#fff',
    minHeight: '100vh',
    position: 'relative',
    boxShadow: '0 0 20px rgba(0,0,0,0.1)', // Makes it look like a phone
    overflowX: 'hidden'
  };

  return (
    <Router>
      <div style={appStyles}>
        <Routes>
          <Route path="/" element={
            <Dashboard 
              user={user} 
              events={filteredEvents} 
              theme={theme} 
              setSearchTerm={setSearchTerm}
            />
          } />
          <Route path="/events" element={<Events allEvents={allEvents} theme={theme} />} />
          <Route path="/my-events" element={<MyEvents theme={theme} />} />
          <Route path="/profile" element={<Profile user={user} theme={theme} />} />
        </Routes>
        <BottomNav theme={theme} />
      </div>
    </Router>
  );
}

export default App;
