import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Auth.css';

const StudentSignIn = ({ onLogin }) => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // 1. EXTRACT NAME FROM EMAIL (Simulating a real backend)
    // If email is "priya@gmail.com", name becomes "Priya"
    const extractedName = formData.email.split('@')[0];
    const capitalizedName = extractedName.charAt(0).toUpperCase() + extractedName.slice(1);

    // 2. Prepare User Data
    const userData = {
      name: capitalizedName, 
      email: formData.email,
      // We keep existing College/RegNo or you can add inputs for them here
    };
    
    // 3. SEND DATA TO APP
    onLogin(userData); 
    
    // 4. Redirect
    navigate('/'); 
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="auth-header">
          <h2>Welcome Back</h2>
          <p>Sign in to your Student Portal</p>
        </div>

        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-group">
            <label>Email Address</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Enter your email"
              required
            />
          </div>

          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Enter your password"
              required
            />
          </div>

          <div style={{ textAlign: 'right' }}>
            <Link to="#" style={{ fontSize: '13px', color: '#2563eb', textDecoration: 'none' }}>
              Forgot Password?
            </Link>
          </div>

          <button type="submit" className="submit-btn">
            Sign In
          </button>
        </form>

        <div className="auth-footer">
          <p>
            Don't have an account? 
            <Link to="/signup" className="auth-link">Create Account</Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default StudentSignIn;
