import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { ArrowLeft, CheckCircle } from 'lucide-react';

const TicketConfirmation = ({ allEvents, theme }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const isDark = theme === 'dark';

  // Find the event details
  const event = allEvents.find(e => e.id === parseInt(id));

  if (!event) return null;

  const styles = {
    container: {
      backgroundColor: isDark ? '#0f172a' : '#f8fafc',
      minHeight: '100vh',
      maxWidth: '430px', // Mobile Width
      margin: '0 auto',
      padding: '20px',
      boxSizing: 'border-box',
      display: 'flex',
      flexDirection: 'column',
      fontFamily: 'sans-serif'
    },
    header: {
      display: 'flex',
      alignItems: 'center',
      gap: '15px',
      marginBottom: '30px'
    },
    title: {
      fontSize: '20px',
      fontWeight: 'bold',
      color: isDark ? '#fff' : '#1e293b',
      margin: 0
    },
    ticketCard: {
      backgroundColor: isDark ? '#1e293b' : '#fff',
      borderRadius: '24px',
      padding: '30px 20px',
      textAlign: 'center',
      boxShadow: '0 10px 30px rgba(0,0,0,0.05)',
      marginBottom: '30px',
      position: 'relative'
    },
    eventName: {
      fontSize: '22px',
      fontWeight: '800',
      color: isDark ? '#fff' : '#1e293b',
      marginBottom: '8px'
    },
    status: {
      color: '#94a3b8',
      fontSize: '14px',
      marginBottom: '25px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '6px'
    },
    divider: {
      borderTop: '2px dashed #e2e8f0',
      margin: '25px 0',
      position: 'relative'
    },
    // The little semi-circles on the side of the ticket
    notchLeft: {
      position: 'absolute',
      left: -30,
      top: -12,
      width: '24px',
      height: '24px',
      backgroundColor: isDark ? '#0f172a' : '#f8fafc',
      borderRadius: '50%'
    },
    notchRight: {
      position: 'absolute',
      right: -30,
      top: -12,
      width: '24px',
      height: '24px',
      backgroundColor: isDark ? '#0f172a' : '#f8fafc',
      borderRadius: '50%'
    },
    detailLabel: {
      fontSize: '11px',
      color: '#94a3b8',
      letterSpacing: '1px',
      textTransform: 'uppercase',
      marginBottom: '6px',
      fontWeight: 'bold'
    },
    detailValue: {
      fontSize: '16px',
      fontWeight: 'bold',
      color: isDark ? '#fff' : '#1e293b',
      marginBottom: '20px'
    },
    qrContainer: {
      marginTop: '10px',
      padding: '10px',
      backgroundColor: '#fff',
      display: 'inline-block',
      borderRadius: '12px'
    },
    qrImage: {
      width: '140px',
      height: '140px',
      objectFit: 'contain'
    },
    ticketId: {
      marginTop: '15px',
      fontSize: '13px',
      color: '#94a3b8',
      backgroundColor: isDark ? '#0f172a' : '#f1f5f9',
      padding: '8px 16px',
      borderRadius: '20px',
      display: 'inline-block',
      fontFamily: 'monospace'
    },
    primaryBtn: {
      width: '100%',
      backgroundColor: '#2563eb',
      color: '#fff',
      padding: '16px',
      borderRadius: '16px',
      border: 'none',
      fontSize: '16px',
      fontWeight: 'bold',
      marginBottom: '12px',
      cursor: 'pointer'
    },
    secondaryBtn: {
      width: '100%',
      backgroundColor: 'transparent',
      color: '#94a3b8',
      border: 'none',
      fontSize: '14px',
      fontWeight: '600',
      cursor: 'pointer'
    }
  };

  return (
    <div style={styles.container}>
      {/* Header */}
      <div style={styles.header}>
        <ArrowLeft size={24} onClick={() => navigate('/my-events')} style={{ cursor: 'pointer', color: isDark ? '#fff' : '#1e293b' }} />
        <h2 style={styles.title}>Your Ticket</h2>
      </div>

      {/* Ticket Card */}
      <div style={styles.ticketCard}>
        <h1 style={styles.eventName}>{event.title}</h1>
        <div style={styles.status}>Confirmed <CheckCircle size={14} color="#22c55e" /></div>

        <div style={styles.divider}>
          <div style={styles.notchLeft}></div>
          <div style={styles.notchRight}></div>
        </div>

        <div>
          <div style={styles.detailLabel}>Date & Time</div>
          <div style={styles.detailValue}>{event.date.replace('·', '|')}</div>
        </div>

        <div>
          <div style={styles.detailLabel}>Venue</div>
          <div style={styles.detailValue}>{event.location}</div>
        </div>

        <div style={styles.divider}>
          <div style={styles.notchLeft}></div>
          <div style={styles.notchRight}></div>
        </div>

        <div style={styles.qrContainer}>
          {/* Using a placeholder QR code API */}
          <img 
            src={`https://api.qrserver.com/v1/create-qr-code/?size=150x150&data=${event.title}`} 
            alt="QR Code" 
            style={styles.qrImage} 
          />
        </div>

        <div>
          <span style={styles.ticketId}>ID: #82991-T{event.id}</span>
        </div>
      </div>

      {/* Bottom Actions */}
      <div style={{ marginTop: 'auto' }}>
        <button style={styles.primaryBtn} onClick={() => navigate('/my-events')}>
          Back to My Events
        </button>
        <button style={styles.secondaryBtn} onClick={() => navigate('/')}>
          Back to Home
        </button>
      </div>
    </div>
  );
};

export default TicketConfirmation;
