import React from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowLeft, CheckCircle, Bell, MessageCircle, Calendar } from 'lucide-react';

const Notifications = () => {
  const navigate = useNavigate();

  const notices = [
    { id: 1, type: 'approval', title: 'Registration approved', desc: 'Your account has been verified. You can now access all features.', time: '2h ago', icon: <CheckCircle size={20} color="#fff" />, bg: '#5c5cfc' },
    { id: 2, type: 'event', title: 'New event added', desc: '"Summer Workshop" has been added to your calendar.', time: 'Yesterday', icon: <Calendar size={20} color="#fff" />, bg: '#818cf8' },
    { id: 3, type: 'update', title: 'Event updated', desc: 'The location for "Team Lunch" has been changed to Main Hall.', time: 'Yesterday', icon: <Bell size={20} color="#fff" />, bg: '#3b82f6' },
    { id: 4, type: 'comment', title: 'New comment', desc: 'Sarah replied to your post regarding the design updates.', time: '2d ago', icon: <MessageCircle size={20} color="#fff" />, bg: '#6366f1' }
  ];

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <ArrowLeft size={24} onClick={() => navigate(-1)} style={{ cursor: 'pointer' }} />
        <h2 style={styles.headerTitle}>Notifications</h2>
        <div style={{ width: 24 }}></div>
      </header>

      <div style={styles.list}>
        {notices.map((note) => (
          <div key={note.id} style={styles.card}>
            <div style={{ ...styles.iconCircle, backgroundColor: note.bg }}>{note.icon}</div>
            <div style={styles.content}>
              <div style={styles.row}>
                <span style={styles.noteTitle}>{note.title}</span>
                <span style={styles.time}>{note.time}</span>
              </div>
              <p style={styles.desc}>{note.desc}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  container: { backgroundColor: '#f8fafc', minHeight: '100vh' },
  header: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '16px', backgroundColor: '#fff', borderBottom: '1px solid #f1f5f9' },
  headerTitle: { fontSize: '18px', fontWeight: 'bold', margin: 0 },
  list: { padding: '16px' },
  card: { display: 'flex', gap: '12px', backgroundColor: '#fff', padding: '16px', borderRadius: '16px', marginBottom: '12px', border: '1px solid #f1f5f9' },
  iconCircle: { width: '40px', height: '40px', borderRadius: '12px', display: 'flex', justifyContent: 'center', alignItems: 'center', flexShrink: 0 },
  content: { flex: 1 },
  row: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '4px' },
  noteTitle: { fontSize: '14px', fontWeight: 'bold', color: '#1e293b' },
  time: { fontSize: '11px', color: '#94a3b8' },
  desc: { fontSize: '12px', color: '#64748b', margin: 0, lineHeight: '1.4' }
};

export default Notifications;
