import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { Eye, EyeOff, ArrowLeft } from 'lucide-react';

const StudentSignUp = ({ onLogin }) => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    fullName: '',
    rollNo: '',
    department: '',
    email: '',
    phone: '',
    password: '',
    confirmPassword: '',
    agree: false
  });

  const [showPass, setShowPass] = useState(false);

  // Styles optimized for NO SCROLLING (Compact)
  const styles = {
    container: {
      height: '100%',
      display: 'flex',
      flexDirection: 'column',
      backgroundColor: '#fff', // White background inside the frame
      fontFamily: 'sans-serif'
    },
    // Header Section
    header: {
      padding: '20px 24px 10px 24px',
      display: 'flex',
      flexDirection: 'column',
      gap: '5px'
    },
    backBtn: {
      display: 'flex',
      alignItems: 'center',
      color: '#64748b',
      fontSize: '14px',
      cursor: 'pointer',
      marginBottom: '10px'
    },
    title: {
      fontSize: '24px',
      fontWeight: '800',
      color: '#1e293b',
      margin: 0
    },
    subtitle: {
      fontSize: '14px',
      color: '#94a3b8'
    },

    // Toggle (Student/Teacher)
    toggleContainer: {
      display: 'flex',
      margin: '15px 24px',
      backgroundColor: '#f1f5f9',
      borderRadius: '12px',
      padding: '4px'
    },
    toggleBtn: (active) => ({
      flex: 1,
      border: 'none',
      padding: '8px',
      borderRadius: '10px',
      fontSize: '13px',
      fontWeight: '600',
      cursor: 'pointer',
      backgroundColor: active ? '#fff' : 'transparent',
      color: active ? '#5c5cfc' : '#94a3b8',
      boxShadow: active ? '0 2px 5px rgba(0,0,0,0.05)' : 'none',
      transition: 'all 0.2s'
    }),

    // Form Area - Uses Grid to fit everything
    form: {
      padding: '0 24px 20px 24px',
      display: 'flex',
      flexDirection: 'column',
      gap: '12px', // Tighter gap
      flex: 1
    },
    row: {
      display: 'flex',
      gap: '12px'
    },
    col: {
      flex: 1,
      display: 'flex',
      flexDirection: 'column'
    },
    input: {
      width: '100%',
      padding: '12px', // Smaller padding
      borderRadius: '10px',
      border: '1px solid #e2e8f0',
      fontSize: '13px',
      outline: 'none',
      backgroundColor: '#f8fafc',
      boxSizing: 'border-box'
    },
    label: {
      fontSize: '11px',
      fontWeight: '600',
      color: '#64748b',
      marginBottom: '4px',
      marginLeft: '2px'
    },

    // Checkbox Area
    checkboxRow: {
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
      fontSize: '12px',
      color: '#64748b',
      marginTop: '5px'
    },

    // Bottom Action Area
    footer: {
      marginTop: 'auto',
      paddingTop: '10px'
    },
    submitBtn: {
      width: '100%',
      padding: '14px',
      borderRadius: '14px',
      border: 'none',
      backgroundColor: '#5c5cfc',
      color: '#fff',
      fontSize: '15px',
      fontWeight: 'bold',
      cursor: 'pointer',
      boxShadow: '0 4px 12px rgba(92, 92, 252, 0.3)'
    },
    loginLink: {
      textAlign: 'center',
      fontSize: '13px',
      color: '#94a3b8',
      marginTop: '15px'
    },
    linkHighlight: {
      color: '#5c5cfc',
      fontWeight: '600',
      textDecoration: 'none'
    }
  };

  const handleChange = (e) => setFormData({...formData, [e.target.name]: e.target.value});

  const handleSubmit = () => {
    // Simulating login
    onLogin({ 
      name: formData.fullName || 'New Student', 
      email: formData.email, 
      role: 'student',
      college: 'Engineering Tech Institute' 
    });
    navigate('/');
  };

  return (
    <div style={styles.container}>
      {/* 1. Header */}
      <div style={styles.header}>
        <div style={styles.backBtn} onClick={() => navigate('/')}>
          <ArrowLeft size={16} style={{marginRight: 6}}/> Back
        </div>
        <h1 style={styles.title}>Create Account</h1>
        <p style={styles.subtitle}>Sign up to get started</p>
      </div>

      {/* 2. Toggle */}
      <div style={styles.toggleContainer}>
        <button style={styles.toggleBtn(true)}>Student</button>
        <button style={styles.toggleBtn(false)} onClick={() => navigate('/teacher-signup')}>Teacher</button>
      </div>

      {/* 3. Compact Form */}
      <div style={styles.form}>
        {/* Full Name */}
        <div style={styles.col}>
          <label style={styles.label}>Full Name</label>
          <input style={styles.input} placeholder="e.g. Lokesh Kumar" name="fullName" onChange={handleChange} />
        </div>

        {/* Row: Roll No + Dept */}
        <div style={styles.row}>
          <div style={styles.col}>
            <label style={styles.label}>Roll Number</label>
            <input style={styles.input} placeholder="ID-2024" name="rollNo" onChange={handleChange} />
          </div>
          <div style={styles.col}>
            <label style={styles.label}>Department</label>
            <select style={styles.input} name="department" onChange={handleChange}>
              <option>Select</option>
              <option>CSE</option>
              <option>ECE</option>
              <option>MECH</option>
            </select>
          </div>
        </div>

        {/* Row: Email + Phone (Optimized space) */}
        <div style={styles.col}>
          <label style={styles.label}>Email Address</label>
          <input style={styles.input} type="email" placeholder="student@college.edu" name="email" onChange={handleChange} />
        </div>

        {/* Row: Password + Confirm */}
        <div style={styles.row}>
          <div style={styles.col}>
            <label style={styles.label}>Password</label>
            <div style={{position: 'relative'}}>
              <input style={styles.input} type={showPass ? "text" : "password"} placeholder="••••••" name="password" onChange={handleChange} />
            </div>
          </div>
          <div style={styles.col}>
            <label style={styles.label}>Confirm</label>
            <input style={styles.input} type="password" placeholder="••••••" name="confirmPassword" onChange={handleChange} />
          </div>
        </div>

        {/* Terms */}
        <div style={styles.checkboxRow}>
          <input type="checkbox" />
          <span>I agree to the Terms & Privacy Policy</span>
        </div>

        {/* 4. Footer Actions */}
        <div style={styles.footer}>
          <button style={styles.submitBtn} onClick={handleSubmit}>Register</button>

          <div style={styles.loginLink}>
            Already have an account? <Link to="/signin" style={styles.linkHighlight}>Login</Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default StudentSignUp;
