import React, { useState } from 'react';
import { Search, MapPin, Calendar, Filter, ArrowRight, X, Check } from 'lucide-react'; 
import { useNavigate } from 'react-router-dom';

const Events = ({ allEvents, theme, searchTerm, setSearchTerm }) => {
  const navigate = useNavigate();
  const isDark = theme === 'dark';

  // 1. FILTER STATE
  const [showFilters, setShowFilters] = useState(false);
  const [activeCategory, setActiveCategory] = useState('All');

  // Hardcoded categories (or you could extract them from allEvents)
  const categories = ['All', 'Tech', 'Cultural', 'Sports', 'Workshop', 'Seminar'];

  // 2. FILTER LOGIC
  // We filter the events that were passed down (which might already be searched)
  const filteredEvents = allEvents.filter(event => {
    if (activeCategory === 'All') return true;
    return event.category === activeCategory;
  });

  const styles = {
    container: { 
      padding: '24px', 
      fontFamily: 'sans-serif', 
      maxWidth: '1200px', 
      margin: '0 auto',
      minHeight: '100vh',
      color: isDark ? '#fff' : '#1e293b'
    },
    header: { marginBottom: '32px' },
    title: { fontSize: '28px', fontWeight: '800', marginBottom: '8px' },
    subtitle: { color: '#64748b', fontSize: '16px' },

    // Controls Section
    controls: { display: 'flex', gap: '16px', marginBottom: '20px', flexWrap: 'wrap' },
    searchBox: {
      flex: 1, minWidth: '280px', display: 'flex', alignItems: 'center',
      backgroundColor: isDark ? '#1e293b' : '#fff',
      border: isDark ? '1px solid #334155' : '1px solid #e2e8f0',
      borderRadius: '12px', padding: '12px 16px',
      boxShadow: '0 2px 4px rgba(0,0,0,0.02)'
    },
    input: {
      border: 'none', outline: 'none', width: '100%', fontSize: '15px', marginLeft: '10px',
      backgroundColor: 'transparent', color: isDark ? '#fff' : '#1e293b'
    },
    filterBtn: {
      display: 'flex', alignItems: 'center', gap: '8px', padding: '0 24px',
      borderRadius: '12px',
      backgroundColor: showFilters ? '#3b82f6' : (isDark ? '#334155' : '#fff'),
      border: showFilters ? '1px solid #3b82f6' : (isDark ? '1px solid #475569' : '1px solid #e2e8f0'),
      color: showFilters ? '#fff' : (isDark ? '#fff' : '#475569'),
      cursor: 'pointer', fontWeight: '600', transition: 'all 0.2s'
    },

    // Filter Chips Area (New)
    filterPanel: {
      display: showFilters ? 'flex' : 'none',
      gap: '10px',
      flexWrap: 'wrap',
      marginBottom: '32px',
      padding: '16px',
      backgroundColor: isDark ? '#1e293b' : '#f8fafc',
      borderRadius: '12px',
      border: isDark ? '1px solid #334155' : '1px solid #e2e8f0',
      animation: 'fadeIn 0.2s ease-in-out'
    },
    chip: (isActive) => ({
      padding: '8px 16px',
      borderRadius: '20px',
      fontSize: '13px',
      fontWeight: '600',
      cursor: 'pointer',
      border: 'none',
      display: 'flex', alignItems: 'center', gap: '6px',
      backgroundColor: isActive ? '#3b82f6' : (isDark ? '#334155' : '#fff'),
      color: isActive ? '#fff' : (isDark ? '#94a3b8' : '#64748b'),
      boxShadow: isActive ? '0 4px 10px rgba(59, 130, 246, 0.3)' : '0 2px 4px rgba(0,0,0,0.02)'
    }),

    // Grid
    grid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
      gap: '24px'
    },
    card: {
      backgroundColor: isDark ? '#1e293b' : '#fff',
      borderRadius: '16px', overflow: 'hidden',
      border: isDark ? '1px solid #334155' : '1px solid #e2e8f0',
      transition: 'transform 0.2s, box-shadow 0.2s',
      cursor: 'pointer', display: 'flex', flexDirection: 'column', height: '100%'
    },
    cardImage: { width: '100%', height: '180px', objectFit: 'cover' },
    cardBody: { padding: '20px', flex: 1, display: 'flex', flexDirection: 'column' },
    categoryTag: {
      display: 'inline-block', fontSize: '12px', fontWeight: '600',
      color: '#3b82f6', backgroundColor: isDark ? 'rgba(59, 130, 246, 0.1)' : '#eff6ff',
      padding: '4px 10px', borderRadius: '6px', marginBottom: '12px', width: 'fit-content'
    },
    cardTitle: { fontSize: '18px', fontWeight: 'bold', marginBottom: '10px', lineHeight: '1.4' },
    meta: { display: 'flex', alignItems: 'center', gap: '6px', fontSize: '14px', color: '#64748b', marginBottom: '6px' },
    footer: { marginTop: 'auto', paddingTop: '16px', borderTop: isDark ? '1px solid #334155' : '1px solid #f1f5f9', display: 'flex', justifyContent: 'space-between', alignItems: 'center', color: '#3b82f6', fontWeight: '600', fontSize: '14px' },
    
    // Empty State
    noResults: {
      gridColumn: '1 / -1',
      textAlign: 'center',
      padding: '40px',
      color: '#94a3b8'
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>Explore Events</h1>
        <p style={styles.subtitle}>Discover workshops, competitions, and seminars.</p>
      </div>

      {/* Search & Filter Button */}
      <div style={styles.controls}>
        <div style={styles.searchBox}>
          <Search size={20} color="#94a3b8" />
          <input 
            style={styles.input} 
            placeholder="Search events..." 
            value={searchTerm} 
            onChange={(e) => setSearchTerm(e.target.value)} 
          />
        </div>
        <button 
          style={styles.filterBtn} 
          onClick={() => setShowFilters(!showFilters)}
        >
          {showFilters ? <X size={18} /> : <Filter size={18} />} 
          {showFilters ? 'Close' : 'Filters'}
        </button>
      </div>

      {/* Filter Selection Panel (Shows when Filters clicked) */}
      <div style={styles.filterPanel}>
        {categories.map(cat => (
          <button 
            key={cat} 
            style={styles.chip(activeCategory === cat)}
            onClick={() => setActiveCategory(cat)}
          >
            {activeCategory === cat && <Check size={14} />}
            {cat}
          </button>
        ))}
      </div>

      {/* Events Grid */}
      <div style={styles.grid}>
        {filteredEvents.length > 0 ? (
          filteredEvents.map(event => (
            <div 
              key={event.id} 
              style={styles.card} 
              onClick={() => navigate(`/event-details/${event.id}`)}
              onMouseEnter={(e) => { e.currentTarget.style.transform = 'translateY(-5px)'; e.currentTarget.style.boxShadow = '0 10px 20px rgba(0,0,0,0.08)'; }}
              onMouseLeave={(e) => { e.currentTarget.style.transform = 'translateY(0)'; e.currentTarget.style.boxShadow = 'none'; }}
            >
              <img src={event.image} alt="" style={styles.cardImage} />
              <div style={styles.cardBody}>
                <span style={styles.categoryTag}>{event.category}</span>
                <h3 style={styles.cardTitle}>{event.title}</h3>
                
                <div style={styles.meta}>
                  <Calendar size={16} /> {event.date}
                </div>
                <div style={styles.meta}>
                  <MapPin size={16} /> {event.location}
                </div>

                <div style={styles.footer}>
                  View Details <ArrowRight size={16} />
                </div>
              </div>
            </div>
          ))
        ) : (
          <div style={styles.noResults}>
            <h3>No events found</h3>
            <p>Try adjusting your search or filters.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Events;
