import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation, Navigate } from 'react-router-dom';
import { CheckCircle, Calendar, RefreshCw, Bell } from 'lucide-react';

// --- IMPORTS ---

// 1. Data & Assets
import { translations } from './data/translations'; // Ensure you have this file
// import logoImg from './assets/logo.jpeg'; // If needed globally

// 2. Components
import PhoneFrame from './components/PhoneFrame';
import BottomNav from './components/BottomNav';
import LandingPage from './components/LandingPage';
import StudentSignIn from './components/StudentSignIn';
import StudentSignUp from './components/StudentSignUp';
// You can add TeacherSignUp route if you have that component ready
// import TeacherSignUp from './components/TeacherSignUp';

// 3. Pages
import Dashboard from './pages/Dashboard';
import Events from './pages/Events';
import MyEvents from './pages/MyEvents'; // This is the "My Tickets" page
import Profile from './pages/Profile';
import EditProfile from './pages/EditProfile';
import ChangePassword from './pages/ChangePassword';
import Settings from './pages/Settings'; 
import ThemeSelection from './pages/ThemeSelection';
import LanguageSelection from './pages/LanguageSelection'; 
import EventDetails from './pages/EventDetails';
import Notifications from './pages/Notifications';
import TicketConfirmation from './pages/TicketConfirmation'; 

// --- APP CONTENT (Handles Routing Logic) ---
const AppContent = ({ 
  isAuthenticated, handleLogin, handleLogout, 
  theme, setTheme, user, setUser, 
  allEvents, handleRegister, handleCancel, 
  searchTerm, setSearchTerm, registeredEvents, filteredEvents, 
  notifications, unreadCount, markNotificationsRead, t
}) => {
  const location = useLocation();

  // A. AUTHENTICATION ROUTES (When not logged in)
  if (!isAuthenticated) {
    return (
      <Routes>
        <Route path="/" element={<LandingPage />} />
        <Route path="/signin" element={<StudentSignIn onLogin={handleLogin} />} /> 
        <Route path="/signup" element={<StudentSignUp onLogin={handleLogin} />} />
        {/* Optional: <Route path="/teacher-signup" element={<TeacherSignUp />} /> */}
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    );
  }

  // B. MAIN APP ROUTES (When logged in)
  
  // Decide where to show Bottom Navigation
  const showNavOn = ['/', '/events', '/my-events', '/profile'];
  const shouldShowNav = showNavOn.includes(location.pathname);

  // Styling for the content area (PhoneFrame handles the outer container)
  const contentStyles = {
    minHeight: '100%', 
    backgroundColor: theme === 'dark' ? '#0f172a' : '#fff', // Global background
    paddingBottom: shouldShowNav ? '80px' : '0px', // Space for Bottom Nav
    boxSizing: 'border-box',
    display: 'flex',
    flexDirection: 'column'
  };

  return (
    <div style={contentStyles}>
      <Routes>
        {/* 1. Dashboard (Home) */}
        <Route 
          path="/" 
          element={
            <Dashboard 
              user={user} 
              events={allEvents} 
              theme={theme} 
              regCount={registeredEvents.length} 
              onRegister={handleRegister} 
              unreadCount={unreadCount} 
              onReadNotifications={markNotificationsRead} 
              t={t} 
            />
          } 
        />
        
        {/* 2. Events Page */}
        <Route 
          path="/events" 
          element={
            <Events 
              allEvents={filteredEvents} 
              theme={theme} 
              searchTerm={searchTerm} 
              setSearchTerm={setSearchTerm} 
              onRegister={handleRegister} 
              t={t} 
            />
          } 
        />
        
        {/* 3. My Tickets (MyEvents) */}
        <Route 
          path="/my-events" 
          element={
            <MyEvents 
              theme={theme} 
              events={registeredEvents} 
              onCancel={handleCancel} 
              t={t} 
            />
          } 
        />
        
        {/* 4. Event Details & Confirmation */}
        <Route path="/event-details/:id" element={<EventDetails allEvents={allEvents} theme={theme} onRegister={handleRegister} t={t} />} />
        <Route path="/ticket-confirmation/:id" element={<TicketConfirmation allEvents={allEvents} theme={theme} onCancel={handleCancel} />} />
        
        {/* 5. Notifications */}
        <Route path="/notifications" element={<Notifications theme={theme} notificationsList={notifications} />} />
        
        {/* 6. Profile & Settings */}
        <Route path="/profile" element={<Profile user={user} theme={theme} t={t} onLogout={handleLogout} />} />
        <Route path="/edit-profile" element={<EditProfile user={user} setUser={setUser} theme={theme} />} />
        <Route path="/change-password" element={<ChangePassword theme={theme} />} />
        
        <Route path="/settings" element={<Settings theme={theme} user={user} onLogout={handleLogout} />} />
        <Route path="/settings/theme" element={<ThemeSelection currentTheme={theme} setTheme={setTheme} />} />
        <Route path="/settings/language" element={<LanguageSelection currentLanguage={user.language || 'English'} setLanguage={(lang) => setUser({...user, language: lang})} theme={theme} />} />
        
        {/* Fallback */}
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>

      {/* Conditional Bottom Navigation */}
      {shouldShowNav && <BottomNav theme={theme} t={t} />}
    </div>
  );
};

// --- ROOT COMPONENT ---
function App() {
  // 1. Theme State
  const [theme, setTheme] = useState('light'); 
  
  // 2. Search State
  const [searchTerm, setSearchTerm] = useState("");
  
  // 3. Authentication State
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState({
    name: "Guest",
    email: "guest@college.edu",
    college: "Engineering Tech Institute",
    regNo: "ETI-2024-001",
    language: "English",
    role: "student" // 'student' or 'teacher'
  });

  // Auth Handlers
  const handleLogin = (userData) => {
    if (userData) {
      setUser(prev => ({ ...prev, ...userData }));
    }
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    // Reset to default
    setUser({
      name: "Guest",
      email: "guest@college.edu",
      college: "Engineering Tech Institute",
      regNo: "ETI-2024-001",
      language: "English",
      role: "student"
    });
  };

  // 4. Translations
  const t = translations[user.language] || translations['English'];

  // 5. Event Data (Mock Data)
  const [allEvents, setAllEvents] = useState([
    { id: 1, title: "AI & Future Tech Workshop", date: "Apr 05, 2024 · 09:00 AM", location: "Innovation Lab", category: "Tech", image: "https://images.unsplash.com/photo-1485827404703-89b55fcc595e?w=800&q=80", status: 'idle' },
    { id: 2, title: "Inter-College Music Battle", date: "Apr 12, 2024 · 05:00 PM", location: "Main Stadium", category: "Cultural", image: "https://images.unsplash.com/photo-1501281668745-f7f57925c3b4?w=800&q=80", status: 'idle' },
    { id: 3, title: "University Basketball Finals", date: "Apr 20, 2024 · 10:00 AM", location: "Indoor Sports Complex", category: "Sports", image: "https://images.unsplash.com/photo-1546519638-68e109498ffc?w=800&q=80", status: 'idle' }
  ]);

  // 6. Notification Data
  const [notifications, setNotifications] = useState([
    { id: 1, title: "AI & Future Tech Workshop", typeLabel: "Registration Approved", desc: "Your seat has been confirmed.", time: "2h ago", icon: <CheckCircle size={20} color="#5c5cfc" />, bgColor: 'rgba(92, 92, 252, 0.1)', eventId: 1 },
    { id: 2, title: "Inter-College Music Battle", typeLabel: "New Event Added", desc: "Registration is now open!", time: "5h ago", icon: <Calendar size={20} color="#22c55e" />, bgColor: 'rgba(34, 197, 94, 0.1)', eventId: 2 },
    { id: 3, title: "University Basketball Finals", typeLabel: "Event Updated", desc: "Location changed.", time: "Yesterday", icon: <RefreshCw size={20} color="#f59e0b" />, bgColor: 'rgba(245, 158, 11, 0.1)', eventId: 3 },
    { id: 4, title: "AI & Future Tech Workshop", typeLabel: "Reminder", desc: "Event starts tomorrow.", time: "2d ago", icon: <Bell size={20} color="#ef4444" />, bgColor: 'rgba(239, 68, 68, 0.1)', eventId: 1 }
  ]);

  const [unreadCount, setUnreadCount] = useState(2);
  const markNotificationsRead = () => setUnreadCount(0);

  // 7. Event Actions
  const handleRegister = (eventId) => {
    setAllEvents(prev => prev.map(ev => ev.id === eventId ? { ...ev, status: 'pending' } : ev));
    // Simulate backend approval after 3 seconds
    setTimeout(() => {
      setAllEvents(prev => prev.map(ev => ev.id === eventId ? { ...ev, status: 'approved' } : ev));
      setUnreadCount(prev => prev + 1); 
      // Add notification for approval
      const newNotif = {
        id: Date.now(),
        title: "Registration Confirmed",
        typeLabel: "Approved",
        desc: "You are registered successfully!",
        time: "Just now",
        icon: <CheckCircle size={20} color="#5c5cfc" />,
        bgColor: 'rgba(92, 92, 252, 0.1)',
        eventId: eventId
      };
      setNotifications(prev => [newNotif, ...prev]);
    }, 3000); 
  };

  const handleCancelRegistration = (eventId) => {
    setAllEvents(prev => prev.map(ev => ev.id === eventId ? { ...ev, status: 'idle' } : ev));
  };

  // Filter Logic
  const filteredEvents = allEvents.filter(e => e.title.toLowerCase().includes(searchTerm.toLowerCase()));
  const registeredEvents = allEvents.filter(e => e.status === 'pending' || e.status === 'approved');

  // --- RETURN STATEMENT ---
  return (
    <Router>
      <PhoneFrame>
        <AppContent 
          isAuthenticated={isAuthenticated}
          handleLogin={handleLogin}
          handleLogout={handleLogout}
          
          theme={theme} setTheme={setTheme} 
          user={user} setUser={setUser}
          
          allEvents={allEvents} 
          handleRegister={handleRegister} 
          handleCancel={handleCancelRegistration}
          
          searchTerm={searchTerm} setSearchTerm={setSearchTerm}
          registeredEvents={registeredEvents} 
          filteredEvents={filteredEvents}
          
          notifications={notifications} 
          unreadCount={unreadCount} 
          markNotificationsRead={markNotificationsRead}
          
          t={t}
        />
      </PhoneFrame>
    </Router>
  );
}

export default App;
