import React from 'react';
import { ArrowLeft, CheckCircle2, Calendar, RefreshCw, MessageSquare } from 'lucide-react';

const Notifications = ({ theme, setIsOpen }) => {
  const isDark = theme === 'dark';

  const notifications = [
    { 
      id: 1, 
      title: "Registration approved", 
      desc: "Your account has been verified. You can now access all features.", 
      time: "2h ago", 
      icon: <CheckCircle2 size={18} />,
      bgColor: isDark ? '#1e293b' : '#eff6ff',
      iconColor: '#2563eb'
    },
    { 
      id: 2, 
      title: "New event added", 
      desc: "Summer Workshop has been added to your calendar.", 
      time: "Yesterday", 
      icon: <Calendar size={18} />,
      bgColor: isDark ? '#1e293b' : '#f0fdf4',
      iconColor: '#16a34a'
    },
    { 
      id: 3, 
      title: "Event updated", 
      desc: "The location for Team Lunch has been changed to Main hall.", 
      time: "Yesterday", 
      icon: <RefreshCw size={18} />,
      bgColor: isDark ? '#1e293b' : '#fff7ed',
      iconColor: '#ea580c'
    },
    { 
      id: 4, 
      title: "New comment", 
      desc: "Sarah replied to your post regarding the design updates.", 
      time: "2d ago", 
      icon: <MessageSquare size={18} />,
      bgColor: isDark ? '#1e293b' : '#f5f3ff',
      iconColor: '#7c3aed'
    }
  ];

  const styles = {
    card: {
      display: 'flex',
      gap: '12px',
      padding: '16px',
      backgroundColor: isDark ? '#111827' : '#fff',
      borderRadius: '16px',
      marginBottom: '12px',
      border: isDark ? '1px solid #374151' : '1px solid #f1f5f9',
      boxShadow: '0 2px 8px rgba(0,0,0,0.02)'
    },
    iconWrapper: (bg, color) => ({
      width: '40px',
      height: '40px',
      borderRadius: '12px',
      backgroundColor: bg,
      color: color,
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      flexShrink: 0
    })
  };

  return (
    <div style={{ width: '100%' }}>
      <div style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '24px' }}>
        <ArrowLeft 
          size={24} 
          onClick={() => setIsOpen(false)} 
          style={{ cursor: 'pointer', color: isDark ? '#fff' : '#1e293b' }} 
        />
        <h2 style={{ fontSize: '20px', fontWeight: 'bold', margin: 0, color: isDark ? '#fff' : '#1e293b' }}>
          Notifications
        </h2>
      </div>

      {notifications.map(n => (
        <div key={n.id} style={styles.card}>
          <div style={styles.iconWrapper(n.bgColor, n.iconColor)}>
            {n.icon}
          </div>
          <div style={{ flex: 1 }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start' }}>
              <p style={{ margin: 0, fontWeight: 'bold', fontSize: '14px', color: n.iconColor }}>{n.title}</p>
              <span style={{ fontSize: '11px', color: '#94a3b8' }}>{n.time}</span>
            </div>
            <p style={{ margin: '4px 0 0 0', fontSize: '13px', color: isDark ? '#94a3b8' : '#64748b', lineHeight: '1.4' }}>
              {n.desc}
            </p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Notifications;
