import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation, Navigate } from 'react-router-dom';
import { CheckCircle, Calendar } from 'lucide-react';
import './App.css'; 

// --- COMPONENTS ---
import Sidebar from './components/Sidebar'; 
import BottomNav from './components/BottomNav';
import LandingPage from './components/LandingPage'; 
import StudentSignIn from './components/StudentSignIn';
import StudentSignUp from './components/StudentSignUp';
import TeacherSignUp from './components/TeacherSignUp';

// --- STUDENT PAGES ---
import Dashboard from './pages/Dashboard';
import Events from './pages/Events';
import MyEvents from './pages/MyEvents';
import TicketConfirmation from './pages/TicketConfirmation'; 

// --- TEACHER PAGES ---
import TeacherDashboard from './pages/TeacherDashboard';
import CreateEvent from './pages/CreateEvent';
import TeacherMyEvents from './pages/TeacherMyEvents';
import TeacherEventDetails from './pages/TeacherEventDetails';
import TeacherRegistrations from './pages/TeacherRegistrations';

// --- SHARED PAGES ---
import Profile from './pages/Profile';
import EditProfile from './pages/EditProfile';
import ChangePassword from './pages/ChangePassword';
import ThemeSelection from './pages/ThemeSelection';
import EventDetails from './pages/EventDetails'; // Student view
import Notifications from './pages/Notifications';
import PrivacyPolicy from './pages/PrivacyPolicy';
import HelpCenter from './pages/HelpCenter'; 

const AppContent = ({ 
  isAuthenticated, handleLogin, handleLogout, 
  theme, toggleTheme, setTheme, user, setUser, 
  allEvents, handleRegister, handleCancel, 
  searchTerm, setSearchTerm, registeredEvents, filteredEvents, 
  notifications, unreadCount, markNotificationsRead,
  // Teacher Props
  handleCreateEvent, handleDeleteEvent, 
  registrations, handleApproveReg, handleRejectReg
}) => {
  const location = useLocation();
  const isDark = theme === 'dark';
  
  // 1. MOBILE DETECTION
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Apply Dark Mode Class
  React.useEffect(() => {
    document.body.className = isDark ? 'dark-mode' : 'light-mode';
    document.body.style.backgroundColor = isDark ? '#0f172a' : '#f8fafc';
    document.body.style.margin = '0';
  }, [isDark]);

  // Check Role
  const isTeacher = user.role === 'teacher';

  const showNavOn = ['/', '/events', '/my-events', '/profile', '/dashboard', '/teacher-events', '/teacher-registrations'];
  const shouldShowNav = showNavOn.includes(location.pathname) && isAuthenticated;

  // ---------------------------------------------------------
  // 1. UNAUTHENTICATED ROUTES (Landing & Auth)
  // ---------------------------------------------------------
  if (!isAuthenticated) {
    return (
      <div style={{ minHeight: '100vh', backgroundColor: isDark ? '#0f172a' : '#fff' }}>
        <Routes>
          <Route path="/" element={<LandingPage />} />
          <Route path="/signin" element={<StudentSignIn onLogin={handleLogin} />} /> 
          <Route path="/signup" element={<StudentSignUp onLogin={handleLogin} />} />
          <Route path="/teacher-signup" element={<TeacherSignUp onLogin={handleLogin} />} />
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </div>
    );
  }

  // ---------------------------------------------------------
  // 2. AUTHENTICATED APP LAYOUT
  // ---------------------------------------------------------
  return (
    <div className="app-layout" style={{ display: 'flex', minHeight: '100vh', width: '100%' }}>
      
      {/* Sidebar (Desktop Only) */}
      {!isMobile && (
        <Sidebar user={user} theme={theme} onLogout={handleLogout} />
      )}

      {/* Main Content Area */}
      <div className="main-content" style={{ 
        flex: 1, 
        marginLeft: isMobile ? '0px' : '50px', 
        width: isMobile ? '100%' : 'calc(100% - 50px)', 
        backgroundColor: isDark ? '#0f172a' : '#f8fafc',
        minHeight: '100vh',
        transition: 'margin-left 0.3s ease' 
      }}>
        <Routes>
          
          {/* --- DASHBOARD (Dynamic based on Role) --- */}
          <Route path="/" element={
            isTeacher ? (
              <TeacherDashboard 
                 user={user} 
                 theme={theme} 
                 toggleTheme={toggleTheme} 
                 onLogout={handleLogout} 
              />
            ) : (
              <Dashboard 
                user={user} 
                events={allEvents} 
                theme={theme} 
                toggleTheme={toggleTheme} 
                regCount={registeredEvents.length} 
                onRegister={handleRegister} 
                unreadCount={unreadCount} 
                onReadNotifications={markNotificationsRead} 
              />
            )
          } />
          
          {/* --- TEACHER ROUTES --- */}
          {isTeacher && (
            <>
              <Route path="/create-event" element={<CreateEvent onCreate={handleCreateEvent} theme={theme} />} />
              <Route path="/teacher-events" element={<TeacherMyEvents events={allEvents} theme={theme} />} />
              <Route path="/teacher-event-details/:id" element={<TeacherEventDetails events={allEvents} onDelete={handleDeleteEvent} theme={theme} />} />
              <Route path="/teacher-registrations" element={<TeacherRegistrations registrations={registrations} onApprove={handleApproveReg} onReject={handleRejectReg} theme={theme} />} />
            </>
          )}

          {/* --- STUDENT ROUTES --- */}
          {!isTeacher && (
            <>
              <Route path="/events" element={<Events allEvents={filteredEvents} theme={theme} searchTerm={searchTerm} setSearchTerm={setSearchTerm} onRegister={handleRegister} />} />
              <Route path="/my-events" element={<MyEvents theme={theme} events={registeredEvents} onCancel={handleCancel} />} />
              <Route path="/event-details/:id" element={<EventDetails allEvents={allEvents} theme={theme} onRegister={handleRegister} />} />
              <Route path="/ticket-confirmation/:id" element={<TicketConfirmation allEvents={allEvents} theme={theme} onCancel={handleCancel} />} />
            </>
          )}

          {/* --- SHARED ROUTES --- */}
          <Route path="/profile" element={<Profile user={user} theme={theme} onLogout={handleLogout} />} />
          <Route path="/edit-profile" element={<EditProfile user={user} setUser={setUser} theme={theme} />} />
          <Route path="/change-password" element={<ChangePassword theme={theme} />} />
          <Route path="/settings/theme" element={<ThemeSelection currentTheme={theme} setTheme={setTheme} />} />
          <Route path="/notifications" element={<Notifications theme={theme} notificationsList={notifications} />} />
          <Route path="/privacy" element={<PrivacyPolicy theme={theme} />} />
          <Route path="/help" element={<HelpCenter theme={theme} />} />

          {/* Fallback */}
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
        
        {/* Mobile Spacer */}
        <div style={{ height: '80px' }} className="mobile-only-spacer"></div>
      </div>

      {/* Mobile Bottom Nav - ✅ FIXED: PASSED USER PROP */}
      {shouldShowNav && isMobile && (
        <BottomNav theme={theme} user={user} />
      )}
    </div>
  );
};

function App() {
  const [theme, setTheme] = useState('light'); 
  const [searchTerm, setSearchTerm] = useState("");
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  
  // USER STATE
  const [user, setUser] = useState({
    name: "Guest",
    email: "",
    college: "Engineering Tech Institute",
    regNo: "",
    role: "student", // 'student' or 'teacher'
    profileImage: "https://images.unsplash.com/photo-1494790108377-be9c29b29330?w=200&q=80" 
  });

  const toggleTheme = () => setTheme((prev) => (prev === 'light' ? 'dark' : 'light'));

  const handleLogin = (userData) => { 
    if (userData) setUser(prev => ({ ...prev, ...userData })); 
    setIsAuthenticated(true); 
  };

  const handleLogout = () => { 
    setIsAuthenticated(false); 
    setUser({ name: "Guest", email: "", role: "student", profileImage: "" }); 
  };

  // --- MOCK DATA: EVENTS ---
  const [allEvents, setAllEvents] = useState([
    { id: 1, title: "AI & Future Tech Workshop", date: "Apr 05, 2024 · 09:00 AM", location: "Innovation Lab", category: "Tech", image: "https://images.unsplash.com/photo-1485827404703-89b55fcc595e?w=800&q=80", status: 'Active' },
    { id: 2, title: "Inter-College Music Battle", date: "Apr 12, 2024 · 05:00 PM", location: "Main Stadium", category: "Cultural", image: "https://images.unsplash.com/photo-1501281668745-f7f57925c3b4?w=800&q=80", status: 'Active' },
    { id: 3, title: "University Basketball Finals", date: "Apr 20, 2024 · 10:00 AM", location: "Indoor Sports Complex", category: "Sports", image: "https://images.unsplash.com/photo-1546519638-68e109498ffc?w=800&q=80", status: 'Active' }
  ]);

  // --- MOCK DATA: REGISTRATIONS (For Teacher) ---
  // ✅ FIXED: Added 'status' so filtering works in TeacherRegistrations
  const [registrations, setRegistrations] = useState([
    { id: 101, name: "Rahul Kumar", email: "rahul@student.edu", phone:"+91 9876543210", event: "AI Workshop", status: "Pending" },
    { id: 102, name: "Priya Sharma", email: "priya@student.edu", phone:"+91 9876543211", event: "Web Seminar", status: "Approved" },
    { id: 103, name: "Amit Verma", email: "amit@student.edu", phone:"+91 9876543212", event: "Music Battle", status: "Pending" },
    { id: 104, name: "Sara Ali", email: "sara@student.edu", phone:"+91 9876543213", event: "AI Workshop", status: "Rejected" },
  ]);

  const [notifications, setNotifications] = useState([
    { id: 1, title: "AI Workshop", typeLabel: "Approved", desc: "Your seat confirmed.", time: "2h ago", icon: <CheckCircle size={20} color="#5c5cfc" />, bgColor: 'rgba(92, 92, 252, 0.1)', eventId: 1 }
  ]);
  const [unreadCount, setUnreadCount] = useState(1);

  // --- HANDLERS ---

  // Student: Register / Cancel
  const handleRegister = (eventId) => { 
    setAllEvents(prev => prev.map(ev => ev.id === eventId ? { ...ev, status: 'pending' } : ev)); 
  };
  const handleCancel = (eventId) => { 
    setAllEvents(prev => prev.map(ev => ev.id === eventId ? { ...ev, status: 'idle' } : ev)); 
  };

  // Teacher: Create / Delete Event
  const handleCreateEvent = (newEvent) => {
    setAllEvents(prev => [newEvent, ...prev]);
  };
  const handleDeleteEvent = (eventId) => {
    setAllEvents(prev => prev.filter(e => e.id !== eventId));
  };

  // Teacher: Approve / Reject Registration
  // ✅ FIXED: Updates status instead of deleting, so they move to "Approved" or "Rejected" tabs
  const handleApproveReg = (id) => {
    setRegistrations(prev => prev.map(r => r.id === id ? { ...r, status: 'Approved' } : r));
  };
  const handleRejectReg = (id) => {
    setRegistrations(prev => prev.map(r => r.id === id ? { ...r, status: 'Rejected' } : r));
  };

  const markNotificationsRead = () => setUnreadCount(0);

  const filteredEvents = allEvents.filter(e => e.title.toLowerCase().includes(searchTerm.toLowerCase()));
  const registeredEvents = allEvents.filter(e => e.status === 'pending' || e.status === 'approved');

  return (
    <Router>
      <AppContent 
        isAuthenticated={isAuthenticated} handleLogin={handleLogin} handleLogout={handleLogout} 
        theme={theme} setTheme={setTheme} toggleTheme={toggleTheme} 
        user={user} setUser={setUser} 
        
        // Data
        allEvents={allEvents} 
        registrations={registrations}
        registeredEvents={registeredEvents} 
        filteredEvents={filteredEvents} 
        notifications={notifications} 
        unreadCount={unreadCount} 
        searchTerm={searchTerm} setSearchTerm={setSearchTerm} 

        // Handlers
        handleRegister={handleRegister} 
        handleCancel={handleCancel} 
        markNotificationsRead={markNotificationsRead}
        handleCreateEvent={handleCreateEvent}
        handleDeleteEvent={handleDeleteEvent}
        handleApproveReg={handleApproveReg}
        handleRejectReg={handleRejectReg}
      />
    </Router>
  );
}

export default App;
